import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { HeartIcon, UserGroupIcon, ShieldCheckIcon, GlobeAltIcon, PlayIcon, PauseIcon } from '@heroicons/react/24/outline';
import { Line } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
  Filler
} from 'chart.js';

ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
  Filler
);

const About = () => {
  const [isPlaying, setIsPlaying] = useState(false);
  const [counters, setCounters] = useState({
    reports: 0,
    lives: 0,
    volunteers: 0,
    stations: 0
  });

  const handleVideoPlay = () => {
    setIsPlaying(true);
  };

  const handleVideoPause = () => {
    setIsPlaying(false);
  };

  useEffect(() => {
    const targetValues = {
      reports: 1452,
      lives: 38,
      volunteers: 75,
      stations: 125
    };

    const duration = 2000; // 2 seconds
    const steps = 60;
    const stepDuration = duration / steps;

    let currentStep = 0;
    const interval = setInterval(() => {
      currentStep++;
      setCounters({
        reports: Math.floor((targetValues.reports * currentStep) / steps),
        lives: Math.floor((targetValues.lives * currentStep) / steps),
        volunteers: Math.floor((targetValues.volunteers * currentStep) / steps),
        stations: Math.floor((targetValues.stations * currentStep) / steps)
      });

      if (currentStep === steps) {
        clearInterval(interval);
      }
    }, stepDuration);

    return () => clearInterval(interval);
  }, []);

  const chartData = {
    labels: ['2019', '2020', '2021', '2022', '2023', '2024'],
    datasets: [
      {
        label: 'Unclaimed Bodies',
        data: [1200, 1350, 1500, 1650, 1800, 1950],
        borderColor: '#f59e0b', // amber-500
        backgroundColor: 'rgba(245, 158, 11, 0.1)', // amber-500 with opacity
        borderWidth: 3,
        pointBackgroundColor: '#f59e0b', // amber-500
        pointBorderColor: '#f8fafc', // slate-50 (for light theme chart bg) or #1e293b for dark
        pointBorderWidth: 2,
        pointRadius: 6,
        pointHoverRadius: 8,
        tension: 0.4,
        fill: true,
        gradient: {
          color: {
            type: 'linear',
            x: 0,
            y: 0,
            x2: 0,
            y2: 1,
            colorStops: [
              {
                offset: 0,
                color: 'rgba(245, 158, 11, 0.7)' // amber-500
              },
              {
                offset: 1,
                color: 'rgba(239, 68, 68, 0.7)' // red-500
              }
            ]
          }
        }
      }
    ]
  };

  const chartOptions = {
    responsive: true,
    maintainAspectRatio: false,
    plugins: {
      legend: {
        display: false
      },
      title: {
        display: true,
        text: [
          'Unclaimed Bodies Found in the Last 5 Years (India)',
          'A Growing Crisis We Can\'t Ignore'
        ],
        color: '#e2e8f0', // slate-200
        font: {
          size: 28,
          weight: 'bold',
          family: 'Merriweather'
        },
        padding: {
          top: 20,
          bottom: 20
        }
      },
      subtitle: {
        display: true,
        text: 'A Growing Crisis We Can\'t Ignore',
        color: '#38bdf8', // sky-400
        font: {
          size: 18,
          style: 'italic',
          family: 'Poppins'
        },
        padding: {
          bottom: 20
        }
      },
      tooltip: {
        backgroundColor: 'rgba(30, 41, 59, 0.95)', // slate-800
        titleColor: '#e2e8f0', // slate-200
        bodyColor: '#e2e8f0', // slate-200
        borderColor: '#38bdf8', // sky-400
        borderWidth: 1,
        padding: 12,
        displayColors: false,
        titleFont: {
          size: 16,
          weight: 'bold',
          family: 'Poppins'
        },
        bodyFont: {
          size: 14,
          family: 'Poppins'
        },
        callbacks: {
          label: function(context) {
            return `Number of Bodies: ${context.raw.toLocaleString()}`;
          }
        }
      }
    },
    scales: {
      y: {
        beginAtZero: true,
        grid: {
          color: 'rgba(71, 85, 105, 0.3)', // slate-500 with opacity
          drawBorder: false
        },
        ticks: {
          color: '#cbd5e1', // slate-300
          font: {
            family: 'Poppins',
            size: 12,
            weight: '500'
          },
          callback: function(value) {
            return value.toLocaleString();
          }
        },
        title: {
          display: true,
          text: 'Number of Bodies',
          color: '#38bdf8', // sky-400
          font: {
            size: 14,
            weight: 'bold',
            family: 'Poppins'
          },
          padding: {
            top: 10,
            bottom: 10
          }
        }
      },
      x: {
        grid: {
          color: 'rgba(71, 85, 105, 0.3)', // slate-500 with opacity
          drawBorder: false
        },
        ticks: {
          color: '#cbd5e1', // slate-300
          font: {
            family: 'Poppins',
            size: 12,
            weight: '500'
          }
        },
        title: {
          display: true,
          text: 'Year',
          color: '#38bdf8', // sky-400
          font: {
            size: 14,
            weight: 'bold',
            family: 'Poppins'
          },
          padding: {
            top: 10,
            bottom: 10
          }
        }
      }
    }
  };

  return (
    <div className="font-poppins"> {/* Removed bg-soft-white, will inherit from App.js main content area */}
      {/* Hero Section with Video Background */}
      <section className="relative min-h-[70vh] sm:min-h-[80vh] flex items-center justify-center overflow-hidden bg-gradient-to-br from-slate-800 to-slate-900">
        <div className="absolute inset-0 z-0">
          <video
            autoPlay
            loop
            muted
            playsInline
            className="w-full h-full object-cover"
            onPlay={handleVideoPlay}
            onPause={handleVideoPause}
          >
            <source src="/videos/about-hero.mp4" type="video/mp4" />
            <track
              kind="subtitles"
              src="/videos/about-hero-subtitles.vtt"
              srcLang="en"
              label="English"
              default
            />
            Your browser does not support the video tag.
          </video>
          <div className="absolute inset-0 bg-gradient-to-br from-slate-800/70 to-slate-900/70" /> {/* Adjusted opacity */}
        </div>
        <div className="relative z-10 max-w-4xl mx-auto text-center px-4 py-16 sm:py-24 animate-fade-in">
          <h1 className="text-4xl sm:text-5xl md:text-6xl font-merriweather font-bold text-sky-400 drop-shadow-lg mb-4 tracking-tight">
            About Avyakta
          </h1>
          <p className="text-lg sm:text-xl md:text-2xl text-slate-200 font-medium mb-8">
            Empowering communities to bring dignity to the unclaimed through technology and compassion.
          </p>
          <div className="flex justify-center gap-4">
            <button
              onClick={() => setIsPlaying(!isPlaying)}
              className="inline-flex items-center gap-2 rounded-full bg-sky-500 px-6 py-3 text-lg font-bold text-white shadow-lg hover:bg-sky-600 transition-all duration-300 hover:scale-105 focus:outline-none focus:ring-2 focus:ring-sky-400 focus:ring-offset-2 focus:ring-offset-slate-900"
            >
              {isPlaying ? (
                <>
                  <PauseIcon className="h-6 w-6" />
                  Pause Video
                </>
              ) : (
                <>
                  <PlayIcon className="h-6 w-6" />
                  Play Video
                </>
              )}
            </button>
          </div>
        </div>
      </section>

      {/* Story Section */}
      <section className="relative py-16 sm:py-20 bg-gradient-to-br from-slate-800 to-slate-900">
        <div className="absolute inset-0 bg-gradient-to-br from-slate-800/95 to-slate-900/95"></div> {/* Slightly less opacity */}
        <div className="relative max-w-6xl mx-auto px-4">
          <div className="text-center mb-12 sm:mb-16">
            <h2 className="text-3xl sm:text-4xl font-merriweather font-bold text-sky-400 mb-6">
              Every Unclaimed Life Tells a Story — Let's Not Ignore It Anymore
            </h2>
            <p className="text-slate-300 text-lg leading-relaxed max-w-3xl mx-auto mb-8">
              In India, every hour, several unidentified dead bodies are found — left unreported, unnamed, and untouched.
              Behind every such body lies a family in pain, a mystery unsolved, and a society that chose to look away.
            </p>
          </div>

          <div className="grid grid-cols-1 md:grid-cols-2 gap-10 sm:gap-12 mb-12 sm:mb-16">
            <div className="space-y-6">
              <h3 className="text-2xl font-bold text-sky-400 mb-4">Why this platform matters now:</h3>
              <ul className="space-y-4">
                <li className="flex items-start gap-3">
                  <span className="text-sky-400 text-xl">•</span>
                  <p className="text-slate-300">Increasing urban migration has left more people vulnerable to anonymity.</p>
                </li>
                <li className="flex items-start gap-3">
                  <span className="text-sky-400 text-xl">•</span>
                  <p className="text-slate-300">Police resources are stretched, making timely intervention harder.</p>
                </li>
                <li className="flex items-start gap-3">
                  <span className="text-sky-400 text-xl">•</span>
                  <p className="text-slate-300">People hesitate to report cases fearing involvement in legal procedures.</p>
                </li>
              </ul>
            </div>
            <div className="space-y-6">
              <h3 className="text-2xl font-bold text-sky-400 mb-4">But what if you could:</h3>
              <ul className="space-y-4">
                <li className="flex items-start gap-3">
                  <span className="text-sky-400 text-xl">•</span>
                  <p className="text-slate-300">Help anonymously, with just one click?</p>
                </li>
                <li className="flex items-start gap-3">
                  <span className="text-sky-400 text-xl">•</span>
                  <p className="text-slate-300">Save a life, bring closure, or stop a crime from happening again?</p>
                </li>
              </ul>
              <p className="text-slate-200 text-lg font-semibold mt-8">
                Let's change the way we respond. Let's be human first.
              </p>
            </div>
          </div>

          {/* Counters Section */}
          <div className="grid grid-cols-2 md:grid-cols-4 gap-6 sm:gap-8 mb-12 sm:mb-16">
            <div className="bg-slate-700/50 backdrop-blur-md rounded-xl p-6 text-center transform hover:scale-105 transition-all duration-300 cursor-pointer group shadow-lg">
              <h3 className="text-3xl font-bold text-sky-400 mb-2">{counters.reports}+</h3>
              <p className="text-slate-300 text-sm sm:text-base">Total Unclaimed Reports Handled</p>
              <p className="text-sky-500/80 text-xs sm:text-sm mt-2 opacity-0 group-hover:opacity-100 transition-opacity">Click to learn more</p>
            </div>
            <div className="bg-slate-700/50 backdrop-blur-md rounded-xl p-6 text-center transform hover:scale-105 transition-all duration-300 cursor-pointer group shadow-lg">
              <h3 className="text-3xl font-bold text-sky-400 mb-2">{counters.lives}</h3>
              <p className="text-slate-300 text-sm sm:text-base">Lives Saved Through Timely Alerts</p>
              <p className="text-sky-500/80 text-xs sm:text-sm mt-2 opacity-0 group-hover:opacity-100 transition-opacity">See success stories</p>
            </div>
            <div className="bg-slate-700/50 backdrop-blur-md rounded-xl p-6 text-center transform hover:scale-105 transition-all duration-300 cursor-pointer group shadow-lg">
              <h3 className="text-3xl font-bold text-sky-400 mb-2">{counters.volunteers}</h3>
              <p className="text-slate-300 text-sm sm:text-base">Active Volunteers Supporting</p>
              <p className="text-sky-500/80 text-xs sm:text-sm mt-2 opacity-0 group-hover:opacity-100 transition-opacity">Join our volunteers</p>
            </div>
            <div className="bg-slate-700/50 backdrop-blur-md rounded-xl p-6 text-center transform hover:scale-105 transition-all duration-300 cursor-pointer group shadow-lg">
              <h3 className="text-3xl font-bold text-sky-400 mb-2">{counters.stations}+</h3>
              <p className="text-slate-300 text-sm sm:text-base">Police Stations Connected</p>
              <p className="text-sky-500/80 text-xs sm:text-sm mt-2 opacity-0 group-hover:opacity-100 transition-opacity">View network map</p>
            </div>
          </div>

          {/* Graph Section */}
          <div className="bg-slate-700/50 backdrop-blur-md rounded-xl p-6 sm:p-8 shadow-xl">
            <div className="h-[350px] sm:h-[400px] mb-6">
              <Line data={chartData} options={chartOptions} />
            </div>
            <div className="text-center">
              <p className="text-red-500 text-lg font-semibold italic">
                "This graph isn't just data — it's lives left unclaimed. Let's bring them back into the story."
              </p>
              <div className="mt-4 flex flex-col sm:flex-row justify-center items-center gap-4">
                <div className="bg-slate-600/70 rounded-lg p-4 w-full sm:w-auto">
                  <p className="text-slate-300 text-sm">Total Reports</p>
                  <p className="text-sky-400 text-2xl font-bold">{counters.reports}+</p>
                </div>
                <div className="bg-slate-600/70 rounded-lg p-4 w-full sm:w-auto">
                  <p className="text-slate-300 text-sm">Yearly Increase</p>
                  <p className="text-sky-400 text-2xl font-bold">~15%</p> {/* Added ~ for approximation */}
                </div>
              </div>
            </div>
          </div>
        </div>
      </section>

      {/* Mission and Vision */}
      <section className="max-w-6xl mx-auto py-16 sm:py-20 px-4"> {/* Adjusted padding */}
        <div className="grid grid-cols-1 md:grid-cols-2 gap-10 sm:gap-12 items-center">
          <div className="space-y-6">
            <h2 className="text-3xl sm:text-4xl font-merriweather font-bold text-slate-800">
              Our Mission
            </h2>
            <p className="text-slate-700 text-lg leading-relaxed">
              At Avyakta, we believe that every individual deserves dignity in life and death.
              Our mission is to leverage technology to help identify and respond to unclaimed bodies
              with respect and compassion, while providing closure to families and communities.
            </p>
            <div className="flex flex-wrap gap-x-6 gap-y-3"> {/* Adjusted gap */}
              <div className="flex items-center gap-2 text-slate-700 font-medium">
                <HeartIcon className="h-6 w-6 text-amber-500" />
                <span>Compassion</span>
              </div>
              <div className="flex items-center gap-2 text-slate-700 font-medium">
                <ShieldCheckIcon className="h-6 w-6 text-amber-500" />
                <span>Dignity</span>
              </div>
              <div className="flex items-center gap-2 text-slate-700 font-medium">
                <UserGroupIcon className="h-6 w-6 text-amber-500" />
                <span>Community</span>
              </div>
              <div className="flex items-center gap-2 text-slate-700 font-medium">
                <GlobeAltIcon className="h-6 w-6 text-amber-500" />
                <span>Impact</span>
              </div>
            </div>
          </div>
          <div className="bg-gradient-to-br from-slate-700 to-slate-800 rounded-2xl p-8 text-slate-100 shadow-xl">
            <h3 className="text-2xl font-bold mb-4 text-sky-400">Our Vision</h3>
            <p className="text-lg leading-relaxed text-slate-200">
              To create a world where no life goes unnoticed, where every individual receives the dignity they deserve,
              and where technology serves as a bridge between the lost and their loved ones.
            </p>
          </div>
        </div>
      </section>

      {/* Impact Section */}
      <section className="bg-slate-800 py-16 sm:py-20"> {/* Adjusted padding */}
        <div className="max-w-6xl mx-auto px-4">
          <div className="text-center mb-12">
            <h2 className="text-3xl sm:text-4xl font-merriweather font-bold text-sky-400 mb-4">
              Our Impact
            </h2>
            <p className="text-slate-300 text-lg max-w-3xl mx-auto">
              Through our dedicated efforts, we have helped bring dignity to countless lives and provided closure to families across the nation.
            </p>
          </div>
          <div className="grid grid-cols-1 md:grid-cols-3 gap-6 sm:gap-8">
            <div className="bg-slate-700/50 backdrop-blur-md rounded-xl p-6 text-center transform hover:scale-105 transition-all duration-300 shadow-lg">
              <h3 className="text-2xl font-bold text-sky-400 mb-2">1000+</h3>
              <p className="text-slate-300">Cases Resolved</p>
            </div>
            <div className="bg-slate-700/50 backdrop-blur-md rounded-xl p-6 text-center transform hover:scale-105 transition-all duration-300 shadow-lg">
              <h3 className="text-2xl font-bold text-sky-400 mb-2">500+</h3>
              <p className="text-slate-300">Families Helped</p>
            </div>
            <div className="bg-slate-700/50 backdrop-blur-md rounded-xl p-6 text-center transform hover:scale-105 transition-all duration-300 shadow-lg">
              <h3 className="text-2xl font-bold text-sky-400 mb-2">50+</h3>
              <p className="text-slate-300">Cities Covered</p>
            </div>
          </div>
        </div>
      </section>

      {/* Team Section */}
      <section className="max-w-6xl mx-auto py-16 sm:py-20 px-4"> {/* Adjusted padding */}
        <div className="text-center mb-12">
          <h2 className="text-3xl sm:text-4xl font-merriweather font-bold text-slate-800 mb-4">
            Our Team
          </h2>
          <p className="text-slate-700 text-lg max-w-3xl mx-auto">
            Meet the dedicated individuals working tirelessly to make a difference.
          </p>
        </div>
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-8">
          {[
            {
              name: 'Dr. Rajesh Kumar',
              role: 'Founder & CEO',
              image: '/images/team/ceo.jpg',
              bio: '20+ years of experience in humanitarian work and technology innovation.'
            },
            {
              name: 'Priya Sharma',
              role: 'Head of Operations',
              image: '/images/team/operations.jpg',
              bio: 'Expert in NGO management and community development.'
            },
            {
              name: 'Amit Patel',
              role: 'Technical Lead',
              image: '/images/team/tech.jpg',
              bio: 'Passionate about using technology for social impact.'
            }
          ].map((member) => (
            <div
              key={member.name}
              className="bg-white rounded-xl shadow-lg overflow-hidden transform hover:scale-105 transition-all duration-300 hover:shadow-2xl"
            >
              <div className="aspect-w-3 aspect-h-4"> {/* Consider aspect-w-1 aspect-h-1 for square if images are so */}
                <img
                  src={member.image}
                  alt={member.name}
                  className="w-full h-full object-cover"
                />
              </div>
              <div className="p-6">
                <h3 className="text-xl font-bold text-slate-800 mb-1">{member.name}</h3>
                <p className="text-sky-600 font-semibold mb-3">{member.role}</p>
                <p className="text-slate-600 text-sm">{member.bio}</p> {/* Made bio text slightly smaller */}
              </div>
            </div>
          ))}
        </div>
      </section>

      {/* Call to Action */}
      <section className="bg-gradient-to-br from-slate-800 to-slate-900 py-16 sm:py-20"> {/* Adjusted padding */}
        <div className="max-w-4xl mx-auto text-center px-4">
          <h2 className="text-3xl sm:text-4xl font-merriweather font-bold text-sky-400 mb-6">
            Join Our Mission
          </h2>
          <p className="text-slate-300 text-lg mb-8">
            Together, we can bring dignity to the forgotten and make a lasting impact in our communities.
          </p>
          <div className="flex flex-col sm:flex-row gap-4 justify-center">
            <Link
              to="/how-it-works" // Ensure this route exists or update
              className="inline-flex items-center justify-center gap-2 rounded-full bg-gradient-to-r from-sky-500 to-sky-600 px-8 py-3 text-lg font-bold text-white shadow-lg hover:from-sky-600 hover:to-sky-700 transition-all duration-300 hover:scale-105 focus:outline-none focus:ring-2 focus:ring-sky-400 focus:ring-offset-2 focus:ring-offset-slate-900"
            >
              Know How it Works
            </Link>
            <Link
              to="/contact"
              className="inline-flex items-center justify-center gap-2 rounded-full bg-gradient-to-r from-amber-400 to-amber-500 px-8 py-3 text-lg font-bold text-slate-900 shadow-lg hover:from-amber-500 hover:to-amber-600 transition-all duration-300 hover:scale-105 focus:outline-none focus:ring-2 focus:ring-amber-400 focus:ring-offset-2 focus:ring-offset-slate-900"
            >
              Contact Us
            </Link>
          </div>
        </div>
      </section>
    </div>
  );
};

export default About;
