                    />
                    {errors.mobile && (
                      <p className="mt-2 text-sm text-red-300 flex items-center">
                        <svg className="h-4 w-4 mr-1" fill="currentColor" viewBox="0 0 20 20">
                          <path fillRule="evenodd" d="M18 10a8 8 0 11-16 0 8 8 0 0116 0zm-7 4a1 1 0 11-2 0 1 1 0 012 0zm-1-9a1 1 0 00-1 1v4a1 1 0 102 0V6a1 1 0 00-1-1z" clipRule="evenodd" />
                        </svg>
                        {errors.mobile}
                      </p>
                    )}
                  </div>

                  <div>
                    <label htmlFor="category" className="block text-sm font-medium text-gray-200 mb-2">
                      Inquiry Category *
                    </label>
                    <select
                      name="category"
                      id="category"
                      required
                      value={formData.category}
                      onChange={handleChange}
                      className={`w-full rounded-xl shadow-sm focus:ring-2 focus:ring-purple-400 transition-all duration-300 px-4 py-3 bg-white/20 backdrop-blur-sm border text-white ${
                        errors.category 
                          ? 'border-red-400 focus:border-red-400' 
                          : 'border-white/30 focus:border-purple-400'
                      }`}
                    >
                      <option value="" className="text-gray-800">Select a category for your inquiry</option>
                      <option value="general" className="text-gray-800">General Inquiry</option>
                      <option value="report" className="text-gray-800">Report a Case</option>
                      <option value="feedback" className="text-gray-800">Feedback & Suggestions</option>
                      <option value="emergency" className="text-gray-800">Emergency Contact</option>
                    </select>
                    {errors.category && (
                      <p className="mt-2 text-sm text-red-300 flex items-center">
                        <svg className="h-4 w-4 mr-1" fill="currentColor" viewBox="0 0 20 20">
                          <path fillRule="evenodd" d="M18 10a8 8 0 11-16 0 8 8 0 0116 0zm-7 4a1 1 0 11-2 0 1 1 0 012 0zm-1-9a1 1 0 00-1 1v4a1 1 0 102 0V6a1 1 0 00-1-1z" clipRule="evenodd" />
                        </svg>
                        {errors.category}
                      </p>
                    )}
                  </div>

                  <div>
                    <label htmlFor="message" className="block text-sm font-medium text-gray-200 mb-2">
                      Your Message *
                    </label>
                    <textarea
                      name="message"
                      id="message"
                      rows={4}
                      required
                      value={formData.message}
                      onChange={handleChange}
                      className={`w-full rounded-xl shadow-sm focus:ring-2 focus:ring-purple-400 transition-all duration-300 px-4 py-3 bg-white/20 backdrop-blur-sm border text-white placeholder-gray-300 ${
                        errors.message 
                          ? 'border-red-400 focus:border-red-400' 
                          : 'border-white/30 focus:border-purple-400'
                      }`}
                      placeholder="Please describe your inquiry in detail. We'll get back to you as soon as possible."
                    />
                    {errors.message && (
                      <p className="mt-2 text-sm text-red-300 flex items-center">
                        <svg className="h-4 w-4 mr-1" fill="currentColor" viewBox="0 0 20 20">
                          <path fillRule="evenodd" d="M18 10a8 8 0 11-16 0 8 8 0 0116 0zm-7 4a1 1 0 11-2 0 1 1 0 012 0zm-1-9a1 1 0 00-1 1v4a1 1 0 102 0V6a1 1 0 00-1-1z" clipRule="evenodd" />
                        </svg>
                        {errors.message}
                      </p>
                    )}
                  </div>

                  <div>
                    <button
                      type="submit"
                      disabled={isSubmitting}
                      className="w-full flex justify-center py-4 px-6 border border-transparent rounded-xl shadow-lg text-lg font-semibold text-white bg-gradient-to-r from-purple-600 to-pink-600 hover:from-purple-700 hover:to-pink-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-purple-500 transition-all duration-300 transform hover:scale-105 hover:shadow-xl disabled:opacity-50 disabled:cursor-not-allowed disabled:transform-none"
                    >
                      {isSubmitting ? (
                        <div className="flex items-center">
                          <svg className="animate-spin -ml-1 mr-3 h-5 w-5 text-white" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24">
                            <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
                            <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
                          </svg>
                          Submitting...
                        </div>
                      ) : (
                        'Send Message'
                      )}
                    </button>
                  </div>
                </form>
              </div>
            </div>

            {/* Contact Information & Map - Right Side */}
