import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { useTranslation } from 'react-i18next';
import { HeartIcon, CurrencyRupeeIcon, FireIcon } from '@heroicons/react/24/solid';

const Footer = () => {
  const { t, i18n } = useTranslation();
  const [fontSize, setFontSize] = useState(1);
  const [highContrast, setHighContrast] = useState(false);
  const [email, setEmail] = useState('');

  const handleFontSize = (delta) => {
    setFontSize(f => Math.max(0.8, Math.min(1.3, f + delta)));
    document.documentElement.style.fontSize = `${Math.max(0.8, Math.min(1.3, fontSize + delta))}em`;
  };

  const handleContrast = () => {
    setHighContrast(h => {
      document.body.classList.toggle('high-contrast', !h);
      return !h;
    });
  };

  const handleSubscribe = (e) => {
    e.preventDefault();
    setEmail('');
    alert('Thank you for subscribing!');
  };

  return (
    <footer className={`relative pt-12 pb-6 px-4 sm:px-8 bg-gradient-to-br from-[#181c3a] via-[#2a225d] to-[#3a2a6d] text-white ${highContrast ? 'contrast-150' : ''}`}
      style={{ fontFamily: 'Poppins, Inter, Nunito, sans-serif', boxShadow: '0 -2px 32px 0 #2a225d88' }}>
      {/* Blurred city/night skyline background (optional) */}
      <div className="absolute inset-0 z-0 pointer-events-none" style={{ background: 'radial-gradient(ellipse at bottom, #2a225d 60%, #181c3a 100%)', opacity: 0.7 }} />
      {/* Candle flame animation (center bottom) */}
      <div className="absolute left-1/2 bottom-0 transform -translate-x-1/2 z-20 flex flex-col items-center">
        <span className="block w-6 h-6">
          <svg width="24" height="24" viewBox="0 0 24 24" fill="none">
            <ellipse cx="12" cy="18" rx="3" ry="1.2" fill="#fff6" />
