import React, { useState, useRef, useEffect } from 'react';
import { PhoneIcon, ChatBubbleLeftRightIcon, XMarkIcon, PaperAirplaneIcon, ExclamationTriangleIcon, ShieldExclamationIcon } from '@heroicons/react/24/outline';

const FloatingActionButton = () => {
  const [isFabExpanded, setIsFabExpanded] = useState(false);
  const [isChatOpen, setIsChatOpen] = useState(false);
  const [messages, setMessages] = useState([]);
  const [inputMessage, setInputMessage] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const chatEndRef = useRef(null);

  const scrollToBottom = () => {
    chatEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  // Clear chat history when opening chat
  useEffect(() => {
    if (isChatOpen) {
      setMessages([{
        type: 'bot',
        text: "Hi, I'm Avyakta Assistant. I'm here to help you with any situation you're facing. How can I support you today?",
        timestamp: new Date()
      }]);
    }
  }, [isChatOpen]);

  const getResponse = (message) => {
    const lowerMessage = message.toLowerCase();
    
    // Emergency situations
    if (lowerMessage.includes('emergency') || lowerMessage.includes('urgent') || lowerMessage.includes('help')) {
      return "This sounds urgent. Please call emergency services (100) immediately. While help is on the way, I can guide you through what to do next. Are you in a safe location?";
    }
    
    // Reporting unclaimed bodies
    if (lowerMessage.includes('dead') || lowerMessage.includes('body') || lowerMessage.includes('found')) {
      return "I understand this is a difficult situation. First, please stay calm. Are you in a safe location? If yes, please call emergency services (100) immediately. I'll guide you through the next steps while help arrives.";
    }
    
    // Medical emergencies
    if (lowerMessage.includes('alive') || lowerMessage.includes('breathing') || lowerMessage.includes('conscious')) {
      return "If the person is alive but in need of help, please call emergency services (100) right away. Stay with them if it's safe to do so. Can you tell me more about their condition?";
    }
    
    // Emotional support
    if (lowerMessage.includes('scared') || lowerMessage.includes('afraid') || lowerMessage.includes('nervous')) {
      return "It's completely normal to feel this way. You're doing the right thing by seeking help. Take a deep breath. I'm here to support you. Would you like me to guide you through what to do next?";
    }
    
    // Location specific
    if (lowerMessage.includes('location') || lowerMessage.includes('place') || lowerMessage.includes('where')) {
      return "Please provide the location details if it's safe to do so. This will help emergency services respond quickly. Are you able to share the address or nearby landmarks?";
    }
    
    // Time related
    if (lowerMessage.includes('when') || lowerMessage.includes('time') || lowerMessage.includes('duration')) {
      return "Time is crucial in emergency situations. Please call emergency services (100) immediately. While help is on the way, I can guide you through what to do next.";
    }
    
    // General information
    if (lowerMessage.includes('what') || lowerMessage.includes('how') || lowerMessage.includes('where')) {
      return "I can help you with information about reporting unclaimed bodies, emergency procedures, and support resources. What specific information are you looking for?";
    }
    
    // Safety check
    if (lowerMessage.includes('safe') || lowerMessage.includes('danger') || lowerMessage.includes('risk')) {
      return "Your safety is the top priority. If you feel unsafe, please move to a safe location and call emergency services (100). Would you like me to guide you through safety procedures?";
    }
    
    // Support resources
    if (lowerMessage.includes('support') || lowerMessage.includes('help') || lowerMessage.includes('resource')) {
      return "We have several support resources available. You can call our 24/7 helpline at 100, or visit our website for more information. Would you like specific details about any of these resources?";
    }
    
    // Default response
    return "I'm here to help you through this situation. Please let me know what you're seeing or experiencing, and I'll guide you through the next steps. Remember, you're not alone in this.";
  };

  const handleSendMessage = async () => {
    if (!inputMessage.trim()) return;

    // Add user message
    setMessages(prev => [...prev, { 
      type: 'user', 
      text: inputMessage,
      timestamp: new Date()
    }]);
    
    setIsLoading(true);
    
    try {
      // Get response using our local function
      const response = getResponse(inputMessage);
      
      // Add a small delay to make the conversation feel more natural
      await new Promise(resolve => setTimeout(resolve, 500));
      
      setMessages(prev => [...prev, { 
        type: 'bot', 
        text: response,
        timestamp: new Date()
      }]);
    } catch (error) {
      setMessages(prev => [...prev, { 
        type: 'bot', 
        text: "I'm here to help you. Please let me know what you're experiencing, and I'll guide you through the next steps.",
        timestamp: new Date()
      }]);
    } finally {
      setIsLoading(false);
      setInputMessage('');
    }
  };

  const formatTime = (date) => {
    return date.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' });
  };

  return (
    <>
      {/* Floating Action Button Group */}
      <div className="fixed bottom-24 right-6 z-50">
        {/* Main FAB */}
        <button
          onClick={() => setIsFabExpanded(!isFabExpanded)}
          className={`w-16 h-16 rounded-full bg-[#003f88] text-white shadow-lg flex items-center justify-center transition-all duration-300 transform hover:scale-110 ${
            isFabExpanded ? 'rotate-45' : ''
          }`}
        >
          {isFabExpanded ? (
            <XMarkIcon className="w-7 h-7" />
          ) : (
            <ShieldExclamationIcon className="w-7 h-7" />
          )}
        </button>

        {/* Expanded Options */}
        <div
          className={`absolute bottom-20 right-0 flex flex-col gap-4 transition-all duration-300 ${
            isFabExpanded ? 'opacity-100 translate-y-0' : 'opacity-0 translate-y-4 pointer-events-none'
          }`}
        >
          {/* Chat Button */}
          <button
            onClick={() => setIsChatOpen(!isChatOpen)}
            className="w-14 h-14 rounded-full bg-blue-500 text-white shadow-lg flex items-center justify-center transform hover:scale-110 transition-all duration-300"
          >
            <ChatBubbleLeftRightIcon className="w-6 h-6" />
          </button>

          {/* SOS Button */}
          <button
            onClick={() => {
              window.location.href = 'tel:100';
            }}
            className="w-14 h-14 rounded-full bg-red-600 text-white shadow-lg flex items-center justify-center transform hover:scale-110 transition-all duration-300 animate-pulse"
          >
            <span className="text-sm font-bold">SOS</span>
          </button>
        </div>
      </div>

      {/* Chat Box - Moved to left side */}
      <div
        className={`fixed bottom-32 left-6 w-80 h-96 bg-white rounded-lg shadow-xl transition-all duration-300 transform ${
          isChatOpen ? 'opacity-100 translate-y-0' : 'opacity-0 translate-y-4 pointer-events-none'
        }`}
      >
        <div className="h-full flex flex-col">
          {/* Chat Header */}
          <div className="bg-[#003f88] text-white p-4 rounded-t-lg flex justify-between items-center">
            <div className="flex items-center gap-2">
              <div className="w-8 h-8 bg-white rounded-full flex items-center justify-center">
                <ShieldExclamationIcon className="w-5 h-5 text-[#003f88]" />
              </div>
              <h3 className="font-semibold">Avyakta Assistant</h3>
            </div>
            <button
              onClick={() => setIsChatOpen(false)}
              className="text-white hover:text-gray-200"
            >
              <XMarkIcon className="w-5 h-5" />
            </button>
          </div>

          {/* Chat Messages Area */}
          <div className="flex-1 p-4 overflow-y-auto bg-gray-50">
            <div className="space-y-4">
              {messages.map((message, index) => (
                <div
                  key={index}
                  className={`flex ${message.type === 'user' ? 'justify-end' : 'justify-start'}`}
                >
                  <div className="flex flex-col max-w-[80%]">
                    <div
                      className={`rounded-lg p-3 ${
                        message.type === 'user'
                          ? 'bg-[#003f88] text-white'
                          : 'bg-blue-100 text-gray-800'
                      }`}
                    >
                      <p className="text-sm whitespace-pre-line">{message.text}</p>
                    </div>
                    <span className="text-xs text-gray-500 mt-1">
                      {formatTime(message.timestamp)}
                    </span>
                  </div>
                </div>
              ))}
              {isLoading && (
                <div className="flex justify-start">
                  <div className="bg-blue-100 rounded-lg p-3">
                    <div className="flex space-x-2">
                      <div className="w-2 h-2 bg-blue-500 rounded-full animate-bounce"></div>
                      <div className="w-2 h-2 bg-blue-500 rounded-full animate-bounce delay-100"></div>
                      <div className="w-2 h-2 bg-blue-500 rounded-full animate-bounce delay-200"></div>
                    </div>
                  </div>
                </div>
              )}
              <div ref={chatEndRef} />
            </div>
          </div>

          {/* Chat Input */}
          <div className="p-4 border-t">
            <div className="flex gap-2">
              <input
                type="text"
                value={inputMessage}
                onChange={(e) => setInputMessage(e.target.value)}
                onKeyPress={(e) => e.key === 'Enter' && !isLoading && handleSendMessage()}
                placeholder="Type your message..."
                className="flex-1 px-4 py-2 border rounded-full focus:outline-none focus:ring-2 focus:ring-[#003f88]"
                disabled={isLoading}
              />
              <button
                onClick={handleSendMessage}
                disabled={isLoading}
                className={`bg-[#003f88] text-white px-4 py-2 rounded-full transition-colors ${
                  isLoading ? 'opacity-50 cursor-not-allowed' : 'hover:bg-blue-700'
                }`}
              >
                <PaperAirplaneIcon className="w-5 h-5" />
              </button>
            </div>
          </div>
        </div>
      </div>
    </>
  );
};

export default FloatingActionButton;
