import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { useTranslation } from 'react-i18next';
import { Disclosure } from '@headlessui/react';
import { Bars3Icon, XMarkIcon } from '@heroicons/react/24/outline';
import logo from '../assets/logo.png';

const Navbar = () => {
  const { t, i18n } = useTranslation();
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [showLanguageModal, setShowLanguageModal] = useState(false);
  const [newLanguage, setNewLanguage] = useState('');

  const handleLanguageChange = () => {
    const newLang = i18n.language === 'en' ? 'hi' : 'en';
    setNewLanguage(newLang);
    setShowLanguageModal(true);
  };

  const confirmLanguageChange = () => {
    i18n.changeLanguage(newLanguage);
    setShowLanguageModal(false);
  };

  const cancelLanguageChange = () => {
    setShowLanguageModal(false);
  };

  return (
    <>
      <Disclosure as="nav" className="bg-gradient-primary shadow-lg font-poppins">
        {({ open }) => (
          <>
            <div className="mx-auto max-w-7xl px-4 sm:px-6 lg:px-8">
              <div className="flex h-20 sm:h-24 items-center justify-between">
                <div className="flex items-center w-full">
                  <Link to="/" className="flex items-center mr-4 sm:mr-6">
                    <img
                      className="h-12 sm:h-16 w-auto object-contain"
                      src={logo}
                      alt="Logo"
                    />
                  </Link>
                  <div className="hidden sm:flex flex-1 justify-center items-center">
                    <Link
                      to="/contact"
                      className="inline-flex items-center px-6 py-3 text-base font-medium text-white bg-sky-600 rounded-md"
                    >
                      Let's Chat
                    </Link>
                  </div>
                </div>
                <div className="hidden sm:flex items-center space-x-3">
                  <button
                    onClick={handleLanguageChange}
                    className="p-2 rounded-full bg-gradient-to-br from-sky-600 to-cyan-600 text-white"
                    aria-label="Switch Language"
                  >
                    {i18n.language === 'en' ? 'हिंदी' : 'English'}
                  </button>
                </div>
                <div className="flex items-center sm:hidden">
                  <Disclosure.Button className="inline-flex items-center justify-center rounded-md p-2 text-slate-200">
                    <span className="sr-only">Open main menu</span>
                    {open ? (
                      <XMarkIcon className="block h-6 w-6" aria-hidden="true" />
                    ) : (
                      <Bars3Icon className="block h-6 w-6" aria-hidden="true" />
                    )}
                  </Disclosure.Button>
                </div>
              </div>
            </div>

            <Disclosure.Panel className="sm:hidden">
              <div className="px-2 pt-2 pb-3 space-y-1 bg-gradient-dark">
                <Disclosure.Button
                  as={Link}
                  to="/contact"
                  className="block px-3 py-2 rounded-md text-base font-medium text-white bg-sky-600"
                >
                  Let's Chat
                </Disclosure.Button>
                <div className="pt-4 pb-3 border-t border-primary/20">
                  <div className="flex items-center justify-center px-3">
                    <button
                      onClick={handleLanguageChange}
                      className="w-full max-w-xs flex justify-center px-4 py-2 rounded-full bg-gradient-primary text-secondary-dark"
                    >
                      {i18n.language === 'en' ? 'हिंदी' : 'English'}
                    </button>
                  </div>
                </div>
              </div>
            </Disclosure.Panel>
          </>
        )}
      </Disclosure>

      {/* Language Change Modal */}
      {showLanguageModal && (
        <div className="fixed inset-0 bg-black/60 backdrop-blur-sm flex items-center justify-center z-50 p-4">
          <div className="bg-slate-50 rounded-xl p-6 sm:p-8 max-w-md w-full shadow-2xl">
            <h3 className="text-xl font-semibold text-slate-800 mb-3">Change Language</h3>
            <p className="text-slate-600 mb-6">Are you sure you want to change the language to {newLanguage === 'en' ? 'English' : 'हिंदी'}?</p>
            <div className="flex justify-end space-x-3">
              <button
                onClick={cancelLanguageChange}
                className="px-5 py-2 text-sm font-medium text-slate-700 bg-slate-200 rounded-lg"
              >
                Cancel
              </button>
              <button
                onClick={confirmLanguageChange}
                className="px-5 py-2 text-sm font-medium text-white bg-sky-600 rounded-lg"
              >
                Confirm
              </button>
            </div>
          </div>
        </div>
      )}
    </>
  );
};

export default Navbar;
