import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useTranslation } from 'react-i18next';
import PasswordRecovery from './PasswordRecovery';
import {
  UserIcon,
  LockClosedIcon,
  EnvelopeIcon,
  PhoneIcon,
  PhotoIcon,
  DocumentTextIcon,
  UserGroupIcon,
  EyeIcon,
  EyeSlashIcon,
  ShieldCheckIcon
} from '@heroicons/react/24/outline';

const AdminAuth = () => {
  const navigate = useNavigate();
  const { t } = useTranslation();
  const [isLogin, setIsLogin] = useState(true);
  const [showForgot, setShowForgot] = useState(false);
  const [showSuccessModal, setShowSuccessModal] = useState(false);
  const [showPassword, setShowPassword] = useState(false);
  const [showConfirmPassword, setShowConfirmPassword] = useState(false);
  const [registrationStep, setRegistrationStep] = useState(1); // 1: Basic info, 2: OTP verification, 3: Final details
  const [otp, setOtp] = useState('');
  const [securityCode, setSecurityCode] = useState('');
  const [generatedSecurityCode] = useState('AVY123'); // This would be generated dynamically
  const [otpLoading, setOtpLoading] = useState(false);
  const [otpError, setOtpError] = useState('');
  const [formData, setFormData] = useState({
    firstName: '',
    middleName: '',
    lastName: '',
    mobile: '',
    email: '',
    password: '',
    confirmPassword: '',
    role: '',
    profilePic: null,
    verificationDoc: null,
    terms: false
  });

  const API_KEY = 'VAf86a11af05d0b85e0aa3171658a5e23f';

  const roles = [
    { value: 'admin', label: 'Admin' },
    { value: 'ngo', label: 'NGO' },
    { value: 'common', label: 'Common User' }
  ];

  const handleInputChange = (e) => {
    const { name, value, type, files, checked } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: type === 'file' ? files[0] : type === 'checkbox' ? checked : value
    }));
  };

  const sendOTP = async (mobileNumber, email) => {
    setOtpLoading(true);
    setOtpError('');

    try {
      // Send OTP via SMS
      const smsResponse = await fetch('https://api.textlocal.in/send/', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          apikey: API_KEY,
          numbers: mobileNumber,
          message: `Your Avyakta verification code is: ${generateOTP()}. Valid for 10 minutes.`,
          sender: 'AVYAKT'
        })
      });

      // Send OTP via Email
      const emailResponse = await fetch('https://api.emailjs.com/api/v1.0/email/send', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          service_id: 'your_service_id',
          template_id: 'your_template_id',
          user_id: 'your_user_id',
          template_params: {
            to_email: email,
            otp: generateOTP(),
            user_name: `${formData.firstName} ${formData.lastName}`
          }
        })
      });

      if (smsResponse.ok || emailResponse.ok) {
        return true;
      } else {
        throw new Error('Failed to send OTP');
      }
    } catch (error) {
      console.error('OTP sending error:', error);
      setOtpError('Failed to send OTP. Please try again.');
      return false;
    } finally {
      setOtpLoading(false);
    }
  };

  const generateOTP = () => {
    return Math.floor(100000 + Math.random() * 900000).toString();
  };

  const handleSendOTP = async () => {
    if (!formData.mobile || !formData.mobile.trim()) {
      setOtpError('Please enter a valid mobile number');
      return;
    }

    // Validate mobile number format (Indian mobile numbers)
    const mobileRegex = /^[6-9]\d{9}$/;
    if (!mobileRegex.test(formData.mobile.replace(/\s/g, ''))) {
      setOtpError('Please enter a valid 10-digit mobile number');
      return;
    }

    const success = await sendOTP(formData.mobile, formData.email);
    if (success) {
      setRegistrationStep(2);
    }
  };

  const handleVerifyOTP = async (e) => {
    e.preventDefault();
    setOtpError('');

    if (!otp || otp.length !== 6) {
      setOtpError('Please enter a valid 6-digit OTP');
      return;
    }

    // Here you would typically verify the OTP with your backend
    // For now, we'll simulate verification
    try {
      const response = await fetch('http://localhost:5000/api/auth/verify-otp', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          mobile: formData.mobile,
          otp: otp,
          apiKey: API_KEY
        })
      });

      const data = await response.json();

      if (response.ok) {
        setRegistrationStep(3);
      } else {
        setOtpError(data.message || 'Invalid OTP. Please try again.');
      }
    } catch (error) {
      // Fallback for demo purposes
      if (otp === '123456') {
        setRegistrationStep(3);
      } else {
        setOtpError('Invalid OTP. Please try again.');
      }
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (formData.password !== formData.confirmPassword) {
      alert('Passwords do not match!');
      return;
    }

    if (securityCode !== generatedSecurityCode) {
      alert('Security code is incorrect!');
      return;
    }

    if (!formData.terms) {
      alert('Please agree to the terms and conditions');
      return;
    }

    // Add your API call here
    console.log('Form submitted:', formData);
    setShowSuccessModal(true);
  };

  const handleSuccessModalClose = () => {
    setShowSuccessModal(false);
    navigate('/');
  };

  return (
    <>
      <div className="min-h-screen flex items-center justify-center relative font-poppins p-4">
        {/* Background image with overlay */}
        <div className="absolute inset-0 z-0">
          <img src="/images/admin-bg.jpg" alt="Background" className="w-full h-full object-cover" />
          <div className="absolute inset-0 bg-gradient-to-br from-slate-900/90 to-blue-900/90 backdrop-blur-sm" />
        </div>
        <div className="max-w-md w-full space-y-6 sm:space-y-8 bg-white/90 p-6 sm:p-8 rounded-2xl shadow-2xl z-10 animate-fade-in">
          {/* Logo and Title */}
          <div className="text-center">
            <img className="mx-auto h-12 sm:h-16 w-auto" src="/images/logo.png" alt="Avyakta" />
            <h2 className="mt-4 sm:mt-6 text-2xl sm:text-3xl font-merriweather font-bold text-[#003f88]">
              {isLogin ? 'Admin Login' : 'Admin Registration'}
            </h2>
            <p className="mt-2 text-sm sm:text-base text-gray-600">
              {isLogin ? 'Sign in to your account' : 'Create a new admin/NGO/common account'}
            </p>
          </div>

          {/* Toggle Buttons */}
          <div className="flex rounded-lg bg-gray-100 p-1">
            <button
              onClick={() => { setIsLogin(true); setShowForgot(false); setRegistrationStep(1); setOtpError(''); }}
              className={`flex-1 py-2 px-3 sm:px-4 rounded-md text-sm font-medium transition-all duration-300 ${
                isLogin ? 'bg-white text-blue-600 shadow-sm' : 'text-gray-500 hover:text-gray-700'
              }`}
            >
              {t('Admin.login')}
            </button>
            <button
              onClick={() => { setIsLogin(false); setShowForgot(false); setRegistrationStep(1); setOtpError(''); }}
              className={`flex-1 py-2 px-3 sm:px-4 rounded-md text-sm font-medium transition-all duration-300 ${
                !isLogin ? 'bg-white text-blue-600 shadow-sm' : 'text-gray-500 hover:text-gray-700'
              }`}
            >
              {t('User.register')}
            </button>
          </div>

          {/* Login Form */}
          {isLogin && !showForgot && (
            <form onSubmit={handleSubmit} className="space-y-4 sm:space-y-6">
              <div>
                <label htmlFor="email" className="block text-sm font-medium text-gray-700 mb-1 flex items-center gap-2">
                  <EnvelopeIcon className="h-5 w-5 text-[#003f88]" />
                  Email
                </label>
                <input
                  id="email"
                  name="email"
                  type="email"
                  required
                  value={formData.email}
                  onChange={handleInputChange}
                  className="w-full px-3 sm:px-4 py-2 sm:py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500 transition-all duration-300"
                  placeholder="Enter your email"
                />
              </div>
              <div>
                <label htmlFor="password" className="block text-sm font-medium text-gray-700 mb-1 flex items-center gap-2">
                  <LockClosedIcon className="h-5 w-5 text-[#003f88]" />
                  Password
                </label>
                <div className="relative">
                  <input
                    id="password"
                    name="password"
                    type={showPassword ? 'text' : 'password'}
                    required
                    value={formData.password}
                    onChange={handleInputChange}
                    className="w-full px-3 sm:px-4 py-2 sm:py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500 transition-all duration-300 pr-10"
                    placeholder="Enter your password"
                  />
                  <button
                    type="button"
                    onClick={() => setShowPassword(!showPassword)}
                    className="absolute inset-y-0 right-0 pr-3 flex items-center text-gray-500 hover:text-[#003f88]"
                  >
                    {showPassword ? (
                      <EyeSlashIcon className="h-5 w-5" />
                    ) : (
                      <EyeIcon className="h-5 w-5" />
                    )}
                  </button>
                </div>
              </div>
              <div className="flex items-center justify-between">
                <button
                  type="button"
                  onClick={() => setShowForgot(true)}
                  className="text-sm text-blue-600 hover:text-blue-500 transition-colors duration-200"
                >
                  Forgot password?
                </button>
              </div>
              <button
                type="submit"
                className="w-full py-2 sm:py-3 px-4 border border-transparent rounded-lg shadow-sm text-sm sm:text-base font-medium text-white bg-blue-600 hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500 transition-all duration-300"
              >
                Sign in
              </button>
            </form>
          )}

          {/* Registration Form - Step 1: Basic Info */}
          {!isLogin && registrationStep === 1 && (
            <div className="space-y-4 sm:space-y-6">
              <div className="text-center mb-4">
                <h3 className="text-lg font-semibold text-[#003f88]">Step 1: Personal Information</h3>
                <p className="text-sm text-gray-600">Enter your basic details</p>
              </div>

              <div className="grid grid-cols-1 sm:grid-cols-3 gap-3">
                <div>
                  <label htmlFor="firstName" className="block text-sm font-medium text-gray-700 mb-1 flex items-center gap-2">
                    <UserIcon className="h-4 w-4 text-[#003f88]" />
                    First Name
                  </label>
                  <input
                    id="firstName"
                    name="firstName"
                    type="text"
                    required
                    value={formData.firstName}
                    onChange={handleInputChange}
                    className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500 transition-all duration-300"
                    placeholder="First"
                  />
                </div>
                <div>
                  <label htmlFor="middleName" className="block text-sm font-medium text-gray-700 mb-1">
                    Middle Name
                  </label>
                  <input
                    id="middleName"
                    name="middleName"
                    type="text"
                    value={formData.middleName}
                    onChange={handleInputChange}
                    className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500 transition-all duration-300"
                    placeholder="Middle"
                  />
                </div>
                <div>
                  <label htmlFor="lastName" className="block text-sm font-medium text-gray-700 mb-1">
                    Last Name
                  </label>
                  <input
                    id="lastName"
                    name="lastName"
                    type="text"
                    required
                    value={formData.lastName}
                    onChange={handleInputChange}
                    className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500 transition-all duration-300"
                    placeholder="Last"
                  />
                </div>
              </div>

              <div>
                <label htmlFor="mobile" className="block text-sm font-medium text-gray-700 mb-1 flex items-center gap-2">
                  <PhoneIcon className="h-5 w-5 text-[#003f88]" />
                  Mobile Number
                </label>
                <div className="flex gap-2">
                  <input
                    id="mobile"
                    name="mobile"
                    type="tel"
                    required
                    value={formData.mobile}
                    onChange={handleInputChange}
                    className="flex-1 px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500 transition-all duration-300"
                    placeholder="Enter your mobile number"
                  />
                  <button
                    type="button"
                    onClick={handleSendOTP}
                    disabled={otpLoading}
                    className="px-4 py-2 bg-[#003f88] text-white rounded-lg hover:bg-blue-700 transition-all duration-300 text-sm font-medium disabled:opacity-50 disabled:cursor-not-allowed"
                  >
                    {otpLoading ? (
                      <div className="flex items-center">
                        <svg className="animate-spin -ml-1 mr-2 h-4 w-4 text-white" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24">
                          <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
                          <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
                        </svg>
                        Sending...
                      </div>
                    ) : (
                      'Send OTP'
                    )}
                  </button>
                </div>
                {otpError && (
                  <p className="mt-2 text-sm text-red-600 flex items-center">
                    <svg className="h-4 w-4 mr-1" fill="currentColor" viewBox="0 0 20 20">
                      <path fillRule="evenodd" d="M18 10a8 8 0 11-16 0 8 8 0 0116 0zm-7 4a1 1 0 11-2 0 1 1 0 012 0zm-1-9a1 1 0 00-1 1v4a1 1 0 102 0V6a1 1 0 00-1-1z" clipRule="evenodd" />
                    </svg>
                    {otpError}
                  </p>
                )}
              </div>

              <button
                type="button"
                onClick={() => setRegistrationStep(2)}
                disabled={!formData.mobile || otpLoading}
                className="w-full py-2 sm:py-3 px-4 border border-transparent rounded-lg shadow-sm text-sm sm:text-base font-medium text-white bg-[#003f88] hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500 transition-all duration-300 disabled:opacity-50 disabled:cursor-not-allowed"
              >
                Continue
              </button>
            </div>
          )}

          {/* Registration Form - Step 2: OTP Verification */}
          {!isLogin && registrationStep === 2 && (
            <form onSubmit={handleVerifyOTP} className="space-y-4 sm:space-y-6">
              <div className="text-center mb-4">
                <h3 className="text-lg font-semibold text-[#003f88]">Step 2: OTP Verification</h3>
                <p className="text-sm text-gray-600">
                  OTP has been sent to <span className="font-semibold">{formData.mobile}</span>
                </p>
              </div>

              <div>
                <label htmlFor="otp" className="block text-sm font-medium text-gray-700 mb-1">
                  Enter OTP
                </label>
                <input
                  id="otp"
                  type="text"
                  value={otp}
                  onChange={(e) => setOtp(e.target.value)}
                  className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500 transition-all duration-300 text-center text-lg tracking-widest"
                  placeholder="Enter 6-digit OTP"
                  maxLength="6"
                  required
                />
              </div>

              {otpError && (
                <div className="p-3 bg-red-100 text-red-700 rounded-md flex items-center">
                  <svg className="h-5 w-5 mr-2" fill="currentColor" viewBox="0 0 20 20">
                    <path fillRule="evenodd" d="M18 10a8 8 0 11-16 0 8 8 0 0116 0zm-7 4a1 1 0 11-2 0 1 1 0 012 0zm-1-9a1 1 0 00-1 1v4a1 1 0 102 0V6a1 1 0 00-1-1z" clipRule="evenodd" />
                  </svg>
                  {otpError}
                </div>
              )}

              <div className="flex gap-2">
                <button
                  type="button"
                  onClick={() => setRegistrationStep(1)}
                  className="flex-1 py-2 px-4 border border-gray-300 rounded-lg text-sm font-medium text-gray-700 hover:bg-gray-50 transition-all duration-300"
                >
                  ← Back
                </button>
                <button
                  type="submit"
                  className="flex-1 py-2 px-4 border border-transparent rounded-lg shadow-sm text-sm font-medium text-white bg-[#003f88] hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500 transition-all duration-300"
                >
                  Verify OTP
                </button>
              </div>
            </form>
          )}

          {/* Registration Form - Step 3: Final Details */}
          {!isLogin && registrationStep === 3 && (
            <form onSubmit={handleSubmit} className="space-y-4 sm:space-y-6">
              <div className="text-center mb-4">
                <h3 className="text-lg font-semibold text-[#003f88]">Step 3: Complete Registration</h3>
                <p className="text-sm text-gray-600">OTP verified successfully! Complete your registration.</p>
              </div>

              <div>
                <label htmlFor="password" className="block text-sm font-medium text-gray-700 mb-1 flex items-center gap-2">
                  <LockClosedIcon className="h-5 w-5 text-[#003f88]" />
                  Password
                </label>
                <div className="relative">
                  <input
                    id="password"
                    name="password"
                    type={showPassword ? 'text' : 'password'}
                    required
                    value={formData.password}
                    onChange={handleInputChange}
                    className="w-full px-3 sm:px-4 py-2 sm:py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500 transition-all duration-300 pr-10"
                    placeholder="Create a password"
                  />
                  <button
                    type="button"
                    onClick={() => setShowPassword(!showPassword)}
                    className="absolute inset-y-0 right-0 pr-3 flex items-center text-gray-500 hover:text-[#003f88]"
                  >
                    {showPassword ? (
                      <EyeSlashIcon className="h-5 w-5" />
                    ) : (
                      <EyeIcon className="h-5 w-5" />
                    )}
                  </button>
                </div>
              </div>

              <div>
                <label htmlFor="confirmPassword" className="block text-sm font-medium text-gray-700 mb-1 flex items-center gap-2">
                  <LockClosedIcon className="h-5 w-5 text-[#003f88]" />
                  Confirm Password
                </label>
                <div className="relative">
                  <input
                    id="confirmPassword"
                    name="confirmPassword"
                    type={showConfirmPassword ? 'text' : 'password'}
                    required
                    value={formData.confirmPassword}
                    onChange={handleInputChange}
                    className="w-full px-3 sm:px-4 py-2 sm:py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500 transition-all duration-300 pr-10"
                    placeholder="Confirm your password"
                  />
                  <button
                    type="button"
                    onClick={() => setShowConfirmPassword(!showConfirmPassword)}
                    className="absolute inset-y-0 right-0 pr-3 flex items-center text-gray-500 hover:text-[#003f88]"
                  >
                    {showConfirmPassword ? (
                      <EyeSlashIcon className="h-5 w-5" />
                    ) : (
                      <EyeIcon className="h-5 w-5" />
                    )}
                  </button>
                </div>
              </div>

              <div>
                <label htmlFor="email" className="block text-sm font-medium text-gray-700 mb-1 flex items-center gap-2">
                  <EnvelopeIcon className="h-5 w-5 text-[#003f88]" />
                  Email Address
                </label>
                <input
                  id="email"
                  name="email"
                  type="email"
                  required
                  value={formData.email}
                  onChange={handleInputChange}
                  className="w-full px-3 sm:px-4 py-2 sm:py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500 transition-all duration-300"
                  placeholder="Enter your email address"
                />
              </div>

              <div>
                <label htmlFor="role" className="block text-sm font-medium text-gray-700 mb-1 flex items-center gap-2">
                  <UserGroupIcon className="h-5 w-5 text-[#003f88]" />
                  Role
                </label>
                <select
                  id="role"
                  name="role"
                  required
                  value={formData.role}
                  onChange={handleInputChange}
                  className="w-full px-3 sm:px-4 py-2 sm:py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500 transition-all duration-300"
                >
                  <option value="">Select your role</option>
                  {roles.map(role => (
                    <option key={role.value} value={role.value}>{role.label}</option>
                  ))}
                </select>
              </div>

              <div>
                <label htmlFor="securityCode" className="block text-sm font-medium text-gray-700 mb-1 flex items-center gap-2">
                  <ShieldCheckIcon className="h-5 w-5 text-[#003f88]" />
                  Security Code
                </label>
                <div className="mb-2 p-3 bg-blue-50 border border-blue-200 rounded-lg">
                  <p className="text-sm text-blue-800 font-mono text-center text-lg">
                    {generatedSecurityCode}
                  </p>
                  <p className="text-xs text-blue-600 text-center mt-1">Type this code for high security</p>
                </div>
                <input
                  id="securityCode"
                  type="text"
                  value={securityCode}
                  onChange={(e) => setSecurityCode(e.target.value)}
                  className="w-full px-3 sm:px-4 py-2 sm:py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500 transition-all duration-300"
                  placeholder="Enter the security code above"
                  required
                />
              </div>

              <div className="flex items-center">
                <input
                  id="terms"
                  name="terms"
                  type="checkbox"
                  checked={formData.terms}
                  onChange={handleInputChange}
                  className="h-4 w-4 text-[#003f88] focus:ring-[#003f88] border-gray-300 rounded"
                />
                <label htmlFor="terms" className="ml-2 block text-sm text-gray-700">
                  I agree to the <a href="#" className="text-[#003f88] hover:underline">User Agreement</a> and <a href="#" className="text-[#003f88] hover:underline">Terms and Conditions</a>
                </label>
              </div>

              <div className="flex gap-2">
                <button
                  type="button"
                  onClick={() => setRegistrationStep(2)}
                  className="flex-1 py-2 px-4 border border-gray-300 rounded-lg text-sm font-medium text-gray-700 hover:bg-gray-50 transition-all duration-300"
                >
                  ← Back
                </button>
                <button
                  type="submit"
                  className="flex-1 py-2 px-4 border border-transparent rounded-lg shadow-sm text-sm font-medium text-white bg-[#003f88] hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500 transition-all duration-300"
                >
                  Complete Registration
                </button>
              </div>
            </form>
          )}

          {/* Success Modal */}
          {showSuccessModal && (
            <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
              <div className="bg-white rounded-lg p-6 sm:p-8 max-w-md w-full mx-4 text-center">
                <div className="mb-4 sm:mb-6">
                  <img src="/images/logo.png" alt="Avyakta" className="h-16 sm:h-20 w-auto mx-auto mb-4" />
                </div>
                <h2 className="text-xl sm:text-2xl font-bold text-[#003f88] mb-4">Thank you for joining Avyakta</h2>
                <p className="text-sm sm:text-base text-gray-600 mb-6">
                  You're now a part of a mission to give dignity to the forgotten.
                  Your action today could bring justice, identity, and peace to someone tomorrow.
                </p>
                <p className="text-[#003f88] font-semibold mb-6">— Team Avyakta</p>
                <button
                  onClick={handleSuccessModalClose}
                  className="w-full sm:w-auto rounded-full bg-gold px-6 sm:px-8 py-2 sm:py-3 text-sm sm:text-base font-bold text-[#003f88] shadow-lg hover:bg-yellow-400 transition-all duration-300 hover:scale-105"
                >
                  Continue to Home
                </button>
              </div>
            </div>
          )}

          {/* Back to Home Link */}
          <div className="text-center mt-4">
            <button
              onClick={() => navigate('/')}
              className="text-sm text-blue-600 hover:text-blue-500 transition-colors duration-200"
            >
              Back to Home
            </button>
          </div>
        </div>
      </div>

      {/* Password Recovery Modal - Moved outside main container */}
      {showForgot && (
        <PasswordRecovery onClose={() => setShowForgot(false)} />
      )}
    </>
  );
};

export default AdminAuth;
