import React, { useState, useEffect } from 'react';
import { PhoneIcon, ExclamationTriangleIcon, XMarkIcon, InformationCircleIcon, ShieldCheckIcon, HeartIcon, FireIcon, UserGroupIcon, UserIcon } from '@heroicons/react/24/outline';

const emergencyNumbers = [
  { 
    number: '112', 
    label: 'All-in-one Emergency Services', 
    icon: <ShieldCheckIcon className="h-6 w-6" />,
    description: 'National emergency number for all services',
    priority: 1
  },
  { 
    number: '1091', 
    label: "Women's Helpline", 
    icon: <UserIcon className="h-6 w-6" />,
    description: '24/7 support for women in distress',
    priority: 2
  },
  { 
    number: '1098', 
    label: 'Child Helpline', 
    icon: <UserGroupIcon className="h-6 w-6" />,
    description: 'Emergency support for children',
    priority: 2
  },
  { 
    number: '100', 
    label: 'Police', 
    icon: <ShieldCheckIcon className="h-6 w-6" />,
    description: 'Police emergency services',
    priority: 3
  },
  { 
    number: '101', 
    label: 'Fire', 
    icon: <FireIcon className="h-6 w-6" />,
    description: 'Fire and rescue services',
    priority: 3
  },
  { 
    number: '102', 
    label: 'Ambulance', 
    icon: <HeartIcon className="h-6 w-6" />,
    description: 'Medical emergency services',
    priority: 3
  },
  { 
    number: '181', 
    label: "Women's Help (State-specific)", 
    icon: <UserIcon className="h-6 w-6" />,
    description: 'State-specific women helpline',
    priority: 4
  },
];

const EmergencyHelp = () => {
  const [isOpen, setIsOpen] = useState(false);
  const [showInfo, setShowInfo] = useState(false);
  const [isSticky, setIsSticky] = useState(false);
  const [isMobile, setIsMobile] = useState(window.innerWidth < 768);

  useEffect(() => {
    const handleResize = () => {
      setIsMobile(window.innerWidth < 768);
    };

    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  const handleCall = (number) => {
    window.location.href = `tel:${number}`;
  };

  const toggleSticky = () => {
    setIsSticky(!isSticky);
  };

  const EmergencyButton = ({ item }) => (
    <button
      onClick={() => handleCall(item.number)}
      className="w-full flex items-center justify-between p-4 bg-red-50 rounded-lg hover:bg-red-100 transition-colors duration-200 group"
      aria-label={`Call ${item.label} at ${item.number}`}
    >
      <div className="flex items-center gap-4">
        <div className="text-red-600">{item.icon}</div>
        <div className="text-left">
          <p className="font-semibold text-red-600 text-lg">{item.number}</p>
          <p className="text-gray-700">{item.label}</p>
          <p className="text-sm text-gray-500">{item.description}</p>
        </div>
      </div>
      <div className="flex items-center gap-2">
        <PhoneIcon className="h-5 w-5 text-red-600 group-hover:scale-110 transition-transform duration-200" />
        <span className="text-sm text-red-600 font-medium hidden md:inline">Call Now</span>
      </div>
    </button>
  );

  const EmergencyList = () => (
    <div className="space-y-3">
      {emergencyNumbers
        .sort((a, b) => a.priority - b.priority)
        .map((item) => (
          <EmergencyButton key={item.number} item={item} />
        ))}
    </div>
  );

  return (
    <>
      {/* Floating Button */}
      <button
        onClick={() => setIsOpen(true)}
        className="fixed bottom-6 right-6 bg-red-600 text-white rounded-full p-4 shadow-lg hover:bg-red-700 transition-all duration-300 z-50 flex items-center gap-2 group"
        aria-label="Open Emergency Help"
      >
        <ExclamationTriangleIcon className="h-6 w-6" />
        <span className="hidden md:inline-block font-semibold">Emergency Help</span>
      </button>

      {/* Sticky Sidebar */}
      {isSticky && !isMobile && (
        <div className="fixed right-0 top-1/2 transform -translate-y-1/2 bg-white shadow-lg rounded-l-lg p-4 z-40 w-80">
          <div className="flex justify-between items-center mb-4">
            <h3 className="text-lg font-semibold text-red-600">Emergency Help</h3>
            <button
              onClick={toggleSticky}
              className="text-gray-500 hover:text-gray-700"
              aria-label="Close Emergency Help"
            >
              <XMarkIcon className="h-5 w-5" />
            </button>
          </div>
          <EmergencyList />
        </div>
      )}

      {/* Modal */}
      {isOpen && (
        <div 
          className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4"
          onClick={(e) => e.target === e.currentTarget && setIsOpen(false)}
        >
          <div className="bg-white rounded-lg max-w-md w-full p-6 relative animate-fade-in">
            {/* Header */}
            <div className="flex justify-between items-center mb-6">
              <div>
                <h2 className="text-2xl font-bold text-red-600 mb-2">Emergency Help</h2>
                <p className="text-gray-600 text-sm">
                  If you or someone is in danger or distress, don't hesitate to call these helplines immediately.
                </p>
              </div>
              <div className="flex gap-2">
                {!isMobile && (
                  <button
                    onClick={toggleSticky}
                    className="text-gray-500 hover:text-gray-700 p-2"
                    aria-label="Pin Emergency Help"
                  >
                    <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5" viewBox="0 0 20 20" fill="currentColor">
                      <path fillRule="evenodd" d="M10 2a1 1 0 011 1v1.323l3.954 1.582 1.599-.8a1 1 0 01.894 1.79l-1.233.616 1.738 5.42a1 1 0 01-.285 1.05A3.989 3.989 0 0115 15a3.989 3.989 0 01-2.667-1.019 1 1 0 01-.285-1.05l1.715-5.349L11 4.477V16h2a1 1 0 110 2H7a1 1 0 110-2h2V4.477L6.237 7.582l1.715 5.349a1 1 0 01-.285 1.05A3.989 3.989 0 015 15a3.989 3.989 0 01-2.667-1.019 1 1 0 01-.285-1.05l1.738-5.42-1.233-.616a1 1 0 01.894-1.79l1.599.8L9 4.323V3a1 1 0 011-1z" clipRule="evenodd" />
                    </svg>
                  </button>
                )}
                <button
                  onClick={() => setIsOpen(false)}
                  className="text-gray-500 hover:text-gray-700 p-2"
                  aria-label="Close Emergency Help"
                >
                  <XMarkIcon className="h-6 w-6" />
                </button>
              </div>
            </div>

            {/* Emergency Numbers */}
            <EmergencyList />

            {/* Info Tooltip */}
            <div className="mt-6 relative">
              <button
                onMouseEnter={() => setShowInfo(true)}
                onMouseLeave={() => setShowInfo(false)}
                className="flex items-center gap-2 text-gray-500 hover:text-gray-700 text-sm"
                aria-label="Show Important Information"
              >
                <InformationCircleIcon className="h-5 w-5" />
                <span>Important Information</span>
              </button>
              {showInfo && (
                <div className="absolute bottom-full left-0 mb-2 p-3 bg-gray-800 text-white text-sm rounded-lg shadow-lg max-w-xs">
                  These helplines are available 24/7. Save these numbers in your phone for quick access during emergencies.
                </div>
              )}
            </div>
          </div>
        </div>
      )}
    </>
  );
};

export default EmergencyHelp;
