import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { useTranslation } from 'react-i18next';
import { Disclosure } from '@headlessui/react';
import { Bars3Icon, XMarkIcon } from '@heroicons/react/24/outline';

const Navbar = () => {
  const { t, i18n } = useTranslation();
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [showLanguageModal, setShowLanguageModal] = useState(false);
  const [newLanguage, setNewLanguage] = useState('');

  const handleLanguageChange = () => {
    const newLang = i18n.language === 'en' ? 'hi' : 'en';
    setNewLanguage(newLang);
    setShowLanguageModal(true);
  };

  const confirmLanguageChange = () => {
    i18n.changeLanguage(newLanguage);
    setShowLanguageModal(false);
  };

  const cancelLanguageChange = () => {
    setShowLanguageModal(false);
  };

  const navItems = [
    { key: 'home', label: 'Home', path: '/' },
    { key: 'report', label: 'Report Case', path: '/report' },
    // { key: 'search', label: 'Search', path: '/search' }, // Example: Temporarily commenting out for brevity if needed
    { key: 'about', label: 'About', path: '/about' },
    { key: 'contact', label: 'Contact', path: '/contact' }
  ];

  return (
    <>
      <Disclosure as="nav" className="bg-gradient-to-r from-slate-800 to-slate-900 shadow-lg font-poppins">
        {({ open }) => (
          <>
            <div className="mx-auto max-w-7xl px-4 sm:px-6 lg:px-8">
              <div className="flex h-20 sm:h-24 items-center justify-between">
                <div className="flex items-center w-full">
                  <Link to="/" className="flex items-center mr-4 sm:mr-6">
                    <img
                      className="h-12 sm:h-20 w-auto object-contain transition-transform duration-300 hover:scale-105"
                      src="/images/logo.png"
                      alt="Nirman Sewa"
                    />
                  </Link>
                  <div className="hidden sm:flex flex-1 justify-center items-center space-x-2 lg:space-x-6">
                    {navItems.map((item) => (
                      <Link
                        key={item.key}
                        to={item.path}
                        className="relative inline-flex items-center px-3 py-2 text-sm sm:text-base font-medium text-slate-100 rounded-md hover:bg-slate-700 hover:text-sky-400 transition-all duration-300 after:absolute after:bottom-0.5 after:left-1/2 after:-translate-x-1/2 after:w-0 after:h-0.5 after:bg-sky-400 hover:after:w-4/5 after:transition-all after:duration-300"
                      >
                        {item.label}
                      </Link>
                    ))}
                  </div>
                </div>
                <div className="hidden sm:flex items-center space-x-3">
                  <button
                    onClick={handleLanguageChange}
                    className="p-2 rounded-full bg-gradient-to-br from-sky-600 to-cyan-600 text-white hover:from-sky-500 hover:to-cyan-500 transition-all duration-300 shadow-md hover:shadow-lg transform hover:scale-105 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-offset-slate-900 focus:ring-cyan-500"
                    aria-label="Switch Language"
                  >
                    {i18n.language === 'en' ? 'हिंदी' : 'English'}
                  </button>
                  <Link
                    to="/admin/login"
                    className="rounded-full bg-gradient-to-br from-amber-500 to-orange-600 px-4 sm:px-5 py-2 text-sm sm:text-base font-semibold text-white shadow-md hover:from-amber-600 hover:to-orange-700 transition-all duration-300 hover:scale-105 transform focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-offset-slate-900 focus:ring-orange-500"
                  >
                    Admin Login
                  </Link>
                </div>
                <div className="flex items-center sm:hidden">
                  {/* Mobile menu button */}
                  <Disclosure.Button className="inline-flex items-center justify-center rounded-md p-2 text-slate-200 hover:bg-slate-700 hover:text-white focus:outline-none focus:ring-2 focus:ring-inset focus:ring-white transition-all duration-300">
                    <span className="sr-only">Open main menu</span>
                    {open ? (
                      <XMarkIcon className="block h-6 w-6" aria-hidden="true" />
                    ) : (
                      <Bars3Icon className="block h-6 w-6" aria-hidden="true" />
                    )}
                  </Disclosure.Button>
                </div>
              </div>
            </div>

            <Disclosure.Panel className="sm:hidden">
              <div className="px-2 pt-2 pb-3 space-y-1 bg-gradient-to-b from-slate-900 to-slate-800">
                {navItems.map((item) => (
                  <Disclosure.Button
                    key={item.key}
                    as={Link}
                    to={item.path}
                    className="block px-3 py-2 rounded-md text-base font-medium text-slate-100 hover:bg-slate-700 hover:text-sky-400 transition-all duration-300"
                  >
                    {item.label}
                  </Disclosure.Button>
                ))}
                <div className="pt-4 pb-3 border-t border-slate-700">
                  <div className="flex items-center justify-center px-3">
                    <button
                      onClick={handleLanguageChange}
                      className="w-full max-w-xs flex justify-center px-4 py-2 rounded-full bg-gradient-to-br from-sky-600 to-cyan-600 text-white hover:from-sky-500 hover:to-cyan-500 transition-all duration-300 shadow-md hover:shadow-lg transform hover:scale-105 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-offset-slate-900 focus:ring-cyan-500"
                    >
                      {i18n.language === 'en' ? 'हिंदी' : 'English'}
                    </button>
                  </div>
                  <div className="mt-3 px-3">
                    <Link
                      to="/admin/login"
                      className="block w-full max-w-xs mx-auto text-center px-4 py-2 rounded-full bg-gradient-to-br from-amber-500 to-orange-600 font-semibold text-white shadow-md hover:from-amber-600 hover:to-orange-700 transition-all duration-300 hover:scale-105 transform focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-offset-slate-900 focus:ring-orange-500"
                    >
                      Admin Login
                    </Link>
                  </div>
                </div>
              </div>
            </Disclosure.Panel>
          </>
        )}
      </Disclosure>

      {/* Language Change Modal */}
      {showLanguageModal && (
        <div className="fixed inset-0 bg-black/60 backdrop-blur-sm flex items-center justify-center z-50 p-4">
          <div className="bg-slate-50 rounded-xl p-6 sm:p-8 max-w-md w-full shadow-2xl transform transition-all duration-300 ease-out scale-95 group-hover:scale-100">
            <h3 className="text-xl font-semibold text-slate-800 mb-3">Change Language</h3>
            <p className="text-slate-600 mb-6">Are you sure you want to change the language to {newLanguage === 'en' ? 'English' : 'हिंदी'}?</p>
            <div className="flex justify-end space-x-3">
              <button
                onClick={cancelLanguageChange}
                className="px-5 py-2 text-sm font-medium text-slate-700 bg-slate-200 rounded-lg hover:bg-slate-300 transition-colors duration-200 focus:outline-none focus:ring-2 focus:ring-slate-400"
              >
                Cancel
              </button>
              <button
                onClick={confirmLanguageChange}
                className="px-5 py-2 text-sm font-medium text-white bg-sky-600 rounded-lg hover:bg-sky-700 transition-colors duration-200 focus:outline-none focus:ring-2 focus:ring-sky-500"
              >
                Confirm
              </button>
            </div>
          </div>
        </div>
      )}
    </>
  );
};

export default Navbar;
