import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const sections = [
  {
    title: 'General Users',
    icon: 'üßæ',
    color: 'text-[#1f5c8d]',
    content: (
      <div className="space-y-4">
        <div>
          <span className="inline-block mr-2 text-[#1f5c8d]">‚úÖ</span>
          <b className="text-[#1f5c8d]">Anonymous Reporting</b>
          <div className="ml-7 text-[#212529]">You can report without revealing your identity. We respect anonymity.</div>
        </div>
        <div>
          <span className="inline-block mr-2 text-[#1f5c8d]">‚úÖ</span>
          <b className="text-[#1f5c8d]">Authentic Information</b>
          <div className="ml-7 text-[#212529]">Share only real, verified, and observed information.<br />Do not share rumors or assumptions.</div>
        </div>
        <div>
          <span className="inline-block mr-2 text-[#1f5c8d]">‚úÖ</span>
          <b className="text-[#1f5c8d]">What to Include in a Report:</b>
          <ul className="ml-7 list-disc space-y-1 text-[#212529]">
            <li><span className="inline-block mr-2">üìç</span>Location: Auto/manual (GPS-based or typed).</li>
            <li><span className="inline-block mr-2">üïí</span>Date & Time: Auto/manual.</li>
            <li><span className="inline-block mr-2">üìù</span>Brief Description: What exactly you saw or noticed.</li>
            <li><span className="inline-block mr-2">üì∏</span>Upload Image: Only if it's ethical, safe, and necessary.</li>
          </ul>
        </div>
        <div>
          <span className="inline-block mr-2 text-[#1f5c8d]">‚úÖ</span>
          <b className="text-[#1f5c8d]">SOS Button Use</b>
          <div className="ml-7 text-[#212529]">Use only in true emergencies (e.g., discovered a body, legal urgency, etc.)</div>
        </div>
        <div className="mt-4 rounded-xl p-4" style={{ background: '#fff3cd', color: '#856404', borderLeft: '6px solid #f8d7da' }}>
          <span className="inline-block mr-2" style={{ color: '#d63333' }}>‚ùå</span>
          <b> You Must NOT:</b>
          <ul className="ml-7 list-disc space-y-1 mt-2">
            <li>Post fake, misleading, or duplicate reports.</li>
            <li>Use platform for personal revenge, trolling, or harassment.</li>
            <li>Attempt to mislead authorities using false data.</li>
            <li>Upload graphic or violent images unnecessarily.</li>
            <li>Abuse the system for defamation or illegal activity.</li>
          </ul>
        </div>
      </div>
    )
  },
  {
    title: 'Health Workers / NGO Representatives',
    icon: 'üë©‚Äç‚öï',
    color: 'text-[#1f5c8d]',
    content: (
      <div className="space-y-4">
        <div>
          <span className="inline-block mr-2 text-[#1f5c8d]">‚úÖ</span>
          <b className="text-[#1f5c8d]">You Must:</b>
          <ul className="ml-7 list-disc space-y-1 text-[#212529]">
            <li>Upload incident details truthfully.</li>
            <li>Confirm report before submitting with your org ID (if available).</li>
            <li>Mention if you've already contacted police or municipal authorities.</li>
          </ul>
        </div>
        <div>
          <span className="inline-block mr-2 text-[#1f5c8d]">‚úÖ</span>
          <b className="text-[#1f5c8d]">Responsibilities Include:</b>
          <ul className="ml-7 list-disc space-y-1 text-[#212529]">
            <li>Preserving dignity in handling remains.</li>
            <li>Coordinating with local departments (police, hospital, municipality).</li>
            <li>Ensuring no legal or ethical violations.</li>
          </ul>
        </div>
      </div>
    )
  },
  {
    title: 'Police / Authorities',
    icon: 'üïµ',
    color: 'text-[#1f5c8d]',
    content: (
      <div className="space-y-4">
        <div>
          <span className="inline-block mr-2 text-[#1f5c8d]">‚úÖ</span>
          <b className="text-[#1f5c8d]">You Should:</b>
          <ul className="ml-7 list-disc space-y-1 text-[#212529]">
            <li>Review UID-linked case reports.</li>
            <li>Update investigation progress through secure admin panel.</li>
            <li>Mark cases "Under Review," "Claimed," or "Closed."</li>
          </ul>
        </div>
      </div>
    )
  },
  {
    title: 'Emotional & Sensitive Content Policy',
    icon: 'üß†',
    color: 'text-[#1f5c8d]',
    content: (
      <div className="space-y-2 text-[#212529]">
        <div>This platform deals with deceased individuals. Always maintain respectful language.</div>
        <div>Refrain from posting graphic descriptions unless required.</div>
        <div>Reports are monitored and moderated for ethical compliance.</div>
      </div>
    )
  },
  {
    title: 'Legal & Traceability Notice',
    icon: '‚ö†',
    color: 'text-[#1f5c8d]',
    content: (
      <div className="space-y-2 text-[#212529]">
        <div>All reports are timestamped and traceable via UID, even if submitted anonymously.</div>
        <div>Misuse may result in permanent ban or legal action.</div>
      </div>
    )
  },
  {
    title: 'Need Support?',
    icon: 'üì¨',
    color: 'text-[#1f5c8d]',
    content: (
      <div className="space-y-2 text-[#212529]">
        <div>If you're unsure about submitting a report or have a concern:</div>
        <div>üì© Email: <a href="mailto:support@avyakta.org" className="underline hover:text-[#007BFF] transition-colors">support@avyakta.org</a></div>
        <div>üí¨ Chat: Use the "Chat With Us" button</div>
        <div>üìû 24/7 Helpline: 1800-XXX-XXXX</div>
      </div>
    )
  }
];

export default function Guidelines() {
  const [open, setOpen] = useState([true, false, false, false, false, false]);
  const [accepted, setAccepted] = useState(false);
  const navigate = useNavigate();

  const handleAccordion = idx => {
    setOpen(prev => prev.map((v, i) => (i === idx ? !v : v)));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (accepted) {
      navigate('/');
    }
  };

  return (
    <div className="min-h-screen py-10 px-2 sm:px-0" style={{ background: '#f8f9fa' }}>
      {/* Intro Section */}
      <div className="max-w-2xl mx-auto mb-8">
        <div className="flex flex-col items-center mb-4">
          <span className="block w-10 h-10 mb-2 animate-glow">
            <svg width="40" height="40" viewBox="0 0 24 24" fill="none">
              <ellipse cx="12" cy="20" rx="5" ry="2" fill="#ffe066" opacity="0.3" />
              <path className="animate-flicker" d="M12 20c3-4.5 3-9 0-13-3 4.5-3 9 0 13z" fill="#ffe066" />
              <path className="animate-flicker" d="M12 16c1.5-2.5 1.5-5 0-7.5-1.5 2.5-1.5 5 0 7.5z" fill="#ffb700" />
            </svg>
          </span>
          <h1 className="text-2xl sm:text-3xl font-bold mb-2" style={{ color: '#1f5c8d' }}>Avyakta Guidelines</h1>
          <div className="text-base sm:text-lg mb-2 text-center font-semibold" style={{ color: '#212529' }}>Your Responsibility. Their Dignity.</div>
          <div className="border-l-4 pl-4 py-2 rounded-xl shadow animate-fade-in max-w-xl mx-auto" style={{ borderColor: '#f8d7da', background: '#fff3cd', color: '#856404' }}>
            <span className="italic font-serif">This is not a casual reporting site. Every action here can affect real investigations and human dignity. Please read and follow these guidelines carefully.</span>
          </div>
        </div>
      </div>
      {/* Guidelines Sections */}
      <div className="max-w-2xl mx-auto space-y-5">
        {sections.map((section, idx) => (
          <div
            key={section.title}
            className={`rounded-2xl shadow-md bg-white transition-all duration-300 overflow-hidden animate-fade-in`}
            style={{ boxShadow: '0 2px 16px 0 #0001' }}
          >
            <button
              className="w-full flex items-center justify-between px-6 py-4 text-lg font-semibold focus:outline-none select-none"
              onClick={() => handleAccordion(idx)}
              aria-expanded={open[idx]}
              style={{ fontFamily: 'Poppins, Inter, Nunito Sans, sans-serif', color: '#1f5c8d' }}
            >
              <span className="flex items-center gap-2">
                <span className={`inline-block text-2xl ${section.color}`}>{section.icon}</span>
                {section.title}
              </span>
              <span className="text-gray-400 text-xl">{open[idx] ? '‚àí' : '+'}</span>
            </button>
            <div
              className={`transition-all duration-400 ease-in-out px-6 ${open[idx] ? 'max-h-96 py-4 opacity-100' : 'max-h-0 py-0 opacity-0'} overflow-hidden`}
              style={{ fontSize: '1.05rem', color: '#212529' }}
            >
              {section.content}
            </div>
          </div>
        ))}
      </div>
      {/* Accept & Continue */}
      <form onSubmit={handleSubmit} className="max-w-2xl mx-auto mt-10 mb-8 bg-white rounded-2xl shadow-md p-6 flex flex-col items-center animate-fade-in">
        <label className="flex items-center gap-2 mb-4" style={{ color: '#212529' }}>
          <input
            type="checkbox"
            checked={accepted}
            onChange={e => setAccepted(e.target.checked)}
            required
            className="form-checkbox h-5 w-5"
            style={{ accentColor: '#4CAF50' }}
          />
          <span className="font-medium">I have read and accept the above guidelines.</span>
        </label>
        <button
          type="submit"
          className="w-full max-w-xs font-bold py-3 px-6 rounded-xl shadow-lg disabled:opacity-60 transition-all"
          style={{ background: '#4CAF50', color: '#fff', border: 'none' }}
          disabled={!accepted}
        >
          Accept & Continue
        </button>
      </form>
      {/* Animations */}
      <style>{`
        @keyframes flicker { 0%{opacity:1;} 50%{opacity:0.7;} 100%{opacity:1;} }
        .animate-flicker { animation: flicker 1.2s infinite alternate; }
        .animate-glow { filter: drop-shadow(0 0 8px #ffe066) drop-shadow(0 0 16px #ffe06688); }
        .animate-fade-in { animation: fadeIn 0.7s ease; }
        @keyframes fadeIn { from { opacity: 0; transform: translateY(16px);} to { opacity: 1; transform: none; } }
        a { color: #007BFF; }
        a:hover { color: #0056b3; text-decoration: underline; }
      `}</style>
    </div>
  );
}
