import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import { useTranslation } from 'react-i18next';
import axios from 'axios';
import Navbar from './components/Navbar';
import Footer from './components/Footer';
import Home from './components/Home';
import AdminAuth from './components/AdminAuth';
import About from './pages/About';
import EmergencyHelp from './components/EmergencyHelp';
import FloatingActionButton from './components/FloatingActionButton';
import Contact from './components/Contact';
import LoadingPage from './components/LoadingPage';

// API base URL
const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';

// Function to register a new user
const registerUser = async (userData) => {
  try {
    const response = await axios.post(`${API_BASE_URL}/auth/register`, userData);
    return response.data;
  } catch (error) {
    throw error;
  }
};

// Function to fetch user profile
const fetchUserProfile = async () => {
  try {
    const token = localStorage.getItem('token');
    const response = await axios.get(`${API_BASE_URL}/users/profile`, {
      headers: {
        Authorization: `Bearer ${token}`
      }
    });
    return response.data;
  } catch (error) {
    throw error;
  }
};

function App() {
  const [isLoading, setIsLoading] = useState(false);
  const [loadingType, setLoadingType] = useState('default');
  const [isServerDown, setIsServerDown] = useState(false);

  useTranslation(); // Keep this for i18n initialization

  // Example function to handle registration
  const handleRegistration = async (userData) => {
    setIsLoading(true);
    setLoadingType('registration');
    try {
      // Your registration logic here
      await registerUser(userData);
    } catch (error) {
      if (error.response?.status === 503) {
        setIsServerDown(true);
        setLoadingType('server');
      }
    } finally {
      setIsLoading(false);
    }
  };

  // Example function to handle profile loading
  const loadUserProfile = async () => {
    setIsLoading(true);
    setLoadingType('profile');
    try {
      // Your profile loading logic here
      await fetchUserProfile();
    } catch (error) {
      if (error.response?.status === 503) {
        setIsServerDown(true);
        setLoadingType('server');
      }
    } finally {
      setIsLoading(false);
    }
  };

  // Check server status periodically
  useEffect(() => {
    const checkServerStatus = async () => {
      try {
        const response = await fetch('/api/health');
        if (!response.ok) {
          setIsServerDown(true);
          setLoadingType('server');
        } else {
          setIsServerDown(false);
        }
      } catch (error) {
        setIsServerDown(true);
        setLoadingType('server');
      }
    };

    const interval = setInterval(checkServerStatus, 30000); // Check every 30 seconds
    return () => clearInterval(interval);
  }, []);

  return (
    <Router>
      <div className="min-h-screen flex flex-col">
        <Navbar />
        <main className="flex-grow">
          <Routes>
            <Route exact path="/" element={<Home />} />
            <Route exact path="/about" element={<About />} />
            <Route exact path="/admin/login" element={<AdminAuth />} />
            <Route exact path="/contact" element={<Contact />} />
          </Routes>
        </main>
        <EmergencyHelp />
        <FloatingActionButton />
        <Footer />
      </div>
      {isLoading && <LoadingPage type={loadingType} />}
      {isServerDown && <LoadingPage type="server" />}
    </Router>
  );
}

export default App;
