import React, { useState } from 'react';
import { ChatBubbleLeftRightIcon, XMarkIcon } from '@heroicons/react/24/outline';
import IntegratedChatbot from './IntegratedChatbot';

const IntegratedChatButton = () => {
  const [isChatOpen, setIsChatOpen] = useState(false);

  const toggleChat = () => {
    setIsChatOpen(!isChatOpen);
  };

  return (
    <>
      {/* Integrated Chat Button - Positioned near SOS button */}
      <button
        onClick={toggleChat}
        className="fixed bottom-6 right-24 z-40 w-12 h-12 bg-gradient-to-r from-blue-600 to-purple-600 text-white rounded-full shadow-lg hover:shadow-xl transform hover:scale-110 transition-all duration-300 flex items-center justify-center group"
        aria-label="Open chat assistant"
      >
        {isChatOpen ? (
          <XMarkIcon className="w-6 h-6" />
        ) : (
          <ChatBubbleLeftRightIcon className="w-6 h-6" />
        )}
        
        {/* Pulse animation when closed */}
        {!isChatOpen && (
          <div className="absolute inset-0 bg-blue-400 rounded-full animate-ping opacity-75"></div>
        )}
        
        {/* Tooltip */}
        <div className="absolute right-14 bottom-2 bg-gray-900 text-white text-xs px-2 py-1 rounded opacity-0 group-hover:opacity-100 transition-opacity duration-300 whitespace-nowrap">
          Need help? Ask me anything!
        </div>
      </button>

      {/* Integrated Chatbot */}
      <IntegratedChatbot isOpen={isChatOpen} onClose={() => setIsChatOpen(false)} />
    </>
  );
};

export default IntegratedChatButton;
