import React from 'react';
import { useTranslation } from 'react-i18next';
import { Link } from 'react-router-dom';

// Cosmic Animation Component
const CosmicAnimation = () => {
  return (
    <div className="absolute inset-0 overflow-hidden pointer-events-none">
      {/* Animated Stars/Souls */}
      {[...Array(50)].map((_, index) => (
        <div
          key={index}
          className="absolute animate-fall"
          style={{
            left: `${Math.random() * 100}%`,
            animationDelay: `${Math.random() * 10}s`,
            animationDuration: `${8 + Math.random() * 12}s`,
            animationIterationCount: 'infinite'
          }}
        >
          <div 
            className="w-1 h-1 bg-white rounded-full opacity-80 animate-twinkle"
            style={{
              animationDelay: `${Math.random() * 2}s`,
              animationDuration: `${1 + Math.random() * 2}s`
            }}
          />
        </div>
      ))}

      {/* Larger Soul Particles */}
      {[...Array(15)].map((_, index) => (
        <div
          key={`soul-${index}`}
          className="absolute animate-soul-fall"
          style={{
            left: `${Math.random() * 100}%`,
            animationDelay: `${Math.random() * 15}s`,
            animationDuration: `${15 + Math.random() * 20}s`,
            animationIterationCount: 'infinite'
          }}
        >
          <div 
            className="w-2 h-2 bg-gradient-to-r from-blue-300 to-purple-300 rounded-full opacity-60 animate-pulse"
            style={{
              animationDuration: `${2 + Math.random() * 3}s`
            }}
          />
        </div>
      ))}

      {/* Shooting Stars */}
      {[...Array(8)].map((_, index) => (
        <div
          key={`shooting-${index}`}
          className="absolute animate-shooting-star"
          style={{
            top: `${Math.random() * 50}%`,
            left: `${Math.random() * 100}%`,
            animationDelay: `${Math.random() * 20}s`,
            animationDuration: `${15 + Math.random() * 20}s`,
            animationIterationCount: 'infinite'
          }}
        >
          <div className="w-1 h-1 bg-white rounded-full opacity-90" />
          <div className="w-20 h-0.5 bg-gradient-to-r from-transparent to-white transform rotate-45 origin-right" />
        </div>
      ))}
    </div>
  );
};

function Home() {
  const { t } = useTranslation();

  return (
    <div className="min-h-screen pt-24">
      {/* Hero Section */}
      <div className="relative h-screen overflow-hidden" style={{ backgroundColor: 'rgb(16, 8, 50)' }}>
        {/* Cosmic Animation */}
        <CosmicAnimation />

        {/* Content */}
        <div className="relative h-full flex flex-col justify-center items-center px-4 sm:px-6 lg:px-8 text-center z-10">
          <h2 className="text-5xl font-extrabold tracking-tight sm:text-6xl lg:text-7xl animate-fade-in" style={{ 
            animationDelay: '0.2s',
            backgroundImage: 'linear-gradient(91deg, #fff, #7e55ff)',
            backgroundClip: 'text',
            WebkitBackgroundClip: 'text',
            color: 'transparent'
          }}>
            Avyakta: Turning Emptiness into Energy
          </h2>
          <p className="mt-8 text-lg font-normal max-w-4xl mx-auto animate-fade-in" style={{ 
            animationDelay: '0.4s',
            color: 'rgb(202 204 255 / var(--tw-text-opacity, 1))',
            '--tw-text-opacity': '1'
          }}>
            Avyakta, inspired by the Bhagavad Gita, is a platform devoted to helping identify and honor unclaimed bodies with dignity, compassion, and purpose.
          </p>
          <div className="mt-12 flex flex-col sm:flex-row sm:justify-center sm:space-x-6 space-y-4 sm:space-y-0 animate-fade-in" style={{ animationDelay: '0.6s' }}>
            <Link
              to="/report"
              className="inline-flex items-center justify-center px-10 py-4 border-2 border-white text-lg font-medium rounded-md shadow-lg hover:shadow-xl transition-all duration-300 transform hover:scale-105"
              style={{
                backgroundImage: 'linear-gradient(91deg, #fff, #7e55ff)',
                backgroundClip: 'text',
                WebkitBackgroundClip: 'text',
                color: 'transparent'
              }}
            >
              Report a Case
            </Link>
            <Link
              to="/search"
              className="inline-flex items-center justify-center px-10 py-4 border-2 border-white text-lg font-medium rounded-md shadow-lg hover:shadow-xl transition-all duration-300 transform hover:scale-105"
              style={{
                backgroundImage: 'linear-gradient(91deg, #fff, #7e55ff)',
                backgroundClip: 'text',
                WebkitBackgroundClip: 'text',
                color: 'transparent'
              }}
            >
              Search records
            </Link>
          </div>
        </div>
      </div>

      {/* Features Section */}
      <section className="py-16 sm:py-20 bg-gradient-to-br from-slate-50 to-slate-100">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="text-center">
            <h2 className="text-base text-sky-600 font-semibold tracking-wide uppercase">
              {t('home.features.title')}
            </h2>
            <p className="mt-2 text-3xl leading-8 font-extrabold tracking-tight text-slate-800 sm:text-4xl">
              {t('home.features.subtitle')}
            </p>
            <p className="mt-4 max-w-2xl text-xl text-slate-600 mx-auto">
              {t('home.features.description')}
            </p>
          </div>

          <div className="mt-16 grid gap-8 md:grid-cols-2 lg:grid-cols-3">
            {[
              {
                key: 'easy',
                icon: (
                  <svg className="h-8 w-8" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 6v6m0 0v6m0-6h6m-6 0H6" />
                  </svg>
                ),
                bgColor: 'bg-sky-500'
              },
              {
                key: 'community',
                icon: (
                  <svg className="h-8 w-8" fill="none" viewBox="0 0 24 24" stroke="currentColor">
