import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { useTranslation } from 'react-i18next';
import { Disclosure } from '@headlessui/react';
import { Bars3Icon, XMarkIcon } from '@heroicons/react/24/outline';

const Navbar = () => {
  const { t, i18n } = useTranslation();
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [showLanguageModal, setShowLanguageModal] = useState(false);
  const [newLanguage, setNewLanguage] = useState('');

  const handleLanguageChange = () => {
    const newLang = i18n.language === 'en' ? 'hi' : 'en';
    setNewLanguage(newLang);
    setShowLanguageModal(true);
  };

  const confirmLanguageChange = () => {
    i18n.changeLanguage(newLanguage);
    setShowLanguageModal(false);
  };

  const cancelLanguageChange = () => {
    setShowLanguageModal(false);
  };

  const navItems = [
    { key: 'home', label: 'Home', path: '/' },
    { key: 'report', label: 'Report Case', path: '/report' },
    { key: 'search', label: 'Search', path: '/search' },
    { key: 'about', label: 'About', path: '/about' },
    { key: 'contact', label: 'Contact', path: '/contact' }
  ];

  return (
    <>
      <Disclosure as="nav" className="bg-gradient-to-r from-[#003f88] to-blue-900 shadow-lg font-poppins">
        {({ open }) => (
          <>
            <div className="mx-auto max-w-7xl px-4 sm:px-6 lg:px-8">
              <div className="flex h-24 sm:h-32 items-center justify-between">
                <div className="flex items-center w-full">
                  <Link to="/" className="flex items-center mr-4 sm:mr-8">
                    <img
                      className="h-16 sm:h-28 max-h-32 w-auto object-contain transition-transform duration-300 hover:scale-105"
                      src="/images/logo.png"
                      alt="Nirman Sewa"
                    />
                  </Link>
                  <div className="hidden sm:flex flex-1 justify-center space-x-4 lg:space-x-8">
                    {navItems.map((item) => (
                      <Link
                        key={item.key}
                        to={item.path}
                        className="relative inline-flex items-center px-2 sm:px-3 py-1 text-base sm:text-lg font-semibold text-white transition-all duration-300 hover:text-gold hover:scale-105 after:absolute after:bottom-0 after:left-1/2 after:-translate-x-1/2 after:w-0 after:h-0.5 after:bg-gold hover:after:w-full after:transition-all after:duration-300"
                      >
                        {item.label}
                      </Link>
                    ))}
                  </div>
                </div>
                <div className="hidden sm:flex items-center space-x-4">
                  <button
                    onClick={handleLanguageChange}
                    className="p-2 rounded-full bg-gradient-to-r from-purple-500 to-pink-500 text-white hover:from-purple-600 hover:to-pink-600 transition-all duration-300 shadow-lg hover:shadow-xl transform hover:scale-105"
                    aria-label="Switch Language"
                  >
                    {i18n.language === 'en' ? 'हिंदी' : 'English'}
                  </button>
                  <Link
                    to="/admin/login"
                    className="rounded-full bg-gradient-to-r from-green-400 to-emerald-500 px-4 sm:px-6 py-2 text-sm sm:text-base font-bold text-white shadow-lg hover:from-green-500 hover:to-emerald-600 transition-all duration-300 hover:scale-105 font-poppins"
                  >
                    Admin Login
                  </Link>
                </div>
                <div className="flex items-center sm:hidden">
                  <Disclosure.Button className="inline-flex items-center justify-center rounded-md p-2 text-white hover:bg-blue-700 hover:text-white transition-all duration-300">
                    <span className="sr-only">Open main menu</span>
                    {open ? (
                      <XMarkIcon className="block h-6 w-6" aria-hidden="true" />
                    ) : (
                      <Bars3Icon className="block h-6 w-6" aria-hidden="true" />
                    )}
                  </Disclosure.Button>
                </div>
              </div>
            </div>

            <Disclosure.Panel className="sm:hidden">
              <div className="px-2 pt-2 pb-3 space-y-1 bg-gradient-to-b from-blue-900 to-[#003f88]">
                {navItems.map((item) => (
                  <Disclosure.Button
                    key={item.key}
                    as={Link}
                    to={item.path}
                    className="block px-3 py-2 rounded-md text-base font-medium text-white hover:bg-blue-700 hover:text-gold transition-all duration-300"
                  >
                    {item.label}
                  </Disclosure.Button>
                ))}
                <div className="pt-4 pb-3 border-t border-blue-700">
                  <div className="flex items-center px-5">
                    <button
                      onClick={handleLanguageChange}
                      className="w-full flex justify-center px-4 py-2 rounded-full bg-gradient-to-r from-purple-500 to-pink-500 text-white hover:from-purple-600 hover:to-pink-600 transition-all duration-300 shadow-lg"
                    >
                      {i18n.language === 'en' ? 'हिंदी' : 'English'}
                    </button>
                  </div>
                  <div className="mt-3 px-2">
                    <Link
                      to="/admin/login"
                      className="block w-full text-center px-4 py-2 rounded-full bg-gradient-to-r from-green-400 to-emerald-500 text-white hover:from-green-500 hover:to-emerald-600 transition-all duration-300 shadow-lg"
                    >
                      Admin Login
                    </Link>
                  </div>
                </div>
              </div>
            </Disclosure.Panel>
          </>
        )}
      </Disclosure>

      {/* Language Change Modal */}
      {showLanguageModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
          <div className="bg-white rounded-lg p-6 max-w-sm w-full">
            <h3 className="text-lg font-bold text-gray-900 mb-4">Change Language</h3>
            <p className="text-gray-600 mb-6">Are you sure you want to change the language to {newLanguage === 'en' ? 'English' : 'हिंदी'}?</p>
            <div className="flex justify-end space-x-4">
              <button
                onClick={cancelLanguageChange}
                className="px-4 py-2 text-sm font-medium text-gray-700 hover:text-gray-900"
              >
                Cancel
              </button>
              <button
                onClick={confirmLanguageChange}
                className="px-4 py-2 text-sm font-medium text-white bg-blue-600 rounded-md hover:bg-blue-700"
              >
                Confirm
              </button>
            </div>
          </div>
        </div>
      )}
    </>
  );
};

export default Navbar;
