import React, { useEffect, useRef } from 'react';

const StarryBackground = () => {
  const canvasRef = useRef(null);

  useEffect(() => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    let animationFrameId;
    let stars = [];

    // Set canvas size
    const setCanvasSize = () => {
      const container = canvas.parentElement;
      canvas.width = container.offsetWidth;
      canvas.height = container.offsetHeight;
    };

    // Create stars
    const createStars = () => {
      stars = [];
      const numberOfStars = Math.floor((canvas.width * canvas.height) / 5000); // Increased star density
      
      for (let i = 0; i < numberOfStars; i++) {
        stars.push({
          x: Math.random() * canvas.width,
          y: Math.random() * canvas.height,
          size: Math.random() * 2 + 0.5, // Larger stars
          speed: Math.random() * 0.5 + 0.2, // Faster speed
          brightness: Math.random() * 0.7 + 0.3, // Brighter stars
          twinkleSpeed: Math.random() * 0.02 + 0.01, // Twinkling effect
          twinkleDirection: Math.random() > 0.5 ? 1 : -1
        });
      }
    };

    // Animate stars
    const animate = () => {
      ctx.clearRect(0, 0, canvas.width, canvas.height);
      
      stars.forEach(star => {
        // Update star brightness for twinkling effect
        star.brightness += star.twinkleSpeed * star.twinkleDirection;
        if (star.brightness > 1 || star.brightness < 0.3) {
          star.twinkleDirection *= -1;
        }

        // Draw star
        ctx.beginPath();
        ctx.arc(star.x, star.y, star.size, 0, Math.PI * 2);
        ctx.fillStyle = `rgba(255, 255, 255, ${star.brightness})`;
        ctx.fill();

        // Move star down
        star.y += star.speed;
        
        // Reset star position when it goes off screen
        if (star.y > canvas.height) {
          star.y = 0;
          star.x = Math.random() * canvas.width;
        }
      });

      animationFrameId = requestAnimationFrame(animate);
    };

    // Initialize
    setCanvasSize();
    createStars();
    animate();

    // Handle window resize
    const handleResize = () => {
      setCanvasSize();
      createStars();
    };

    window.addEventListener('resize', handleResize);

    // Cleanup
    return () => {
      window.removeEventListener('resize', handleResize);
      cancelAnimationFrame(animationFrameId);
    };
  }, []);

  return (
    <canvas
      ref={canvasRef}
      className="absolute inset-0 w-full h-full"
      style={{ zIndex: 1 }}
    />
  );
};

export default StarryBackground;
