import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useTranslation } from 'react-i18next';
import { UserIcon, LockClosedIcon, EnvelopeIcon, PhoneIcon, PhotoIcon, DocumentTextIcon, UserGroupIcon } from '@heroicons/react/24/outline';

const roles = [
  { value: 'admin', label: 'Admin' },
  { value: 'ngo', label: 'NGO' },
  { value: 'common', label: 'Common People' },
];

const AdminAuth = () => {
  const navigate = useNavigate();
  const { t } = useTranslation();
  const [isLogin, setIsLogin] = useState(true);
  const [showForgot, setShowForgot] = useState(false);
  const [otpSent, setOtpSent] = useState(false);
  const [showPassword, setShowPassword] = useState(false);
  const [showSuccessModal, setShowSuccessModal] = useState(false);
  const [formData, setFormData] = useState({
    email: '',
    password: '',
    confirmPassword: '',
    name: '',
    phone: '',
    role: 'admin',
    profilePic: null,
    verificationDoc: null,
    terms: false,
    otp: '',
    forgotPhone: '',
    forgotOtp: '',
    newPassword: '',
    confirmNewPassword: '',
    rememberMe: false,
  });

  const handleInputChange = (e) => {
    const { name, value, type, checked, files } = e.target;
    if (type === 'checkbox') {
      setFormData(prev => ({ ...prev, [name]: checked }));
    } else if (type === 'file') {
      setFormData(prev => ({ ...prev, [name]: files[0] }));
    } else {
      setFormData(prev => ({ ...prev, [name]: value }));
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // TODO: Implement authentication logic
    console.log('Form submitted:', formData);
    if (!isLogin) {
      setShowSuccessModal(true);
    }
  };

  const handleForgotSubmit = (e) => {
    e.preventDefault();
    // TODO: Send OTP logic
    setOtpSent(true);
  };

  const handleOtpVerify = (e) => {
    e.preventDefault();
    // TODO: Verify OTP and reset password logic
    setShowForgot(false);
    setOtpSent(false);
  };

  const handleSuccessModalClose = () => {
    setShowSuccessModal(false);
    navigate('/');
  };

  return (
    <div className="min-h-screen flex items-center justify-center relative font-poppins">
      {/* Background image with overlay */}
      <div className="absolute inset-0 z-0">
        <img src="/images/admin-bg.jpg" alt="Compassion" className="w-full h-full object-cover" />
        <div className="absolute inset-0 bg-gradient-to-br from-[#003f88]/80 to-blue-900/80" />
      </div>
      <div className="max-w-md w-full space-y-8 bg-white/90 p-8 rounded-2xl shadow-2xl z-10 animate-fade-in">
        {/* Logo and Title */}
        <div className="text-center">
          <img className="mx-auto h-16 w-16" src="/images/logo.png" alt="Avyakta" />
          <h2 className="mt-6 text-3xl font-merriweather font-bold text-[#003f88]">{isLogin ? 'Admin Login' : 'Admin Registration'}</h2>
          <p className="mt-2 text-sm text-gray-600">{isLogin ? 'Sign in to your account' : 'Create a new admin/NGO/common account'}</p>
        </div>

        {/* Toggle Buttons */}
        <div className="flex rounded-lg bg-gray-100 p-1 mb-2">
          <button
            onClick={() => { setIsLogin(true); setShowForgot(false); }}
            className={`flex-1 py-2 px-4 rounded-md text-sm font-medium transition-all duration-300 ${isLogin ? 'bg-white text-blue-600 shadow-sm' : 'text-gray-500 hover:text-gray-700'}`}
          >
            {t('admin.login.title')}
          </button>
          <button
            onClick={() => { setIsLogin(false); setShowForgot(false); }}
            className={`flex-1 py-2 px-4 rounded-md text-sm font-medium transition-all duration-300 ${!isLogin ? 'bg-white text-blue-600 shadow-sm' : 'text-gray-500 hover:text-gray-700'}`}
          >
            {t('admin.register.title')}
          </button>
        </div>

        {/* Login Form */}
        {isLogin && !showForgot && (
          <form onSubmit={handleSubmit} className="space-y-6">
            <div className="space-y-4">
              <div>
                <label htmlFor="email" className="block text-sm font-medium text-gray-700 flex items-center gap-1"><EnvelopeIcon className="h-4 w-4" /> Email</label>
                <input id="email" name="email" type="email" required value={formData.email} onChange={handleInputChange} className="mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm placeholder-gray-400 focus:outline-none focus:ring-[#003f88] focus:border-[#003f88] transition-colors duration-200" />
              </div>
              <div>
                <label htmlFor="password" className="block text-sm font-medium text-gray-700 flex items-center gap-1"><LockClosedIcon className="h-4 w-4" /> Password</label>
                <div className="relative">
                  <input id="password" name="password" type={showPassword ? 'text' : 'password'} required value={formData.password} onChange={handleInputChange} className="mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm placeholder-gray-400 focus:outline-none focus:ring-[#003f88] focus:border-[#003f88] transition-colors duration-200 pr-10" />
                  <button type="button" onClick={() => setShowPassword(v => !v)} className="absolute inset-y-0 right-2 flex items-center text-gray-500 hover:text-[#003f88] focus:outline-none" tabIndex={-1} aria-label={showPassword ? 'Hide password' : 'Show password'}>
                    {showPassword ? (
                      <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth={1.5} stroke="currentColor" className="w-5 h-5"><path strokeLinecap="round" strokeLinejoin="round" d="M3.98 8.223A10.477 10.477 0 001.934 12.001c1.636 4.01 5.735 6.999 10.066 6.999 2.042 0 3.97-.613 5.566-1.662M6.455 6.455A9.956 9.956 0 0112 5c4.332 0 8.43 2.989 10.066 7a10.477 10.477 0 01-2.025 3.775M15 12a3 3 0 11-6 0 3 3 0 016 0zm6 6l-1.5-1.5M3 3l18 18" /></svg>
                    ) : (
                      <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth={1.5} stroke="currentColor" className="w-5 h-5"><path strokeLinecap="round" strokeLinejoin="round" d="M2.25 12c0-1.25.25-2.45.7-3.54a10.477 10.477 0 012.03-3.23C7.57 2.99 11.67 0 16 0c4.33 0 8.43 2.99 10.07 7a10.477 10.477 0 01-2.03 3.23c-.45 1.09-.7 2.29-.7 3.54s.25 2.45.7 3.54a10.477 10.477 0 002.03 3.23C24.43 21.01 20.33 24 16 24c-4.33 0-8.43-2.99-10.07-7a10.477 10.477 0 01-2.03-3.23c-.45-1.09-.7-2.29-.7-3.54z" /></svg>
                    )}
                  </button>
                </div>
              </div>
              <div className="flex items-center">
                <input id="rememberMe" name="rememberMe" type="checkbox" checked={formData.rememberMe} onChange={handleInputChange} className="h-4 w-4 text-gold focus:ring-gold border-gray-300 rounded" />
                <label htmlFor="rememberMe" className="ml-2 block text-sm text-gray-700">Remember Me</label>
              </div>
            </div>
            <div className="flex justify-between items-center">
              <button type="button" className="text-sm text-gold hover:underline" onClick={() => setShowForgot(true)}>Forgot Password?</button>
              <button type="submit" className="rounded-full bg-gold px-6 py-2 text-base font-bold text-[#003f88] shadow-lg hover:bg-yellow-400 transition-all duration-300 hover:scale-105">Login</button>
            </div>
          </form>
        )}

        {/* Forgot Password Form */}
        {isLogin && showForgot && (
          <form onSubmit={otpSent ? handleOtpVerify : handleForgotSubmit} className="space-y-6 animate-fade-in">
            {!otpSent ? (
              <>
                <div>
                  <label htmlFor="forgotPhone" className="block text-sm font-medium text-gray-700 flex items-center gap-1"><PhoneIcon className="h-4 w-4" /> Phone Number</label>
                  <input id="forgotPhone" name="forgotPhone" type="tel" required value={formData.forgotPhone} onChange={handleInputChange} className="mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm placeholder-gray-400 focus:outline-none focus:ring-[#003f88] focus:border-[#003f88] transition-colors duration-200" />
                </div>
                <button type="submit" className="rounded-full bg-gold px-6 py-2 text-base font-bold text-[#003f88] shadow-lg hover:bg-yellow-400 transition-all duration-300 hover:scale-105 w-full">Send OTP</button>
              </>
            ) : (
              <>
                <div>
                  <label htmlFor="forgotOtp" className="block text-sm font-medium text-gray-700 flex items-center gap-1">OTP</label>
                  <input id="forgotOtp" name="forgotOtp" type="text" required value={formData.forgotOtp} onChange={handleInputChange} className="mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm placeholder-gray-400 focus:outline-none focus:ring-[#003f88] focus:border-[#003f88] transition-colors duration-200" />
                </div>
                <div>
                  <label htmlFor="newPassword" className="block text-sm font-medium text-gray-700 flex items-center gap-1">New Password</label>
                  <input id="newPassword" name="newPassword" type="password" required value={formData.newPassword} onChange={handleInputChange} className="mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm placeholder-gray-400 focus:outline-none focus:ring-[#003f88] focus:border-[#003f88] transition-colors duration-200" />
                </div>
                <div>
                  <label htmlFor="confirmNewPassword" className="block text-sm font-medium text-gray-700 flex items-center gap-1">Confirm New Password</label>
                  <input id="confirmNewPassword" name="confirmNewPassword" type="password" required value={formData.confirmNewPassword} onChange={handleInputChange} className="mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm placeholder-gray-400 focus:outline-none focus:ring-[#003f88] focus:border-[#003f88] transition-colors duration-200" />
                </div>
                <button type="submit" className="rounded-full bg-gold px-6 py-2 text-base font-bold text-[#003f88] shadow-lg hover:bg-yellow-400 transition-all duration-300 hover:scale-105 w-full">Reset Password</button>
              </>
            )}
            <button type="button" className="text-sm text-blue-600 hover:underline mt-2" onClick={() => setShowForgot(false)}>Back to Login</button>
          </form>
        )}

        {/* Register Form */}
        {!isLogin && (
          <form onSubmit={handleSubmit} className="space-y-6 animate-fade-in">
            <div className="space-y-4">
              <div>
                <label htmlFor="name" className="block text-sm font-medium text-gray-700 flex items-center gap-1"><UserIcon className="h-4 w-4" /> Name</label>
                <input id="name" name="name" type="text" required value={formData.name} onChange={handleInputChange} className="mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm placeholder-gray-400 focus:outline-none focus:ring-[#003f88] focus:border-[#003f88] transition-colors duration-200" />
              </div>
              <div>
                <label htmlFor="phone" className="block text-sm font-medium text-gray-700 flex items-center gap-1"><PhoneIcon className="h-4 w-4" /> Phone Number</label>
                <input id="phone" name="phone" type="tel" required value={formData.phone} onChange={handleInputChange} className="mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm placeholder-gray-400 focus:outline-none focus:ring-[#003f88] focus:border-[#003f88] transition-colors duration-200" />
              </div>
              <div>
                <label htmlFor="role" className="block text-sm font-medium text-gray-700 flex items-center gap-1"><UserGroupIcon className="h-4 w-4" /> Role</label>
                <select id="role" name="role" value={formData.role} onChange={handleInputChange} className="mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-[#003f88] focus:border-[#003f88] transition-colors duration-200">
                  {roles.map(r => <option key={r.value} value={r.value}>{r.label}</option>)}
                </select>
              </div>
              <div>
                <label htmlFor="register-email" className="block text-sm font-medium text-gray-700 flex items-center gap-1"><EnvelopeIcon className="h-4 w-4" /> Email</label>
                <input id="register-email" name="email" type="email" required value={formData.email} onChange={handleInputChange} className="mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm placeholder-gray-400 focus:outline-none focus:ring-[#003f88] focus:border-[#003f88] transition-colors duration-200" />
              </div>
              <div>
                <label htmlFor="profilePic" className="block text-sm font-medium text-gray-700 flex items-center gap-1"><PhotoIcon className="h-4 w-4" /> Profile Picture</label>
                <input id="profilePic" name="profilePic" type="file" accept="image/*" onChange={handleInputChange} className="mt-1 block w-full text-sm text-gray-500 file:mr-4 file:py-2 file:px-4 file:rounded-full file:border-0 file:text-sm file:font-semibold file:bg-gold file:text-[#003f88] hover:file:bg-yellow-400" />
              </div>
              <div>
                <label htmlFor="register-password" className="block text-sm font-medium text-gray-700 flex items-center gap-1"><LockClosedIcon className="h-4 w-4" /> Password</label>
                <input id="register-password" name="password" type="password" required value={formData.password} onChange={handleInputChange} className="mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm placeholder-gray-400 focus:outline-none focus:ring-[#003f88] focus:border-[#003f88] transition-colors duration-200" />
              </div>
              <div>
                <label htmlFor="confirm-password" className="block text-sm font-medium text-gray-700 flex items-center gap-1">Confirm Password</label>
                <input id="confirm-password" name="confirmPassword" type="password" required value={formData.confirmPassword} onChange={handleInputChange} className="mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm placeholder-gray-400 focus:outline-none focus:ring-[#003f88] focus:border-[#003f88] transition-colors duration-200" />
              </div>
              <div>
                <label htmlFor="verificationDoc" className="block text-sm font-medium text-gray-700 flex items-center gap-1"><DocumentTextIcon className="h-4 w-4" /> Verification Document</label>
                <input id="verificationDoc" name="verificationDoc" type="file" accept="application/pdf,image/*" onChange={handleInputChange} className="mt-1 block w-full text-sm text-gray-500 file:mr-4 file:py-2 file:px-4 file:rounded-full file:border-0 file:text-sm file:font-semibold file:bg-gold file:text-[#003f88] hover:file:bg-yellow-400" />
              </div>
              <div className="flex items-center">
                <input id="terms" name="terms" type="checkbox" checked={formData.terms} onChange={handleInputChange} className="h-4 w-4 text-gold focus:ring-gold border-gray-300 rounded" />
                <label htmlFor="terms" className="ml-2 block text-sm text-gray-700">I agree to the <a href="#" className="text-gold hover:underline">Terms and Conditions</a></label>
              </div>
            </div>
            <button type="submit" className="rounded-full bg-gold px-6 py-2 text-base font-bold text-[#003f88] shadow-lg hover:bg-yellow-400 transition-all duration-300 hover:scale-105 w-full">Register</button>
          </form>
        )}

        {/* Success Modal */}
        {showSuccessModal && (
          <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
            <div className="bg-white rounded-lg p-8 max-w-md w-full mx-4 text-center">
              <div className="mb-6">
                <img src="/images/logo.png" alt="Avyakta" className="h-20 w-auto mx-auto mb-4" />
              </div>
              <h2 className="text-2xl font-bold text-[#003f88] mb-4">Thank you for joining Avyakta</h2>
              <p className="text-gray-600 mb-6">
                You're now a part of a mission to give dignity to the forgotten.
                Your action today could bring justice, identity, and peace to someone tomorrow.
              </p>
              <p className="text-[#003f88] font-semibold mb-6">— Team Avyakta</p>
              <button
                onClick={handleSuccessModalClose}
                className="rounded-full bg-gold px-8 py-3 text-base font-bold text-[#003f88] shadow-lg hover:bg-yellow-400 transition-all duration-300 hover:scale-105"
              >
                Continue to Home
              </button>
            </div>
          </div>
        )}

        {/* Back to Home Link */}
        <div className="text-center mt-4">
          <button onClick={() => navigate('/')} className="text-sm text-blue-600 hover:text-blue-500 transition-colors duration-200">Back to Home</button>
        </div>
      </div>
    </div>
  );
};

export default AdminAuth;
