import React, { useState, useEffect } from 'react';
import { PhoneIcon, ExclamationTriangleIcon, XMarkIcon, InformationCircleIcon, ShieldCheckIcon, HeartIcon, FireIcon, UserGroupIcon, UserIcon } from '@heroicons/react/24/outline';

const emergencyNumbers = [
  { 
    number: '112', 
    label: 'All-in-one Emergency Services', 
    icon: <ShieldCheckIcon className="h-6 w-6" />,
    description: 'National emergency number for all services',
    priority: 1,
    color: 'bg-red-600'
  },
  { 
    number: '1091', 
    label: "Women's Helpline", 
    icon: <UserIcon className="h-6 w-6" />,
    description: '24/7 support for women in distress',
    priority: 2,
    color: 'bg-pink-600'
  },
  { 
    number: '1098', 
    label: 'Child Helpline', 
    icon: <UserGroupIcon className="h-6 w-6" />,
    description: 'Emergency support for children',
    priority: 2,
    color: 'bg-blue-600'
  },
  { 
    number: '100', 
    label: 'Police', 
    icon: <ShieldCheckIcon className="h-6 w-6" />,
    description: 'Police emergency services',
    priority: 3,
    color: 'bg-indigo-600'
  },
  { 
    number: '101', 
    label: 'Fire', 
    icon: <FireIcon className="h-6 w-6" />,
    description: 'Fire and rescue services',
    priority: 3,
    color: 'bg-orange-600'
  },
  { 
    number: '102', 
    label: 'Ambulance', 
    icon: <HeartIcon className="h-6 w-6" />,
    description: 'Medical emergency services',
    priority: 3,
    color: 'bg-green-600'
  },
  { 
    number: '181', 
    label: "Women's Help (State-specific)", 
    icon: <UserIcon className="h-6 w-6" />,
    description: 'State-specific women helpline',
    priority: 4,
    color: 'bg-purple-600'
  },
];

const EmergencyHelp = () => {
  const [isOpen, setIsOpen] = useState(false);
  const [showInfo, setShowInfo] = useState(false);
  const [isSticky, setIsSticky] = useState(false);
  const [isMobile, setIsMobile] = useState(window.innerWidth < 768);

  useEffect(() => {
    const handleResize = () => {
      setIsMobile(window.innerWidth < 768);
    };

    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  const handleCall = (number) => {
    window.location.href = `tel:${number}`;
  };

  const toggleSticky = () => {
    setIsSticky(!isSticky);
  };

  const EmergencyButton = ({ item }) => (
    <button
      onClick={() => handleCall(item.number)}
      className={`w-full flex items-center justify-between p-3 sm:p-4 rounded-lg transition-all duration-300 group hover:scale-[1.02] hover:shadow-lg ${item.color} bg-opacity-10 hover:bg-opacity-20`}
      aria-label={`Call ${item.label} at ${item.number}`}
    >
      <div className="flex items-center gap-3 sm:gap-4">
        <div className={`p-2 sm:p-3 rounded-full ${item.color} bg-opacity-20 group-hover:bg-opacity-30 transition-all duration-300`}>
          <div className="text-white text-sm sm:text-base">{item.icon}</div>
        </div>
        <div className="text-left">
          <p className={`font-bold text-base sm:text-lg ${item.color.replace('bg', 'text')}`}>{item.number}</p>
          <p className="text-gray-700 font-medium text-sm sm:text-base">{item.label}</p>
          <p className="text-xs sm:text-sm text-gray-500">{item.description}</p>
        </div>
      </div>
      
      <div className="flex items-center gap-2">
        <div className={`p-2 rounded-full ${item.color} bg-opacity-20 group-hover:bg-opacity-30 transition-all duration-300`}>
          <PhoneIcon className={`h-4 w-4 sm:h-5 sm:w-5 ${item.color.replace('bg', 'text')} group-hover:scale-110 transition-transform duration-200`} />
        </div>
        <span className={`text-xs sm:text-sm font-medium hidden md:inline ${item.color.replace('bg', 'text')}`}>Call Now</span>
      </div>
    </button>
  );

  const EmergencyList = () => (
    <div className="space-y-3 max-h-[60vh] overflow-y-auto pr-2 custom-scrollbar">
      {emergencyNumbers
        .sort((a, b) => a.priority - b.priority)
        .map((item) => (
          <EmergencyButton key={item.number} item={item} />
        ))}
    </div>
  );

  return (
    <>
      {/* Floating Button */}
      <button
        onClick={() => setIsOpen(true)}
        className="emergency-btn-responsive bg-gradient-to-r from-red-600 to-red-700 text-white shadow-lg hover:shadow-xl hover:scale-105 transition-all duration-300 flex items-center gap-2 group"
        aria-label="Open Emergency Help"
      >
        <div className="relative">
          <ExclamationTriangleIcon className="h-5 w-5 sm:h-6 sm:w-6 animate-pulse" />
          <span className="absolute -top-1 -right-1 h-2 w-2 sm:h-3 sm:w-3 bg-red-400 rounded-full animate-ping"></span>
        </div>
        <span className="hidden md:inline-block font-semibold text-sm sm:text-base">Emergency Help</span>
      </button>

      {/* Sticky Sidebar */}
      {isSticky && !isMobile && (
        <div className="fixed right-0 top-1/2 transform -translate-y-1/2 bg-white shadow-2xl rounded-l-lg p-4 sm:p-6 z-40 w-80 sm:w-96 border-l-4 border-red-500 animate-slide-in">
          <div className="flex justify-between items-center mb-4 sm:mb-6">
            <h3 className="text-lg sm:text-xl font-bold text-red-600">Emergency Help</h3>
            <button
              onClick={toggleSticky}
              className="text-gray-500 hover:text-gray-700 p-2 hover:bg-gray-100 rounded-full transition-colors duration-200"
              aria-label="Close Emergency Help"
            >
              <XMarkIcon className="h-4 w-4 sm:h-5 sm:w-5" />
            </button>
          </div>
          <EmergencyList />
        </div>
      )}

      {/* Modal */}
      {isOpen && (
        <div 
          className="modal-responsive bg-black bg-opacity-50 backdrop-blur-sm animate-fade-in"
          onClick={(e) => e.target === e.currentTarget && setIsOpen(false)}
        >
          <div className="modal-content-responsive bg-white relative animate-scale-in shadow-2xl border border-gray-100">
            {/* Header */}
            <div className="flex justify-between items-center mb-4 sm:mb-6">
              <div>
                <h2 className="text-xl sm:text-2xl lg:text-3xl font-bold text-red-600 mb-2">Emergency Help</h2>
                <p className="text-gray-600 text-sm sm:text-base">
                  If you or someone is in danger or distress, don't hesitate to call these helplines immediately.
                </p>
              </div>
              
              <div className="flex items-center gap-2">
                {!isMobile && (
                  <button
                    onClick={toggleSticky}
                    className="text-gray-500 hover:text-gray-700 p-2 hover:bg-gray-100 rounded-full transition-colors duration-200"
                    aria-label="Pin Emergency Help"
                  >
                    <svg xmlns="http://www.w3.org/2000/svg" className="h-4 w-4 sm:h-5 sm:w-5" viewBox="0 0 20 20" fill="currentColor">
                      <path fillRule="evenodd" d="M10 2a1 1 0 011 1v1.323l3.954 1.582 1.599-.8a1 1 0 01.894 1.79l-1.233.616 1.738 5.42a1 1 0 01-.285 1.05A3.989 3.989 0 0115 15a3.989 3.989 0 01-2.667-1.019 1 1 0 01-.285-1.05l1.715-5.349L11 4.477V16h2a1 1 0 110 2H7a1 1 0 110-2h2V4.477L6.237 7.582l1.715 5.349a1 1 0 01-.285 1.05A3.989 3.989 0 015 15a3.989 3.989 0 01-2.667-1.019 1 1 0 01-.285-1.05l1.738-5.42-1.233-.616a1 1 0 01.894-1.79l1.599.8L9 4.323V3a1 1 0 011-1z" clipRule="evenodd" />
                    </svg>
                  </button>
                )}
                <button
                  onClick={() => setIsOpen(false)}
                  className="text-gray-500 hover:text-gray-700 p-2 hover:bg-gray-100 rounded-full transition-colors duration-200"
                  aria-label="Close Emergency Help"
                >
                  <XMarkIcon className="h-5 w-5 sm:h-6 sm:w-6" />
                </button>
              </div>
            </div>

            {/* Emergency Numbers */}
            <EmergencyList />

            {/* Info Tooltip */}
            <div className="mt-4 sm:mt-6 pt-4 sm:pt-6 border-t border-gray-200">
              <div className="flex items-center justify-between">
                <div className="flex items-center gap-2">
                  <InformationCircleIcon className="h-4 w-4 sm:h-5 sm:w-5 text-blue-500" />
                  <span className="text-sm sm:text-base text-gray-600">Need more information?</span>
                </div>
                <button
                  onClick={() => setShowInfo(!showInfo)}
                  className="text-blue-600 hover:text-blue-700 text-sm sm:text-base font-medium"
                >
                  {showInfo ? 'Hide' : 'Show'} Info
                </button>
              </div>
              
              {showInfo && (
                <div className="mt-3 p-3 sm:p-4 bg-blue-50 rounded-lg animate-slide-up">
                  <h4 className="font-semibold text-blue-900 mb-2 text-sm sm:text-base">Emergency Guidelines:</h4>
                  <ul className="text-sm sm:text-base text-blue-800 space-y-1">
                    <li>• Stay calm and provide clear information</li>
                    <li>• Follow operator instructions carefully</li>
                    <li>• Keep the line open until help arrives</li>
                    <li>• If possible, stay with the person in need</li>
                  </ul>
                </div>
              )}
            </div>
          </div>
        </div>
      )}

      <style jsx>{`
        .custom-scrollbar::-webkit-scrollbar {
          width: 6px;
        }
        .custom-scrollbar::-webkit-scrollbar-track {
          background: #f1f1f1;
          border-radius: 3px;
        }
        .custom-scrollbar::-webkit-scrollbar-thumb {
          background: #888;
          border-radius: 3px;
        }
        .custom-scrollbar::-webkit-scrollbar-thumb:hover {
          background: #555;
        }
        @keyframes slide-in {
          from {
            transform: translateX(100%);
          }
          to {
            transform: translateX(0);
          }
        }
        @keyframes scale-in {
          from {
            transform: scale(0.95);
            opacity: 0;
          }
          to {
            transform: scale(1);
            opacity: 1;
          }
        }
        .animate-slide-in {
          animation: slide-in 0.3s ease-out;
        }
        .animate-scale-in {
          animation: scale-in 0.2s ease-out;
        }
      `}</style>
    </>
  );
};

export default EmergencyHelp;
