import React, { useState, useRef, useEffect } from 'react';
import { useTranslation } from 'react-i18next';
import {
  ChatBubbleLeftRightIcon,
  XMarkIcon,
  PaperAirplaneIcon,
  QuestionMarkCircleIcon,
  UserIcon,
  ComputerDesktopIcon,
  MagnifyingGlassIcon,
  LightBulbIcon,
  HeartIcon,
  ShieldCheckIcon,
  GlobeAltIcon,
  PhoneIcon,
  EnvelopeIcon,
  MapPinIcon,
  ClockIcon,
  DocumentTextIcon,
  UserGroupIcon,
  CogIcon,
  ExclamationTriangleIcon,
  CheckCircleIcon
} from '@heroicons/react/24/outline';

const FAQChatbot = ({ isOpen, onClose }) => {
  const { t } = useTranslation();
  const [messages, setMessages] = useState([]);
  const [inputMessage, setInputMessage] = useState('');
  const [isTyping, setIsTyping] = useState(false);
  const [showQuickReplies, setShowQuickReplies] = useState(true);
  const messagesEndRef = useRef(null);
  const inputRef = useRef(null);

  // Comprehensive FAQ database with human-like responses
  const faqDatabase = {
    // General Questions
    'what is avyakta': {
      answer: "Avyakta is a revolutionary platform dedicated to giving dignity to the forgotten. We help unidentified individuals, missing persons, and their families by providing a comprehensive system for reporting, tracking, and resolving cases. Our mission is to bridge the gap between those who need help and those who can provide it.",
      category: 'general',
      keywords: ['what', 'is', 'avyakta', 'platform', 'purpose', 'mission']
    },
    'how does avyakta work': {
      answer: "Avyakta works through a simple yet powerful process: 1) Users can report missing persons or unidentified individuals, 2) Our AI-powered system analyzes and matches cases, 3) We connect families, law enforcement, and NGOs, 4) We provide regular updates and support throughout the process. It's like having a digital detective that never sleeps!",
      category: 'general',
      keywords: ['how', 'does', 'work', 'process', 'system', 'ai']
    },
    'who can use avyakta': {
      answer: "Anyone can use Avyakta! Families looking for missing loved ones, law enforcement agencies, NGOs, hospitals, and even concerned citizens who want to help. We have different user roles - from common users who can report cases to admins and NGOs who can manage and coordinate efforts. Everyone has a role in bringing people home.",
      category: 'general',
      keywords: ['who', 'can', 'use', 'users', 'roles', 'families', 'police', 'ngo']
    },

    // Registration and Account
    'how to register': {
      answer: "Registration is super easy! Just click on 'Register' and follow these steps: 1) Fill in your basic details (name, mobile, email), 2) Verify your mobile with OTP, 3) Set up your password and choose your role, 4) Complete the security verification. The whole process takes just a few minutes, and we'll send you a welcome email!",
      category: 'account',
      keywords: ['how', 'to', 'register', 'signup', 'account', 'create', 'otp']
    },
    'what are the different user roles': {
