      category: 'account',
      keywords: ['roles', 'user', 'types', 'admin', 'ngo', 'common', 'permissions']
    },
    'forgot password': {
      answer: "No worries! If you've forgotten your password, just click on 'Forgot Password' on the login page. We'll send you a password reset link to your registered email address. Make sure to check your spam folder if you don't see it in your inbox. The link is valid for 24 hours for your security.",
      category: 'account',
      keywords: ['forgot', 'password', 'reset', 'recover', 'lost', 'email']
    },

    // Reporting Cases
    'how to report missing person': {
      answer: "Reporting a missing person is straightforward: 1) Log into your account, 2) Click 'Report Missing Person', 3) Fill in all the details (name, age, last seen location, description, photos), 4) Submit the report. Our system will immediately start working to help find them. The more details you provide, the better our chances of success!",
      category: 'reporting',
      keywords: ['how', 'to', 'report', 'missing', 'person', 'case', 'submit']
    },
    'what information do i need to report': {
