import React, { useState } from 'react';

const sections = [
  {
    title: 'General User Guidelines',
    icon: '📘',
    color: 'bg-blue-100 text-blue-700',
    content: (
      <ul className="space-y-2">
        <li><span className="inline-block mr-2">✅</span> You can report anonymously</li>
        <li><span className="inline-block mr-2">✅</span> Share only verified, real information</li>
        <li><span className="inline-block mr-2">📍</span> Location, Date, Time (auto/manual)</li>
        <li><span className="inline-block mr-2">📎</span> Upload image (only if necessary)</li>
        <li><span className="inline-block mr-2">✍</span> Add short, clear description</li>
      </ul>
    )
  },
  {
    title: 'You Must NOT',
    icon: '🚫',
    color: 'bg-red-100 text-red-700',
    content: (
      <ul className="space-y-2">
        <li><span className="inline-block mr-2">❌</span> Submit false/misleading reports</li>
        <li><span className="inline-block mr-2">❌</span> Use it to harm/defame others</li>
        <li><span className="inline-block mr-2">❌</span> Use someone else's identity</li>
      </ul>
    )
  },
  {
    title: 'Privacy & Traceability',
    icon: '🔒',
    color: 'bg-purple-100 text-purple-700',
    content: (
      <ul className="space-y-2">
        <li><span className="inline-block mr-2">🔎</span> All reports are traceable under UID</li>
        <li><span className="inline-block mr-2">👤</span> Anonymous reports are also tracked for transparency</li>
      </ul>
    )
  },
  {
    title: 'Need Help?',
    icon: '📩',
    color: 'bg-green-100 text-green-700',
    content: (
      <ul className="space-y-2">
        <li><span className="inline-block mr-2">✉️</span> Email: <a href="mailto:support@avyakta.org" className="underline text-blue-700">support@avyakta.org</a></li>
        <li><span className="inline-block mr-2">🚨</span> Use Emergency button or Live chat if stuck</li>
      </ul>
    )
  }
];

export default function Guidelines() {
  const [open, setOpen] = useState([true, false, false, false]);
  const [accepted, setAccepted] = useState(false);

  const handleAccordion = idx => {
    setOpen(prev => prev.map((v, i) => (i === idx ? !v : v)));
  };

  return (
    <div className="min-h-screen py-10 px-2 sm:px-0 bg-[#f9f9f9] dark:bg-[#18132a] transition-colors duration-300 font-[Inter,Poppins,Nunito_Sans,sans-serif]">
      {/* Intro Section */}
      <div className="max-w-2xl mx-auto mb-8">
        <div className="flex flex-col items-center mb-4">
          <span className="block w-10 h-10 mb-2 animate-glow">
            <svg width="40" height="40" viewBox="0 0 24 24" fill="none">
              <ellipse cx="12" cy="20" rx="5" ry="2" fill="#ffe066" opacity="0.3" />
              <path className="animate-flicker" d="M12 20c3-4.5 3-9 0-13-3 4.5-3 9 0 13z" fill="#ffe066" />
              <path className="animate-flicker" d="M12 16c1.5-2.5 1.5-5 0-7.5-1.5 2.5-1.5 5 0 7.5z" fill="#ffb700" />
            </svg>
          </span>
          <h1 className="text-2xl sm:text-3xl font-bold text-gray-900 dark:text-white mb-2">Before You Report, Please Read</h1>
          <div className="text-base sm:text-lg text-gray-700 dark:text-gray-200 mb-2 text-center">This is not a casual reporting site. Every case matters, every report carries weight.</div>
          <div className="border-l-4 border-yellow-400 pl-4 py-2 bg-yellow-50 dark:bg-yellow-900/20 text-yellow-800 dark:text-yellow-200 rounded-xl shadow animate-fade-in max-w-xl mx-auto">
            <span className="italic font-serif">"Dignity begins with truth. Even the voiceless deserve justice."</span>
          </div>
        </div>
      </div>
      {/* Guidelines Sections */}
      <div className="max-w-2xl mx-auto space-y-5">
        {sections.map((section, idx) => (
          <div
            key={section.title}
            className={`rounded-2xl shadow-md bg-white dark:bg-[#231c3a] transition-all duration-300 overflow-hidden animate-fade-in`}
            style={{ boxShadow: '0 2px 16px 0 #0001' }}
          >
            <button
              className="w-full flex items-center justify-between px-6 py-4 text-lg font-semibold focus:outline-none select-none"
              onClick={() => handleAccordion(idx)}
              aria-expanded={open[idx]}
              style={{ fontFamily: 'Poppins, Inter, Nunito Sans, sans-serif' }}
            >
              <span className="flex items-center gap-2">
                <span className={`inline-block text-2xl ${section.color}`}>{section.icon}</span>
                {section.title}
              </span>
              <span className="text-gray-400 text-xl">{open[idx] ? '−' : '+'}</span>
            </button>
            <div
              className={`transition-all duration-400 ease-in-out px-6 ${open[idx] ? 'max-h-96 py-4 opacity-100' : 'max-h-0 py-0 opacity-0'} overflow-hidden`}
              style={{ fontSize: '1.05rem' }}
            >
              {section.content}
            </div>
          </div>
        ))}
      </div>
      {/* Accept & Continue */}
      <form className="max-w-2xl mx-auto mt-10 mb-8 bg-white dark:bg-[#231c3a] rounded-2xl shadow-md p-6 flex flex-col items-center animate-fade-in">
        <label className="flex items-center gap-2 mb-4 text-gray-800 dark:text-gray-100">
          <input
            type="checkbox"
            checked={accepted}
            onChange={e => setAccepted(e.target.checked)}
            required
            className="form-checkbox h-5 w-5 text-purple-600"
          />
          <span className="font-medium">I have read and accept the above guidelines</span>
        </label>
        <button
          type="submit"
          className="w-full max-w-xs bg-gradient-to-r from-purple-700 to-purple-900 text-white font-bold py-3 px-6 rounded-xl shadow-lg disabled:opacity-60 transition-all hover:from-purple-800 hover:to-purple-950"
          disabled={!accepted}
        >
          I Accept & Continue
        </button>
      </form>
      {/* Animations */}
      <style>{`
        @keyframes flicker { 0%{opacity:1;} 50%{opacity:0.7;} 100%{opacity:1;} }
        .animate-flicker { animation: flicker 1.2s infinite alternate; }
        .animate-glow { filter: drop-shadow(0 0 8px #ffe066) drop-shadow(0 0 16px #ffe06688); }
        .animate-fade-in { animation: fadeIn 0.7s ease; }
        @keyframes fadeIn { from { opacity: 0; transform: translateY(16px);} to { opacity: 1; transform: none; } }
      `}</style>
    </div>
  );
}
