import React, { useState, useRef, useEffect } from 'react';
import { PhoneIcon, ChatBubbleLeftRightIcon, XMarkIcon, PaperAirplaneIcon, ExclamationTriangleIcon, ShieldExclamationIcon } from '@heroicons/react/24/outline';

const FloatingActionButton = () => {
  const [isFabExpanded, setIsFabExpanded] = useState(false);
  const [isChatOpen, setIsChatOpen] = useState(false);
  const [messages, setMessages] = useState([
    { type: 'bot', text: 'Hello! How can I help you today?' }
  ]);
  const [inputMessage, setInputMessage] = useState('');
  const chatEndRef = useRef(null);

  const scrollToBottom = () => {
    chatEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const handleSendMessage = () => {
    if (!inputMessage.trim()) return;

    // Add user message
    setMessages(prev => [...prev, { type: 'user', text: inputMessage }]);
    
    // Simulate bot response
    setTimeout(() => {
      const response = getBotResponse(inputMessage);
      setMessages(prev => [...prev, { type: 'bot', text: response }]);
    }, 1000);

    setInputMessage('');
  };

  const getBotResponse = (message) => {
    const lowerMessage = message.toLowerCase();
    
    // Website information responses
    if (lowerMessage.includes('website') || lowerMessage.includes('site')) {
      return "Avyakta is a platform dedicated to helping identify and bring dignity to unclaimed bodies. We use technology to connect families with their lost loved ones and provide support to those in need.";
    }
    
    if (lowerMessage.includes('about') || lowerMessage.includes('mission')) {
      return "Our mission is to leverage technology to help identify and respond to unclaimed bodies with respect and compassion, while providing closure to families and communities.";
    }
    
    if (lowerMessage.includes('contact') || lowerMessage.includes('reach')) {
      return "You can reach us through our contact page, email at support@avyakta.org, or call our helpline at 1800-XXX-XXXX.";
    }
    
    if (lowerMessage.includes('help') || lowerMessage.includes('support')) {
      return "We're here to help! You can report an unclaimed body, seek support, or get information about our services. What specific assistance do you need?";
    }
    
    if (lowerMessage.includes('report') || lowerMessage.includes('found')) {
      return "To report an unclaimed body, please use our emergency SOS button or visit the 'Report' section on our website. We'll guide you through the process.";
    }
    
    // Default response
    return "I'm here to help you with information about Avyakta. You can ask me about our mission, services, how to report cases, or how to get support.";
  };

  return (
    <>
      {/* Floating Action Button Group */}
      <div className="fixed bottom-24 right-6 z-50">
        {/* Main FAB */}
        <button
          onClick={() => setIsFabExpanded(!isFabExpanded)}
          className={`w-16 h-16 rounded-full bg-[#003f88] text-white shadow-lg flex items-center justify-center transition-all duration-300 transform hover:scale-110 ${
            isFabExpanded ? 'rotate-45' : ''
          }`}
        >
          {isFabExpanded ? (
            <XMarkIcon className="w-7 h-7" />
          ) : (
            <ShieldExclamationIcon className="w-7 h-7" />
          )}
        </button>

        {/* Expanded Options */}
        <div
          className={`absolute bottom-20 right-0 flex flex-col gap-4 transition-all duration-300 ${
            isFabExpanded ? 'opacity-100 translate-y-0' : 'opacity-0 translate-y-4 pointer-events-none'
          }`}
        >
          {/* Emergency Help Button */}
          <button
            onClick={() => {
              // Handle emergency help action
              window.location.href = '/emergency-help';
            }}
            className="w-14 h-14 rounded-full bg-orange-500 text-white shadow-lg flex items-center justify-center transform hover:scale-110 transition-all duration-300"
          >
            <ExclamationTriangleIcon className="w-6 h-6" />
          </button>

          {/* Chat Button */}
          <button
            onClick={() => setIsChatOpen(!isChatOpen)}
            className="w-14 h-14 rounded-full bg-blue-500 text-white shadow-lg flex items-center justify-center transform hover:scale-110 transition-all duration-300"
          >
            <ChatBubbleLeftRightIcon className="w-6 h-6" />
          </button>

          {/* SOS Button */}
          <button
            onClick={() => {
              window.location.href = 'tel:100';
            }}
            className="w-14 h-14 rounded-full bg-red-600 text-white shadow-lg flex items-center justify-center transform hover:scale-110 transition-all duration-300 animate-pulse"
          >
            <span className="text-sm font-bold">SOS</span>
          </button>
        </div>
      </div>

      {/* Chat Box */}
      <div
        className={`fixed bottom-32 right-6 w-80 h-96 bg-white rounded-lg shadow-xl transition-all duration-300 transform ${
          isChatOpen ? 'opacity-100 translate-y-0' : 'opacity-0 translate-y-4 pointer-events-none'
        }`}
      >
        <div className="h-full flex flex-col">
          {/* Chat Header */}
          <div className="bg-[#003f88] text-white p-4 rounded-t-lg flex justify-between items-center">
            <h3 className="font-semibold">Emergency Support Chat</h3>
            <button
              onClick={() => setIsChatOpen(false)}
              className="text-white hover:text-gray-200"
            >
              <XMarkIcon className="w-5 h-5" />
            </button>
          </div>

          {/* Chat Messages Area */}
          <div className="flex-1 p-4 overflow-y-auto bg-gray-50">
            <div className="space-y-4">
              {messages.map((message, index) => (
                <div
                  key={index}
                  className={`flex ${message.type === 'user' ? 'justify-end' : 'justify-start'}`}
                >
                  <div
                    className={`rounded-lg p-3 max-w-[80%] ${
                      message.type === 'user'
                        ? 'bg-[#003f88] text-white'
                        : 'bg-blue-100 text-gray-800'
                    }`}
                  >
                    <p className="text-sm">{message.text}</p>
                  </div>
                </div>
              ))}
              <div ref={chatEndRef} />
            </div>
          </div>

          {/* Chat Input */}
          <div className="p-4 border-t">
            <div className="flex gap-2">
              <input
                type="text"
                value={inputMessage}
                onChange={(e) => setInputMessage(e.target.value)}
                onKeyPress={(e) => e.key === 'Enter' && handleSendMessage()}
                placeholder="Type your message..."
                className="flex-1 px-4 py-2 border rounded-full focus:outline-none focus:ring-2 focus:ring-[#003f88]"
              />
              <button
                onClick={handleSendMessage}
                className="bg-[#003f88] text-white px-4 py-2 rounded-full hover:bg-blue-700 transition-colors"
              >
                <PaperAirplaneIcon className="w-5 h-5" />
              </button>
            </div>
          </div>
        </div>
      </div>
    </>
  );
};

export default FloatingActionButton;
