import React, { useState, useRef, useEffect } from 'react';
import { 
  PhoneIcon, 
  ChatBubbleLeftRightIcon, 
  XMarkIcon, 
  PaperAirplaneIcon, 
  ExclamationTriangleIcon, 
  ShieldExclamationIcon,
  InformationCircleIcon,
  UserIcon,
  HeartIcon,
  GlobeAltIcon,
  DocumentTextIcon,
  MapPinIcon,
  ClockIcon
} from '@heroicons/react/24/outline';

const FloatingActionButton = () => {
  const [isFabExpanded, setIsFabExpanded] = useState(false);
  const [isChatOpen, setIsChatOpen] = useState(false);
  const [messages, setMessages] = useState([]);
  const [inputMessage, setInputMessage] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const chatEndRef = useRef(null);

  const scrollToBottom = () => {
    chatEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  // Clear chat history when opening chat
  useEffect(() => {
    if (isChatOpen) {
      setMessages([{
        type: 'bot',
        text: "Hi! I'm Avyakta Assistant. I'm here to help you with any questions about our website or support you in difficult situations. How can I assist you today?",
        timestamp: new Date(),
        quickActions: [
          { text: "About Avyakta", action: "about" },
          { text: "Emergency Help", action: "emergency" },
          { text: "Contact Us", action: "contact" },
          { text: "How to Report", action: "report" }
        ]
      }]);
    }
  }, [isChatOpen]);

  const getResponse = (message) => {
    const lowerMessage = message.toLowerCase();

    // EMERGENCY SITUATIONS - HIGH PRIORITY
    if (lowerMessage.includes('dead') || lowerMessage.includes('body') || lowerMessage.includes('corpse') || lowerMessage.includes('unconscious') || lowerMessage.includes('not breathing')) {
      return {
        text: "ðŸš¨ EMERGENCY: This is a critical situation!

IMMEDIATE ACTIONS:
1ï¸âƒ£ Call 100 (Police) immediately
2ï¸âƒ£ Call 102 (Ambulance) if medical help needed
3ï¸âƒ£ Stay at location if safe
4ï¸âƒ£ Don't touch or move anything
5ï¸âƒ£ Take photos if safe to do so

While help arrives:
â€¢ Note exact location details
â€¢ Check for any identification
â€¢ Look for witnesses
â€¢ Stay calm and wait for authorities

I'm here to guide you through this difficult time.",
        quickActions: [
          { text: "ðŸš¨ Call 100 Now", action: "call_100" },
          { text: "ðŸš‘ Call Ambulance", action: "call_ambulance" },
          { text: "ðŸ“ Location Help", action: "location_help" },
          { text: "ðŸ“‹ Report Online", action: "report_online" }
        ]
      };
    }

    // Missing Person Cases
    if (lowerMessage.includes('missing') || lowerMessage.includes('lost') || lowerMessage.includes('disappeared') || lowerMessage.includes('can't find')) {
      return {
        text: "ðŸ” MISSING PERSON CASE

URGENT STEPS:
1ï¸âƒ£ File FIR at nearest police station
2ï¸âƒ£ Contact local hospitals and morgues
3ï¸âƒ£ Check CCTV footage in area
4ï¸âƒ£ Share on social media with photos
5ï¸âƒ£ Contact us for additional support

What to provide:
â€¢ Recent photo
â€¢ Last known location
â€¢ Clothing description
â€¢ Personal details
â€¢ Timeline of events

We can help you create a missing person report and coordinate with authorities.",
        quickActions: [
          { text: "ðŸ“‹ File Missing Report", action: "missing_report" },
          { text: "ðŸ¥ Check Hospitals", action: "check_hospitals" },
          { text: "ðŸ‘® Contact Police", action: "contact_police" },
          { text: "ðŸ“ž Call Helpline", action: "call_helpline" }
        ]
      };
    }

    // Medical Emergencies
    if (lowerMessage.includes('heart attack') || lowerMessage.includes('stroke') || lowerMessage.includes('bleeding') || lowerMessage.includes('accident') || lowerMessage.includes('injury')) {
      return {
        text: "ðŸš‘ MEDICAL EMERGENCY

IMMEDIATE ACTION:
1ï¸âƒ£ Call 102 (Ambulance) NOW
2ï¸âƒ£ Call 100 (Police) if accident
3ï¸âƒ£ Don't move injured person
4ï¸âƒ£ Apply first aid if trained
5ï¸âƒ£ Keep person warm and comfortable

While waiting:
â€¢ Monitor breathing
â€¢ Keep airway clear
â€¢ Don't give food/water
â€¢ Stay with the person

Every second counts in medical emergencies!",
        quickActions: [
          { text: "ðŸš‘ Call Ambulance", action: "call_ambulance" },
          { text: "ðŸš¨ Call Police", action: "call_100" },
          { text: "ðŸ“ž Emergency Help", action: "emergency_help" },
          { text: "ðŸ¥ Find Hospital", action: "find_hospital" }
        ]
      };
    }

    // Domestic Violence/Abuse
    if (lowerMessage.includes('abuse') || lowerMessage.includes('violence') || lowerMessage.includes('beaten') || lowerMessage.includes('hit') || lowerMessage.includes('threatened')) {
      return {
        text: "ðŸ›¡ï¸ DOMESTIC VIOLENCE/ABUSE

SAFETY FIRST:
1ï¸âƒ£ Get to a safe location immediately
2ï¸âƒ£ Call 100 (Police) for immediate help
3ï¸âƒ£ Call 1091 (Women Helpline)
4ï¸âƒ£ Contact trusted family/friend
5ï¸âƒ£ Document injuries with photos

Support Available:
â€¢ Legal assistance
â€¢ Shelter homes
â€¢ Counseling services
â€¢ Medical help
â€¢ Police protection

You're not alone. Help is available 24/7.",
        quickActions: [
          { text: "ðŸš¨ Call Police", action: "call_100" },
          { text: "ðŸ‘© Women Helpline", action: "women_helpline" },
          { text: "ðŸ  Find Shelter", action: "find_shelter" },
          { text: "ðŸ“ž Get Help", action: "get_help" }
        ]
      };
    }

    // Suicide Prevention
    if (lowerMessage.includes('suicide') || lowerMessage.includes('kill myself') || lowerMessage.includes('end life') || lowerMessage.includes('no reason to live')) {
      return {
        text: "ðŸ’™ SUICIDE PREVENTION

YOU MATTER! Please don't give up.

IMMEDIATE HELP:
1ï¸âƒ£ Call 9152987821 (AASRA)
2ï¸âƒ£ Call 1800-599-0019 (KIRAN)
3ï¸âƒ£ Talk to someone you trust
4ï¸âƒ£ Go to nearest hospital
5ï¸âƒ£ You're not alone in this

Remember:
â€¢ This feeling is temporary
â€¢ Help is available 24/7
â€¢ You are valued and loved
â€¢ Things will get better

Please reach out for help. You deserve support.",
        quickActions: [
          { text: "ðŸ“ž AASRA Helpline", action: "aasra_helpline" },
          { text: "ðŸ“ž KIRAN Helpline", action: "kiran_helpline" },
          { text: "ðŸ¥ Go to Hospital", action: "go_hospital" },
          { text: "ðŸ’¬ Talk to Someone", action: "talk_someone" }
        ]
      };
    }

    // Child Abuse/Exploitation
    if (lowerMessage.includes('child') && (lowerMessage.includes('abuse') || lowerMessage.includes('molest') || lowerMessage.includes('exploit'))) {
      return {
        text: "ðŸ‘¶ CHILD ABUSE/EXPLOITATION

URGENT ACTION REQUIRED:
1ï¸âƒ£ Call 1098 (Child Helpline) immediately
2ï¸âƒ£ Call 100 (Police)
3ï¸âƒ£ Contact Child Welfare Committee
4ï¸âƒ£ Document everything
5ï¸âƒ£ Keep child safe

What to do:
â€¢ Don't confront abuser
â€¢ Preserve evidence
â€¢ Get medical examination
â€¢ Contact authorities
â€¢ Provide emotional support

Child safety is everyone's responsibility!",
        quickActions: [
          { text: "ðŸ“ž Child Helpline", action: "child_helpline" },
          { text: "ðŸš¨ Call Police", action: "call_100" },
          { text: "ðŸ¥ Medical Help", action: "medical_help" },
          { text: "ðŸ“‹ Report Case", action: "report_case" }
        ]
      };
    }

    // Sexual Harassment/Assault
    if (lowerMessage.includes('sexual') || lowerMessage.includes('harassment') || lowerMessage.includes('assault') || lowerMessage.includes('rape')) {
      return {
        text: "ðŸš¨ SEXUAL HARASSMENT/ASSAULT

IMMEDIATE STEPS:
1ï¸âƒ£ Get to safe location
2ï¸âƒ£ Call 100 (Police) immediately
3ï¸âƒ£ Call 1091 (Women Helpline)
4ï¸âƒ£ Don't wash/change clothes
5ï¸âƒ£ Go to hospital for medical exam

Important:
â€¢ Preserve all evidence
â€¢ Document everything
â€¢ Get medical attention
â€¢ File FIR
â€¢ Seek counseling

You have rights. Justice will be served.",
        quickActions: [
          { text: "ðŸš¨ Call Police", action: "call_100" },
          { text: "ðŸ‘© Women Helpline", action: "women_helpline" },
          { text: "ðŸ¥ Medical Exam", action: "medical_exam" },
          { text: "ðŸ“‹ File FIR", action: "file_fir" }
        ]
      };
    }

    // Road Accidents
    if (lowerMessage.includes('accident') || lowerMessage.includes('crash') || lowerMessage.includes('hit by car') || lowerMessage.includes('road')) {
      return {
        text: "ðŸš— ROAD ACCIDENT

EMERGENCY RESPONSE:
1ï¸âƒ£ Call 100 (Police) immediately
2ï¸âƒ£ Call 102 (Ambulance)
3ï¸âƒ£ Call 103 (Traffic Police)
4ï¸âƒ£ Secure accident scene
5ï¸âƒ£ Help injured if safe

At scene:
â€¢ Don't move injured
â€¢ Take photos
â€¢ Note vehicle numbers
â€¢ Get witness details
â€¢ Stay until police arrive

Safety first - don't risk your life!",
        quickActions: [
          { text: "ðŸš¨ Call Police", action: "call_100" },
          { text: "ðŸš‘ Call Ambulance", action: "call_ambulance" },
          { text: "ðŸš¦ Traffic Police", action: "traffic_police" },
          { text: "ðŸ“‹ Accident Report", action: "accident_report" }
        ]
      };
    }

    // Fire Emergencies
    if (lowerMessage.includes('fire') || lowerMessage.includes('burning') || lowerMessage.includes('smoke')) {
      return {
        text: "ðŸ”¥ FIRE EMERGENCY

IMMEDIATE ACTION:
1ï¸âƒ£ Call 101 (Fire Brigade) immediately
2ï¸âƒ£ Call 100 (Police)
3ï¸âƒ£ Evacuate area
4ï¸âƒ£ Don't use elevators
5ï¸âƒ£ Stay low if smoke

Safety:
â€¢ Get out quickly
â€¢ Don't go back for belongings
â€¢ Meet at safe location
â€¢ Don't re-enter building
â€¢ Wait for firefighters

Fire spreads fast - evacuate immediately!",
        quickActions: [
          { text: "ðŸ”¥ Call Fire Brigade", action: "call_fire" },
          { text: "ðŸš¨ Call Police", action: "call_100" },
          { text: "ðŸšª Evacuate", action: "evacuate" },
          { text: "ðŸ“ž Emergency", action: "emergency" }
        ]
      };
    }

    // Natural Disasters
    if (lowerMessage.includes('earthquake') || lowerMessage.includes('flood') || lowerMessage.includes('cyclone') || lowerMessage.includes('tsunami')) {
      return {
        text: "ðŸŒªï¸ NATURAL DISASTER

SAFETY PROTOCOLS:
1ï¸âƒ£ Call 100 (Emergency)
2ï¸âƒ£ Follow local authorities
3ï¸âƒ£ Move to safe location
4ï¸âƒ£ Stay informed via radio/TV
5ï¸âƒ£ Help others if safe

General Safety:
â€¢ Stay indoors if advised
â€¢ Avoid flood waters
â€¢ Don't use phone unless emergency
â€¢ Keep emergency kit ready
â€¢ Follow evacuation orders

Stay calm and follow official instructions!",
        quickActions: [
          { text: "ðŸš¨ Emergency", action: "call_100" },
          { text: "ðŸ“» Stay Informed", action: "stay_informed" },
          { text: "ðŸ  Safe Location", action: "safe_location" },
          { text: "ðŸ“ž Get Help", action: "get_help" }
        ]
      };
