import React, { useState, useRef, useEffect } from 'react';
import { 
  PhoneIcon, 
  ChatBubbleLeftRightIcon, 
  XMarkIcon, 
  PaperAirplaneIcon, 
  ExclamationTriangleIcon, 
  ShieldExclamationIcon,
  InformationCircleIcon,
  UserIcon,
  HeartIcon,
  GlobeAltIcon,
  DocumentTextIcon,
  MapPinIcon,
  ClockIcon
} from '@heroicons/react/24/outline';

const FloatingActionButton = () => {
  const [isFabExpanded, setIsFabExpanded] = useState(false);
  const [isChatOpen, setIsChatOpen] = useState(false);
  const [messages, setMessages] = useState([]);
  const [inputMessage, setInputMessage] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const chatEndRef = useRef(null);

  const scrollToBottom = () => {
    chatEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  // Clear chat history when opening chat
  useEffect(() => {
    if (isChatOpen) {
      setMessages([{
        type: 'bot',
        text: "Hi! I'm Avyakta Assistant. I'm here to help you with any questions about our website or support you in difficult situations. How can I assist you today?",
        timestamp: new Date(),
        quickActions: [
          { text: "About Avyakta", action: "about" },
          { text: "Emergency Help", action: "emergency" },
          { text: "Contact Us", action: "contact" },
          { text: "How to Report", action: "report" }
        ]
      }]);
    }
  }, [isChatOpen]);

  const getResponse = (message) => {
    const lowerMessage = message.toLowerCase();

    // EMERGENCY SITUATIONS - HIGH PRIORITY
    if (lowerMessage.includes('dead') || lowerMessage.includes('body') || lowerMessage.includes('corpse') || lowerMessage.includes('unconscious') || lowerMessage.includes('not breathing')) {
      return {
        text: "üö® EMERGENCY: This is a critical situation!

IMMEDIATE ACTIONS:
1Ô∏è‚É£ Call 100 (Police) immediately
2Ô∏è‚É£ Call 102 (Ambulance) if medical help needed
3Ô∏è‚É£ Stay at location if safe
4Ô∏è‚É£ Don't touch or move anything
5Ô∏è‚É£ Take photos if safe to do so

While help arrives:
‚Ä¢ Note exact location details
‚Ä¢ Check for any identification
‚Ä¢ Look for witnesses
‚Ä¢ Stay calm and wait for authorities

I'm here to guide you through this difficult time.",
        quickActions: [
          { text: "üö® Call 100 Now", action: "call_100" },
          { text: "üöë Call Ambulance", action: "call_ambulance" },
          { text: "üìç Location Help", action: "location_help" },
          { text: "üìã Report Online", action: "report_online" }
        ]
      };
    }

    // Missing Person Cases
    if (lowerMessage.includes('missing') || lowerMessage.includes('lost') || lowerMessage.includes('disappeared') || lowerMessage.includes('can't find')) {
      return {
        text: "üîç MISSING PERSON CASE

URGENT STEPS:
1Ô∏è‚É£ File FIR at nearest police station
2Ô∏è‚É£ Contact local hospitals and morgues
3Ô∏è‚É£ Check CCTV footage in area
4Ô∏è‚É£ Share on social media with photos
5Ô∏è‚É£ Contact us for additional support

What to provide:
‚Ä¢ Recent photo
‚Ä¢ Last known location
‚Ä¢ Clothing description
‚Ä¢ Personal details
‚Ä¢ Timeline of events

We can help you create a missing person report and coordinate with authorities.",
        quickActions: [
          { text: "üìã File Missing Report", action: "missing_report" },
          { text: "üè• Check Hospitals", action: "check_hospitals" },
          { text: "üëÆ Contact Police", action: "contact_police" },
          { text: "üìû Call Helpline", action: "call_helpline" }
        ]
      };
    }

    // Medical Emergencies
    if (lowerMessage.includes('heart attack') || lowerMessage.includes('stroke') || lowerMessage.includes('bleeding') || lowerMessage.includes('accident') || lowerMessage.includes('injury')) {
      return {
        text: "üöë MEDICAL EMERGENCY

IMMEDIATE ACTION:
1Ô∏è‚É£ Call 102 (Ambulance) NOW
2Ô∏è‚É£ Call 100 (Police) if accident
3Ô∏è‚É£ Don't move injured person
4Ô∏è‚É£ Apply first aid if trained
5Ô∏è‚É£ Keep person warm and comfortable

While waiting:
‚Ä¢ Monitor breathing
‚Ä¢ Keep airway clear
‚Ä¢ Don't give food/water
‚Ä¢ Stay with the person

Every second counts in medical emergencies!",
        quickActions: [
          { text: "üöë Call Ambulance", action: "call_ambulance" },
          { text: "üö® Call Police", action: "call_100" },
          { text: "üìû Emergency Help", action: "emergency_help" },
          { text: "üè• Find Hospital", action: "find_hospital" }
        ]
      };
    }

    // Domestic Violence/Abuse
    if (lowerMessage.includes('abuse') || lowerMessage.includes('violence') || lowerMessage.includes('beaten') || lowerMessage.includes('hit') || lowerMessage.includes('threatened')) {
      return {
        text: "üõ°Ô∏è DOMESTIC VIOLENCE/ABUSE

SAFETY FIRST:
1Ô∏è‚É£ Get to a safe location immediately
2Ô∏è‚É£ Call 100 (Police) for immediate help
3Ô∏è‚É£ Call 1091 (Women Helpline)
4Ô∏è‚É£ Contact trusted family/friend
5Ô∏è‚É£ Document injuries with photos

Support Available:
‚Ä¢ Legal assistance
‚Ä¢ Shelter homes
‚Ä¢ Counseling services
‚Ä¢ Medical help
‚Ä¢ Police protection

You're not alone. Help is available 24/7.",
        quickActions: [
          { text: "üö® Call Police", action: "call_100" },
          { text: "üë© Women Helpline", action: "women_helpline" },
          { text: "üè† Find Shelter", action: "find_shelter" },
          { text: "üìû Get Help", action: "get_help" }
        ]
      };
    }

    // Suicide Prevention
    if (lowerMessage.includes('suicide') || lowerMessage.includes('kill myself') || lowerMessage.includes('end life') || lowerMessage.includes('no reason to live')) {
      return {
        text: "üíô SUICIDE PREVENTION

YOU MATTER! Please don't give up.

IMMEDIATE HELP:
1Ô∏è‚É£ Call 9152987821 (AASRA)
2Ô∏è‚É£ Call 1800-599-0019 (KIRAN)
3Ô∏è‚É£ Talk to someone you trust
4Ô∏è‚É£ Go to nearest hospital
5Ô∏è‚É£ You're not alone in this

Remember:
‚Ä¢ This feeling is temporary
‚Ä¢ Help is available 24/7
‚Ä¢ You are valued and loved
‚Ä¢ Things will get better

Please reach out for help. You deserve support.",
        quickActions: [
          { text: "üìû AASRA Helpline", action: "aasra_helpline" },
          { text: "üìû KIRAN Helpline", action: "kiran_helpline" },
          { text: "üè• Go to Hospital", action: "go_hospital" },
          { text: "üí¨ Talk to Someone", action: "talk_someone" }
        ]
      };
    }

    // Child Abuse/Exploitation
    if (lowerMessage.includes('child') && (lowerMessage.includes('abuse') || lowerMessage.includes('molest') || lowerMessage.includes('exploit'))) {
      return {
        text: "üë∂ CHILD ABUSE/EXPLOITATION

URGENT ACTION REQUIRED:
1Ô∏è‚É£ Call 1098 (Child Helpline) immediately
