import React, { useState } from 'react';

const initialState = {
  location: '',
  manualLocation: false,
  dateTime: '',
  image: null,
  description: '',
  contact: '',
  anonymous: false,
  message: '',
  terms: false,
};

const ReportUnclaimedBody = () => {
  const [form, setForm] = useState(initialState);
  const [errors, setErrors] = useState({});
  const [submitting, setSubmitting] = useState(false);
  const [submitted, setSubmitted] = useState(false);
  const [otpSent, setOtpSent] = useState(false);
  const [otp, setOtp] = useState('');
  const [otpVerified, setOtpVerified] = useState(false);

  // Auto-fetch location and date/time on mount
  React.useEffect(() => {
    if (!form.manualLocation && navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        (pos) => {
          setForm((f) => ({ ...f, location: `${pos.coords.latitude}, ${pos.coords.longitude}` }));
        },
        () => {},
        { enableHighAccuracy: true }
      );
    }
    if (!form.dateTime) {
      setForm((f) => ({ ...f, dateTime: new Date().toISOString().slice(0, 16) }));
    }
  }, [form.manualLocation]);

  // Validation
  const validate = () => {
    const e = {};
    if (!form.location) e.location = 'Location is required.';
    if (!form.dateTime) e.dateTime = 'Date & Time is required.';
    if (!form.description.trim()) e.description = 'Description is required.';
    if (form.image && form.image.size > 5 * 1024 * 1024) e.image = 'Max file size is 5MB.';
    if (form.image && !['image/jpeg', 'image/png'].includes(form.image.type)) e.image = 'Only JPEG/PNG allowed.';
    if (form.contact && !/^([+]?\d{10,}|[\w.-]+@[\w.-]+\.[A-Za-z]{2,})$/.test(form.contact)) e.contact = 'Enter a valid phone or email.';
    if (!form.terms) e.terms = 'You must confirm the report is genuine.';
    return e;
  };

  // Handle field changes
  const handleChange = (e) => {
    const { name, value, type, checked, files } = e.target;
    if (type === 'checkbox') {
      setForm((f) => ({ ...f, [name]: checked }));
    } else if (type === 'file') {
      setForm((f) => ({ ...f, image: files[0] }));
    } else {
      setForm((f) => ({ ...f, [name]: value }));
    }
  };

  // Handle OTP send/verify (stubbed)
  const handleSendOtp = () => {
    // TODO: Integrate with backend (Twilio/EmailJS)
    setOtpSent(true);
  };
  const handleVerifyOtp = () => {
    // TODO: Integrate with backend
    setOtpVerified(true);
  };

  // Handle form submit
  const handleSubmit = (e) => {
    e.preventDefault();
    const eObj = validate();
    setErrors(eObj);
    if (Object.keys(eObj).length > 0) return;
    setSubmitting(true);
    // TODO: Integrate with backend, reCAPTCHA, etc.
    setTimeout(() => {
      setSubmitting(false);
      setSubmitted(true);
      setForm(initialState);
      setOtpSent(false);
      setOtp('');
      setOtpVerified(false);
    }, 2000);
  };

  if (submitted) {
    return (
      <div className="max-w-xl mx-auto bg-white rounded-2xl shadow-lg p-8 mt-10 text-center animate-fade-in">
        <h2 className="text-2xl font-bold mb-4 text-green-700">Thank you for your courage and compassion.</h2>
        <p className="text-lg text-gray-700 mb-4">Your report has been securely submitted and will be reviewed. You've taken a step towards dignity and justice.</p>
        <button className="mt-6 px-6 py-2 bg-blue-600 text-white rounded-lg font-semibold shadow hover:bg-blue-700" onClick={() => setSubmitted(false)}>Submit Another Report</button>
      </div>
    );
  }

  return (
    <div className="max-w-2xl mx-auto bg-white rounded-2xl shadow-lg p-8 mt-10 animate-fade-in">
      {/* Prompt Statement */}
      <div className="mb-6 text-center">
        <h1 className="text-2xl sm:text-3xl font-bold mb-2">Report Unclaimed / Unidentified Body</h1>
        <div className="text-base sm:text-lg text-gray-700 font-medium mb-2">Help bring dignity and closure to the unidentified.<br />Your report can be anonymous, safe, and confidential.<br />Please provide the following details to assist concerned authorities.</div>
      </div>
      <form onSubmit={handleSubmit} className="space-y-5">
        {/* Location */}
        <div>
          <label className="block font-semibold mb-1">üìç Location of body found *</label>
          <div className="flex items-center gap-2 mb-2">
            <input
              type="checkbox"
              name="manualLocation"
              checked={form.manualLocation}
              onChange={handleChange}
              className="form-checkbox"
              id="manualLocation"
            />
            <label htmlFor="manualLocation" className="text-sm">Enter location manually</label>
          </div>
          {form.manualLocation ? (
            <input
              type="text"
              name="location"
              value={form.location}
              onChange={handleChange}
              className="form-input w-full"
              placeholder="Enter location (address, landmark, etc.)"
              required
            />
          ) : (
            <input
              type="text"
              name="location"
              value={form.location}
              readOnly
              className="form-input w-full bg-gray-100 cursor-not-allowed"
              placeholder="Fetching location..."
              required
            />
          )}
          {errors.location && <p className="text-red-500 text-sm mt-1">{errors.location}</p>}
        </div>
        {/* Date & Time */}
        <div>
          <label className="block font-semibold mb-1">üìÖ Date & Time of sighting *</label>
          <input
            type="datetime-local"
            name="dateTime"
            value={form.dateTime}
            onChange={handleChange}
            className="form-input w-full"
            required
          />
          {errors.dateTime && <p className="text-red-500 text-sm mt-1">{errors.dateTime}</p>}
        </div>
        {/* Image Upload */}
        <div>
          <label className="block font-semibold mb-1">üì∏ Upload Image (if any)</label>
          <input
            type="file"
            name="image"
            accept="image/jpeg,image/png"
            onChange={handleChange}
            className="form-input w-full"
          />
          {errors.image && <p className="text-red-500 text-sm mt-1">{errors.image}</p>}
        </div>
        {/* Description */}
        <div>
          <label className="block font-semibold mb-1">üßç Description of the deceased *</label>
          <textarea
            name="description"
            value={form.description}
            onChange={handleChange}
            className="form-input w-full min-h-[80px]"
            placeholder="Visible features, clothes, tattoos, etc."
            required
          />
          {errors.description && <p className="text-red-500 text-sm mt-1">{errors.description}</p>}
        </div>
        {/* Contact */}
        <div>
          <label className="block font-semibold mb-1">üìû Contact number or Email (optional, OTP verified)</label>
          <input
            type="text"
            name="contact"
            value={form.contact}
            onChange={handleChange}
            className="form-input w-full"
            placeholder="Phone or email (optional)"
          />
          {form.contact && !otpSent && (
            <button type="button" className="mt-2 px-4 py-1 bg-blue-600 text-white rounded" onClick={handleSendOtp}>Send OTP</button>
          )}
          {otpSent && !otpVerified && (
            <div className="flex items-center gap-2 mt-2">
              <input
                type="text"
                value={otp}
                onChange={e => setOtp(e.target.value)}
                className="form-input w-32"
                placeholder="Enter OTP"
              />
              <button type="button" className="px-3 py-1 bg-green-600 text-white rounded" onClick={handleVerifyOtp}>Verify</button>
            </div>
          )}
          {otpVerified && <span className="ml-2 text-green-600 font-semibold">Verified</span>}
          {errors.contact && <p className="text-red-500 text-sm mt-1">{errors.contact}</p>}
        </div>
        {/* Anonymous */}
        <div className="flex items-center gap-2">
          <input
            type="checkbox"
            name="anonymous"
            checked={form.anonymous}
            onChange={handleChange}
            className="form-checkbox"
            id="anonymous"
          />
          <label htmlFor="anonymous" className="text-sm">Submit anonymously?</label>
        </div>
        {/* Any other message */}
        <div>
          <label className="block font-semibold mb-1">üìÑ Any other message?</label>
          <textarea
            name="message"
            value={form.message}
            onChange={handleChange}
            className="form-input w-full min-h-[60px]"
            placeholder="Free form notes (optional)"
          />
        </div>
        {/* Terms disclaimer */}
        <div className="flex items-center gap-2">
          <input
            type="checkbox"
            name="terms"
            checked={form.terms}
            onChange={handleChange}
            className="form-checkbox"
            id="terms"
            required
          />
          <label htmlFor="terms" className="text-sm">I confirm this report is genuine to the best of my knowledge.</label>
        </div>
        {errors.terms && <p className="text-red-500 text-sm mt-1">{errors.terms}</p>}
        {/* Submit Button */}
        <button
          type="submit"
          className="w-full mt-4 py-3 rounded-xl bg-gradient-to-r from-blue-600 to-green-600 text-white font-bold shadow-lg hover:from-blue-700 hover:to-green-700 transition-all text-lg"
          disabled={submitting}
        >
          {submitting ? 'Submitting...' : 'Submit Report Securely'}
        </button>
      </form>
      {/* Safety, Privacy & Trust Section */}
      <div className="mt-10 p-5 rounded-xl bg-blue-50 border border-blue-200 shadow-inner animate-fade-in">
        <h3 className="font-bold text-blue-800 mb-2">üõ°Ô∏è üîí Safety, Privacy & Trust</h3>
        <ul className="list-disc ml-6 text-blue-900 space-y-1 text-sm">
          <li>Your identity is optional. Anonymous reports are allowed.</li>
          <li>No sensitive personal details are collected without your consent.</li>
          <li>All data is securely stored using industry-standard encryption (MongoDB Atlas).</li>
          <li>Location, date & time are auto-fetched (editable), ensuring authenticity.</li>
          <li>OTP verification (if phone/email is provided) ensures safe submission.</li>
          <li>Reports are monitored and verified by a backend admin before any action.</li>
          <li>IP logging & spam detection prevent misuse and fake submissions.</li>
        </ul>
      </div>
    </div>
  );
};

export default ReportUnclaimedBody;
