import React, { useState, useEffect } from 'react';

const COOKIE_CONSENT_KEY = 'avyakta_cookie_consent';

const CookieConsent = () => {
  const [visible, setVisible] = useState(false);
  const [checked, setChecked] = useState(false);
  const [confirmed, setConfirmed] = useState(false);

  useEffect(() => {
    // Show only if not already accepted this session
    const consent = localStorage.getItem(COOKIE_CONSENT_KEY);
    if (!consent) setVisible(true);
  }, []);

  const handleCheck = (e) => {
    setChecked(e.target.checked);
    if (e.target.checked) setConfirmed(true);
    else setConfirmed(false);
  };

  const handleAccept = () => {
    localStorage.setItem(COOKIE_CONSENT_KEY, JSON.stringify({ consent: true, timestamp: new Date().toISOString() }));
    setVisible(false);
    console.log('Cookie consent accepted at', new Date().toISOString());
  };

  if (!visible) return null;

  return (
    <div className="fixed bottom-0 left-0 w-full z-50 flex justify-center items-end">
      <div className="w-full max-w-2xl mx-auto mb-6 bg-amber-50 border border-amber-200 rounded-xl shadow-lg p-6 flex flex-col items-center space-y-4 animate-fade-in" style={{boxShadow: '0 4px 24px 0 rgba(0,0,0,0.08)'}}>
        <div className="text-base text-amber-900 font-medium text-center">
          We use cookies to improve your experience, understand how our platform is used, and ensure your journey here remains meaningful and safe. We promise to use your data responsibly.
        </div>
        <form className="w-full flex flex-col items-center" onSubmit={e => { e.preventDefault(); if (checked) handleAccept(); }}>
          <label className="flex items-start gap-3 cursor-pointer select-none text-base font-medium mb-2" tabIndex={0} onKeyDown={e => { if (e.key === ' ' || e.key === 'Enter') { setChecked(c => !c); setConfirmed(!checked); }}}>
            <input
              type="checkbox"
              className="accent-green-600 w-5 h-5 mt-1 rounded focus:ring-2 focus:ring-green-400 focus:outline-none transition-all duration-200"
              checked={checked}
              onChange={handleCheck}
              aria-checked={checked}
              aria-label="Accept Cookie Policy"
            />
            <span className="leading-snug">
              <span role="img" aria-label="checkbox">☑</span> I understand and accept the use of cookies as outlined in the <a href="/cookie-policy" target="_blank" rel="noopener noreferrer" className="underline text-green-700 hover:text-green-900">Cookie Policy</a>, and I trust this data will be used ethically.
            </span>
          </label>
          {confirmed && (
            <div className="w-full text-green-800 bg-green-50 border border-green-200 rounded p-2 text-center text-base mt-2 animate-fade-in">
              <span role="img" aria-label="check">✔</span> Thank you for trusting us. Your preferences are respected.
            </div>
          )}
          <button
            type="submit"
            className={`w-full mt-4 py-3 rounded-lg font-semibold text-lg transition-all duration-200 focus:outline-none focus:ring-2 focus:ring-green-400 focus:ring-offset-2
              ${checked ? 'bg-green-700 hover:bg-green-800 text-white shadow-md hover:scale-105' : 'bg-amber-200 text-amber-400 cursor-not-allowed'}`}
            disabled={!checked}
            tabIndex={0}
          >
            Accept Cookies
          </button>
        </form>
        <div className="w-full flex justify-center mt-2">
          <a href="#" className="text-green-700 underline hover:text-green-900 text-sm transition-colors" tabIndex={0}>Cookie Settings</a>
        </div>
      </div>
    </div>
  );
};

export default CookieConsent;
