import React from 'react';

const SplashLoader = () => {
  return (
    <div className="fixed inset-0 z-50 flex items-center justify-center bg-gradient-to-br from-[#22313a] via-[#2c3e50] to-[#1a232b] transition-all duration-700">
      {/* Spinner with logo */}
      <div className="relative w-40 h-40 flex items-center justify-center">
        {/* Animated outer lines - all softly glowing */}
        <svg className="absolute inset-0 w-full h-full animate-spin-slow" viewBox="0 0 160 160">
          {[...Array(12)].map((_, i) => (
            <rect
              key={i}
              x="78"
              y="18"
              width="4"
              height="24"
              rx="2"
              fill="#e3f6ff"
              opacity={0.85}
              style={{
                transform: `rotate(${i * 30}deg)`,
                transformOrigin: '80px 80px',
                filter: 'drop-shadow(0 0 12px #e3f6ffcc)',
                transition: 'opacity 0.5s',
              }}
            />
          ))}
        </svg>
        {/* Center logo placeholder (replace with your real logo if needed) */}
        <img
          src="/images/loading.png"
          alt="Loading..."
          className="relative z-10 w-24 h-24 object-contain rounded-full shadow-lg"
        />
      </div>
    </div>
  );
};

export default SplashLoader;

// CSS (add to your global CSS or Tailwind config):
// .animate-spin-slow { animation: spin 2.5s linear infinite; }
// @keyframes spin { 100% { transform: rotate(360deg); } }
