import React, { useState } from 'react';

const FriendlyRobot = () => {
  const [isHovered, setIsHovered] = useState(false);

  return (
    <div 
      className="fixed bottom-6 right-6 z-40 cursor-pointer"
      onMouseEnter={() => setIsHovered(true)}
      onMouseLeave={() => setIsHovered(false)}
    >
      {/* Speech Bubble */}
      <div className={`absolute bottom-20 right-0 bg-white rounded-2xl px-4 py-3 shadow-lg border-2 border-purple-200 transition-all duration-300 ${
        isHovered ? 'scale-110 opacity-100' : 'scale-100 opacity-90'
      }`}>
        <div className="text-sm font-medium text-gray-800 whitespace-nowrap">
          May I help you?
        </div>
        {/* Speech bubble tail */}
        <div className="absolute -bottom-2 right-4 w-4 h-4 bg-white border-r-2 border-b-2 border-purple-200 transform rotate-45"></div>
      </div>

      {/* Robot Character */}
      <div className={`relative transition-all duration-300 ${
        isHovered ? 'transform -translate-y-2' : 'animate-bounce'
      }`}>
        {/* Robot Body */}
        <div className="w-16 h-20 bg-gradient-to-b from-blue-400 to-blue-600 rounded-2xl border-4 border-blue-300 shadow-lg relative">
          
          {/* Glowing Face */}
          <div className="absolute top-2 left-1/2 transform -translate-x-1/2 w-12 h-12 bg-gradient-to-br from-yellow-300 to-orange-400 rounded-full border-3 border-yellow-200 shadow-lg animate-pulse">
            
            {/* Expressive Digital Eyes */}
            <div className="absolute top-3 left-2 w-2 h-2 bg-black rounded-full animate-blink"></div>
            <div className="absolute top-3 right-2 w-2 h-2 bg-black rounded-full animate-blink" style={{ animationDelay: '0.5s' }}></div>
            
            {/* Happy Mouth */}
            <div className="absolute bottom-2 left-1/2 transform -translate-x-1/2 w-4 h-1 bg-black rounded-full"></div>
          </div>

          {/* Robot Arms */}
          <div className="absolute top-8 left-1 w-3 h-8 bg-blue-500 rounded-full transform -rotate-12 animate-wave"></div>
          <div className="absolute top-8 right-1 w-3 h-8 bg-blue-500 rounded-full transform rotate-12 animate-wave" style={{ animationDelay: '0.3s' }}></div>

          {/* Robot Legs */}
          <div className="absolute bottom-2 left-2 w-4 h-6 bg-blue-500 rounded-full"></div>
          <div className="absolute bottom-2 right-2 w-4 h-6 bg-blue-500 rounded-full"></div>

          {/* Glowing Effects */}
          <div className="absolute inset-0 bg-gradient-to-t from-transparent via-blue-300/20 to-transparent rounded-2xl"></div>
          
          {/* Digital Circuit Lines */}
          <div className="absolute top-4 left-2 w-1 h-1 bg-cyan-300 rounded-full animate-pulse"></div>
          <div className="absolute top-4 right-2 w-1 h-1 bg-cyan-300 rounded-full animate-pulse" style={{ animationDelay: '0.7s' }}></div>
          <div className="absolute bottom-4 left-3 w-1 h-1 bg-cyan-300 rounded-full animate-pulse" style={{ animationDelay: '1.4s' }}></div>
          <div className="absolute bottom-4 right-3 w-1 h-1 bg-cyan-300 rounded-full animate-pulse" style={{ animationDelay: '2.1s' }}></div>
        </div>

        {/* Energy Particles */}
        <div className="absolute -top-2 -left-2 w-2 h-2 bg-yellow-300 rounded-full animate-ping"></div>
        <div className="absolute -top-1 -right-1 w-1 h-1 bg-cyan-300 rounded-full animate-ping" style={{ animationDelay: '0.5s' }}></div>
        <div className="absolute -bottom-1 -left-1 w-1 h-1 bg-pink-300 rounded-full animate-ping" style={{ animationDelay: '1s' }}></div>
      </div>
    </div>
  );
};

export default FriendlyRobot;
