  GlobeAltIcon,
  DocumentTextIcon,
  MapPinIcon,
  ClockIcon
} from '@heroicons/react/24/outline';

const FloatingActionButton = () => {
  const [isFabExpanded, setIsFabExpanded] = useState(false);
  const [isChatOpen, setIsChatOpen] = useState(false);
  const [messages, setMessages] = useState([]);
  const [inputMessage, setInputMessage] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const chatEndRef = useRef(null);

  const scrollToBottom = () => {
    chatEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  // Clear chat history when opening chat
  useEffect(() => {
    if (isChatOpen) {
      setMessages([{
        type: 'bot',
        text: "Hi! I'm Avyakta Assistant. I'm here to help you with any questions about our website or support you in difficult situations. How can I assist you today?",
        timestamp: new Date(),
        quickActions: [
          { text: "About Avyakta", action: "about" },
          { text: "Emergency Help", action: "emergency" },
          { text: "Contact Us", action: "contact" },
          { text: "How to Report", action: "report" }
        ]
      }]);
    }
  }, [isChatOpen]);

  const getResponse = (message) => {
    const lowerMessage = message.toLowerCase();

    // About Avyakta
    if (lowerMessage.includes('about') || lowerMessage.includes('what is') || lowerMessage.includes('avyakta')) {
      return {
        text: "Avyakta is a compassionate platform dedicated to giving dignity to the forgotten. We help report and identify unclaimed bodies, provide emergency support, and connect families with their lost loved ones. Our mission is to bring justice, identity, and peace to those who need it most.",
        quickActions: [
          { text: "Our Mission", action: "mission" },
          { text: "How We Help", action: "help" },
          { text: "Contact Support", action: "contact" }
        ]
      };
    }

    // Mission
    if (lowerMessage.includes('mission') || lowerMessage.includes('purpose')) {
      return {
        text: "Our mission is to ensure that no life goes unnoticed or unclaimed. We work to identify unclaimed bodies, provide closure to families, and offer support during difficult times. Every person deserves dignity, even in death.",
        quickActions: [
          { text: "Emergency Services", action: "emergency" },
          { text: "Report a Case", action: "report" },
          { text: "Get Help", action: "help" }
        ]
      };
    }

    // Emergency situations
    if (lowerMessage.includes('emergency') || lowerMessage.includes('urgent') || lowerMessage.includes('sos')) {
      return {
        text: "ðŸš¨ This sounds urgent! Please call emergency services (100) immediately. While help is on the way, I can guide you through what to do next. Are you in a safe location?",
        quickActions: [
          { text: "Call 100 Now", action: "call_100" },
          { text: "I'm Safe", action: "safe" },
          { text: "Need Guidance", action: "guidance" }
        ]
      };
    }

    // Reporting unclaimed bodies
    if (lowerMessage.includes('dead') || lowerMessage.includes('body') || lowerMessage.includes('found') || lowerMessage.includes('report')) {
      return {
        text: "I understand this is a difficult situation. First, please stay calm. Are you in a safe location? If yes, please call emergency services (100) immediately. I'll guide you through the reporting process while help arrives.",
        quickActions: [
          { text: "Call Emergency", action: "call_100" },
          { text: "Report Online", action: "report_online" },
          { text: "I'm Safe", action: "safe" }
        ]
      };
    }

    // Contact information
    if (lowerMessage.includes('contact') || lowerMessage.includes('phone') || lowerMessage.includes('email') || lowerMessage.includes('reach')) {
      return {
        text: "You can reach us through multiple channels:\nðŸ“ž 24/7 Helpline: +91 1800-XXX-XXXX\nðŸ“§ Email: support@avyakta.org\nðŸŒ Website: www.avyakta.org\nðŸ“ Location: New Delhi, India\nWe're here 24/7 to help you.",
        quickActions: [
          { text: "Call Helpline", action: "call_helpline" },
          { text: "Send Email", action: "send_email" },
          { text: "Visit Website", action: "visit_website" }
        ]
      };
    }

    // How to report
    if (lowerMessage.includes('how to') || lowerMessage.includes('process') || lowerMessage.includes('steps')) {
      return {
        text: "Here's how to report a case:\n1ï¸âƒ£ Call emergency services (100) immediately\n2ï¸âƒ£ Stay at the location if safe\n3ï¸âƒ£ Provide clear location details\n4ï¸âƒ£ Don't touch or move anything\n5ï¸âƒ£ Wait for authorities to arrive\n6ï¸âƒ£ Contact us for additional support",
        quickActions: [
          { text: "Call 100 Now", action: "call_100" },
          { text: "Online Report", action: "report_online" },
          { text: "Need Help", action: "help" }
        ]
      };
    }

    // Safety check
    if (lowerMessage.includes('safe') || lowerMessage.includes('danger') || lowerMessage.includes('risk')) {
      return {
        text: "Your safety is the top priority! If you feel unsafe, please move to a safe location immediately and call emergency services (100). Would you like me to guide you through safety procedures?",
        quickActions: [
          { text: "I'm Safe", action: "safe" },
          { text: "Call Emergency", action: "call_100" },
          { text: "Safety Tips", action: "safety_tips" }
        ]
      };
    }

    // Support resources
    if (lowerMessage.includes('support') || lowerMessage.includes('help') || lowerMessage.includes('resource')) {
      return {
        text: "We have several support resources available:\nðŸ†˜ Emergency: Call 100\nðŸ“ž Helpline: +91 1800-XXX-XXXX\nðŸ’¬ Chat: I'm here 24/7\nðŸ“§ Email: support@avyakta.org\nðŸŒ Website: Complete information\nWould you like specific details about any of these?",
        quickActions: [
          { text: "Emergency Help", action: "emergency" },
          { text: "Call Helpline", action: "call_helpline" },
          { text: "Visit Website", action: "visit_website" }
        ]
      };
    }

    // Website features
    if (lowerMessage.includes('website') || lowerMessage.includes('features') || lowerMessage.includes('services')) {
      return {
        text: "Our website offers:\nðŸ  Home: Learn about our mission\nðŸ“ž Contact: Get in touch with us\nðŸš¨ Emergency: Quick access to help\nðŸ‘¤ Admin Login: For authorities\nðŸ“‹ Reports: View and manage cases\nðŸŒ Multi-language support\nðŸ“± Mobile responsive design",
        quickActions: [
          { text: "Contact Page", action: "contact" },
          { text: "Emergency Help", action: "emergency" },
          { text: "Admin Login", action: "admin" }
        ]
      };
    }

    // Admin login
    if (lowerMessage.includes('admin') || lowerMessage.includes('login') || lowerMessage.includes('register')) {
      return {
        text: "For admin access and registration:\nðŸ‘¤ Admin Login: Available on homepage\nðŸ“ Registration: Open for authorities, NGOs, and common users\nðŸ” Secure: Multi-step verification with OTP\nðŸ“§ Support: Email assistance available\nWould you like to access the admin panel?",
        quickActions: [
          { text: "Go to Login", action: "admin_login" },
          { text: "Register Now", action: "register" },
          { text: "Need Help", action: "help" }
        ]
      };
    }

    // Default response
    return {
      text: "I'm here to help you! You can ask me about:\nâ€¢ About Avyakta and our mission\nâ€¢ Emergency procedures and help\nâ€¢ How to report cases\nâ€¢ Contact information\nâ€¢ Website features\nâ€¢ Admin login and registration\nWhat would you like to know?",
      quickActions: [
        { text: "About Avyakta", action: "about" },
        { text: "Emergency Help", action: "emergency" },
        { text: "Contact Us", action: "contact" },
        { text: "How to Report", action: "report" }
      ]
    };
  };

  const handleQuickAction = (action) => {
    let response;
    switch (action) {
      case 'call_100':
        window.location.href = 'tel:100';
        response = { text: "Calling emergency services (100) now. Please stay on the line and follow their instructions." };
        break;
      case 'call_helpline':
        window.location.href = 'tel:+911800XXX';
        response = { text: "Calling our helpline now. Our team will assist you immediately." };
        break;
      case 'send_email':
        window.location.href = 'mailto:support@avyakta.org';
        response = { text: "Opening email client. Please send us your message and we'll respond within 24 hours." };
        break;
      case 'visit_website':
        response = { text: "You're already on our website! You can navigate to different sections using the menu above." };
        break;
      case 'admin_login':
        window.location.href = '/admin';
        response = { text: "Redirecting you to the admin login page. Please use your credentials to access the system." };
        break;
      case 'register':
        window.location.href = '/admin';
        response = { text: "Redirecting you to the registration page. You can register as an admin, NGO, or common user." };
        break;
      case 'report_online':
        window.location.href = '/contact';
        response = { text: "Redirecting you to our contact form. You can report cases and get immediate assistance." };
        break;
      default:
        response = getResponse(action);
    }

    setMessages(prev => [...prev, { 
      type: 'bot', 
      text: response.text,
      timestamp: new Date(),
      quickActions: response.quickActions
    }]);
  };

  const handleSendMessage = async () => {
    if (!inputMessage.trim()) return;

    // Add user message
    setMessages(prev => [...prev, { 
      type: 'user', 
      text: inputMessage,
      timestamp: new Date()
    }]);

    setIsLoading(true);

    try {
      // Get response using our local function
      const response = getResponse(inputMessage);

      // Add a small delay to make the conversation feel more natural
      await new Promise(resolve => setTimeout(resolve, 800));

      setMessages(prev => [...prev, { 
        type: 'bot', 
        text: response.text,
        timestamp: new Date(),
        quickActions: response.quickActions
      }]);
    } catch (error) {
      setMessages(prev => [...prev, { 
        type: 'bot', 
        text: "I'm here to help you. Please let me know what you're experiencing, and I'll guide you through the next steps.",
        timestamp: new Date()
      }]);
    } finally {
      setIsLoading(false);
      setInputMessage('');
    }
  };

  const formatTime = (date) => {
    return date.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' });
  };

  return (
    <>
      {/* Floating Action Button Group */}
      <div className="fixed bottom-24 right-6 z-50">
        {/* Main FAB */}
        <button
          onClick={() => setIsFabExpanded(!isFabExpanded)}
          className={`w-16 h-16 rounded-full bg-gradient-to-r from-blue-600 to-blue-700 text-white shadow-lg flex items-center justify-center transition-all duration-300 transform hover:scale-110 hover:shadow-xl ${
            isFabExpanded ? 'rotate-45' : ''
          }`}
        >
          {isFabExpanded ? (
            <XMarkIcon className="w-7 h-7 transition-transform duration-300" />
          ) : (
            <ShieldExclamationIcon className="w-7 h-7 transition-transform duration-300" />
          )}
        </button>

        {/* Expanded Options */}
        <div
          className={`absolute bottom-20 right-0 flex flex-col gap-4 transition-all duration-300 ${
            isFabExpanded ? 'opacity-100 translate-y-0' : 'opacity-0 translate-y-4 pointer-events-none'
          }`}
        >
          {/* Chat Button */}
          <button
            onClick={() => setIsChatOpen(!isChatOpen)}
            className="w-14 h-14 rounded-full bg-gradient-to-r from-green-500 to-green-600 text-white shadow-lg flex items-center justify-center transform hover:scale-110 transition-all duration-300 hover:shadow-xl"
          >
            <ChatBubbleLeftRightIcon className="w-6 h-6" />
          </button>

          {/* SOS Button */}
          <button
            onClick={() => {
              window.location.href = 'tel:100';
            }}
            className="w-14 h-14 rounded-full bg-gradient-to-r from-red-600 to-red-700 text-white shadow-lg flex items-center justify-center transform hover:scale-110 transition-all duration-300 hover:shadow-xl animate-pulse"
          >
            <span className="text-sm font-bold">SOS</span>
          </button>
        </div>
      </div>

      {/* Chat Box */}
      <div
        className={`fixed bottom-6 left-6 w-80 h-96 bg-white rounded-2xl shadow-2xl transition-all duration-300 transform border border-gray-200 ${
          isChatOpen ? 'opacity-100 translate-y-0' : 'opacity-0 translate-y-4 pointer-events-none'
        }`}
      >
        <div className="h-full flex flex-col">
          {/* Chat Header */}
          <div className="bg-gradient-to-r from-blue-600 to-blue-700 text-white p-4 rounded-t-2xl flex justify-between items-center">
            <div className="flex items-center gap-3">
              <div className="w-10 h-10 bg-white rounded-full flex items-center justify-center">
                <ShieldExclamationIcon className="w-6 h-6 text-blue-600" />
              </div>
              <div>
                <h3 className="font-semibold text-lg">Avyakta Assistant</h3>
                <p className="text-blue-100 text-sm">24/7 Support</p>
              </div>
            </div>
            <button
              onClick={() => setIsChatOpen(false)}
              className="text-white hover:text-blue-100 p-2 hover:bg-blue-600 rounded-full transition-colors duration-200"
            >
              <XMarkIcon className="w-5 h-5" />
            </button>
          </div>

          {/* Chat Messages Area */}
          <div className="flex-1 p-4 overflow-y-auto bg-gray-50 custom-scrollbar">
            <div className="space-y-4">
              {messages.map((message, index) => (
                <div key={index}>
                  <div
                    className={`flex ${message.type === 'user' ? 'justify-end' : 'justify-start'} animate-fade-in`}
                    style={{ animationDelay: `${index * 0.1}s` }}
                  >
                    <div className="flex flex-col max-w-[80%]">
                      <div
                        className={`rounded-2xl p-3 ${
                          message.type === 'user'
                            ? 'bg-blue-600 text-white'
                            : 'bg-white text-gray-800 border border-gray-200'
                        }`}
                      >
                        <p className="text-sm leading-relaxed whitespace-pre-line">{message.text}</p>
                      </div>
                      <span className="text-xs text-gray-500 mt-1">
                        {formatTime(message.timestamp)}
                      </span>
                    </div>
                  </div>

                  {/* Quick Action Buttons */}
                  {message.type === 'bot' && message.quickActions && (
                    <div className="flex flex-wrap gap-2 mt-3 animate-fade-in" style={{ animationDelay: `${(index + 1) * 0.1}s` }}>
                      {message.quickActions.map((action, actionIndex) => (
                        <button
                          key={actionIndex}
                          onClick={() => handleQuickAction(action.action)}
                          className="px-3 py-1 bg-blue-100 text-blue-700 text-xs rounded-full hover:bg-blue-200 transition-all duration-200 hover:scale-105"
                        >
                          {action.text}
                        </button>
                      ))}
                    </div>
                  )}
                </div>
              ))}
              {isLoading && (
                <div className="flex justify-start animate-fade-in">
                  <div className="bg-white text-gray-800 border border-gray-200 rounded-2xl p-3">
                    <div className="flex space-x-1">
                      <div className="w-2 h-2 bg-gray-400 rounded-full animate-bounce"></div>
                      <div className="w-2 h-2 bg-gray-400 rounded-full animate-bounce" style={{ animationDelay: '0.1s' }}></div>
                      <div className="w-2 h-2 bg-gray-400 rounded-full animate-bounce" style={{ animationDelay: '0.2s' }}></div>
                    </div>
                  </div>
                </div>
              )}
            </div>
            <div ref={chatEndRef} />
          </div>

          {/* Chat Input */}
          <div className="p-4 border-t border-gray-200 bg-white rounded-b-2xl">
            <div className="flex gap-2">
              <input
                type="text"
                value={inputMessage}
                onChange={(e) => {
                  console.log('Input changed:', e.target.value); // Debug log
                  setInputMessage(e.target.value);
                }}
                onKeyPress={(e) => e.key === 'Enter' && handleSendMessage()}
                placeholder="Ask me anything about Avyakta..."
                className="flex-1 px-4 py-2 border border-gray-300 rounded-full focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent transition-all duration-200 text-sm"
                disabled={false} // Explicitly set to false to ensure it's not disabled
                autoComplete="off"
                spellCheck="false"
              />
              <button
                onClick={handleSendMessage}
                disabled={!inputMessage.trim() || isLoading}
                className="p-2 bg-blue-600 text-white rounded-full hover:bg-blue-700 transition-all duration-200 disabled:opacity-50 disabled:cursor-not-allowed hover:scale-105"
              >
                <PaperAirplaneIcon className="w-5 h-5" />
              </button>
            </div>
          </div>
        </div>
      </div>
    </>
  );
};

export default FloatingActionButton;
