import React from 'react';
import { useTranslation } from 'react-i18next';
import { Link } from 'react-router-dom';

// Sacred Candle Animation Component
const SacredCandleAnimation = () => {
  return (
    <div className="absolute inset-0 overflow-hidden pointer-events-none">
      {/* Central Candle */}
      <div className="absolute top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-1/2">
        {/* Candle Base */}
        <div className="relative">
          {/* Candle Body */}
          <div className="w-8 h-32 bg-gradient-to-b from-amber-100 via-amber-50 to-amber-200 rounded-t-lg shadow-lg">
            {/* Candle Wick */}
            <div className="absolute top-0 left-1/2 transform -translate-x-1/2 w-1 h-4 bg-gray-800 rounded-b-sm"></div>

            {/* Flickering Flame */}
            <div className="absolute -top-6 left-1/2 transform -translate-x-1/2">
              {/* Main Flame */}
              <div className="animate-flicker w-4 h-8 bg-gradient-to-t from-amber-400 via-yellow-300 to-orange-200 rounded-full shadow-lg">
                {/* Inner Flame */}
                <div className="absolute inset-1 w-2 h-4 bg-gradient-to-t from-yellow-200 to-white rounded-full animate-inner-flicker"></div>
                {/* Flame Glow */}
                <div className="absolute -inset-2 w-8 h-12 bg-gradient-to-t from-amber-300/30 to-transparent rounded-full animate-glow"></div>
              </div>
            </div>
          </div>

          {/* Candle Glow */}
          <div className="absolute -inset-8 w-24 h-40 bg-gradient-to-t from-amber-200/20 via-amber-100/10 to-transparent rounded-full animate-candle-glow"></div>
        </div>
      </div>

      {/* Gentle Falling Stars */}
      {[...Array(20)].map((_, index) => (
        <div
          key={index}
          className="absolute animate-gentle-fall"
          style={{
            left: `${Math.random() * 100}%`,
            animationDelay: `${Math.random() * 15}s`,
            animationDuration: `${12 + Math.random() * 18}s`,
            animationIterationCount: 'infinite'
          }}
        >
          <div 
            className="w-1 h-1 bg-white rounded-full opacity-60 animate-gentle-twinkle"
            style={{
              animationDelay: `${Math.random() * 3}s`,
              animationDuration: `${2 + Math.random() * 3}s`
            }}
          />
        </div>
      ))}

      {/* Distant Twinkling Stars */}
      {[...Array(30)].map((_, index) => (
        <div
          key={`star-${index}`}
          className="absolute animate-distant-twinkle"
          style={{
            top: `${Math.random() * 100}%`,
            left: `${Math.random() * 100}%`,
            animationDelay: `${Math.random() * 5}s`,
            animationDuration: `${3 + Math.random() * 4}s`,
            animationIterationCount: 'infinite'
          }}
        >
          <div 
            className="w-0.5 h-0.5 bg-white rounded-full opacity-40"
          />
        </div>
      ))}
    </div>
  );
};

function Home() {
  const { t } = useTranslation();

  return (
    <div className="min-h-screen">
      {/* Hero Section */}
      <div className="relative h-screen overflow-hidden" style={{ backgroundColor: 'rgb(8, 3, 36)' }}>
        {/* Sacred Candle Animation */}
        <SacredCandleAnimation />

        {/* Content */}
        <div className="relative h-full flex flex-col justify-center items-center px-4 sm:px-6 lg:px-8 text-center z-10">
          <h2 className="text-5xl font-extrabold tracking-tight sm:text-6xl lg:text-7xl animate-fade-in" style={{ 
            animationDelay: '0.2s',
            backgroundImage: 'linear-gradient(91deg, #fff, #7e55ff)',
            backgroundClip: 'text',
            WebkitBackgroundClip: 'text',
            color: 'transparent'
          }}>
            Avyakta: Turning Emptiness into Energy
          </h2>
          <p className="mt-8 text-2xl text-gray-200 max-w-4xl mx-auto animate-fade-in" style={{ animationDelay: '0.4s' }}>
            Avyakta, inspired by the Bhagavad Gita, is a platform devoted to helping identify and honor unclaimed bodies with dignity, compassion, and purpose.
          </p>
          <div className="mt-12 flex flex-col sm:flex-row sm:justify-center sm:space-x-6 space-y-4 sm:space-y-0 animate-fade-in" style={{ animationDelay: '0.6s' }}>
            <Link
              to="/report"
              className="inline-flex items-center justify-center px-10 py-4 border-2 border-white text-lg font-medium rounded-md shadow-lg hover:shadow-xl transition-all duration-300 transform hover:scale-105"
              style={{
                backgroundImage: 'linear-gradient(91deg, #fff, #7e55ff)',
                backgroundClip: 'text',
                WebkitBackgroundClip: 'text',
                color: 'transparent'
              }}
            >
              Report a Case
            </Link>
            <Link
              to="/search"
