import React, { useState } from 'react';
import { useTranslation } from 'react-i18next';
import {
  MagnifyingGlassIcon,
  QuestionMarkCircleIcon,
  UserIcon,
  ShieldCheckIcon,
  DocumentTextIcon,
  PhoneIcon,
  CogIcon,
  HeartIcon,
  GlobeAltIcon,
  ExclamationTriangleIcon,
  CheckCircleIcon,
  ChatBubbleLeftRightIcon
} from '@heroicons/react/24/outline';

const FAQPage = () => {
  const { t } = useTranslation();
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedCategory, setSelectedCategory] = useState('all');
  const [expandedItems, setExpandedItems] = useState(new Set());

  // Comprehensive FAQ database
  const faqData = {
    general: {
      title: 'General Questions',
      icon: GlobeAltIcon,
      color: 'from-blue-500 to-purple-600',
      questions: [
        {
          question: 'What is Avyakta?',
          answer: 'Avyakta is a revolutionary platform dedicated to giving dignity to the forgotten. We help unidentified individuals, missing persons, and their families by providing a comprehensive system for reporting, tracking, and resolving cases. Our mission is to bridge the gap between those who need help and those who can provide it.'
        },
        {
          question: 'How does Avyakta work?',
          answer: 'Avyakta works through a simple yet powerful process: 1) Users can report missing persons or unidentified individuals, 2) Our AI-powered system analyzes and matches cases, 3) We connect families, law enforcement, and NGOs, 4) We provide regular updates and support throughout the process. It\'s like having a digital detective that never sleeps!'
        },
        {
          question: 'Who can use Avyakta?',
          answer: 'Anyone can use Avyakta! Families looking for missing loved ones, law enforcement agencies, NGOs, hospitals, and even concerned citizens who want to help. We have different user roles - from common users who can report cases to admins and NGOs who can manage and coordinate efforts. Everyone has a role in bringing people home.'
        }
      ]
    },
    account: {
      title: 'Account & Registration',
      icon: UserIcon,
      color: 'from-green-500 to-teal-600',
      questions: [
        {
          question: 'How to register?',
          answer: 'Registration is super easy! Just click on \'Register\' and follow these steps: 1) Fill in your basic details (name, mobile, email), 2) Verify your mobile with OTP, 3) Set up your password and choose your role, 4) Complete the security verification. The whole process takes just a few minutes, and we\'ll send you a welcome email!'
        },
        {
          question: 'What are the different user roles?',
          answer: 'We have three main user roles: 1) **Common User** - Can report cases and search for missing persons, 2) **NGO** - Can manage cases, coordinate with families and authorities, and access advanced features, 3) **Admin** - Full system access for managing users, cases, and platform operations. Each role is designed to serve different needs in our mission.'
        },
        {
          question: 'I forgot my password, what should I do?',
          answer: 'No worries! If you\'ve forgotten your password, just click on \'Forgot Password\' on the login page. We\'ll send you a password reset link to your registered email address. Make sure to check your spam folder if you don\'t see it in your inbox. The link is valid for 24 hours for your security.'
        }
      ]
    },
    reporting: {
      title: 'Reporting Cases',
      icon: DocumentTextIcon,
      color: 'from-orange-500 to-red-600',
      questions: [
        {
          question: 'How to report a missing person?',
          answer: 'Reporting a missing person is straightforward: 1) Log into your account, 2) Click \'Report Missing Person\', 3) Fill in all the details (name, age, last seen location, description, photos), 4) Submit the report. Our system will immediately start working to help find them. The more details you provide, the better our chances of success!'
        },
        {
          question: 'What information do I need to report?',
          answer: 'For the best results, please provide: Full name, age, gender, height, weight, last seen location and time, clothing description, any identifying marks or tattoos, recent photos, contact information, and any other relevant details. Even small details can make a big difference in finding someone!'
        },
        {
          question: 'Can I report anonymously?',
          answer: 'Yes, you can report cases anonymously! We understand that some situations require discretion. However, providing your contact information helps us follow up with updates and ask for additional details if needed. Your privacy is important to us, and we never share personal information without permission.'
        }
      ]
    },
    cases: {
      title: 'Case Management',
      icon: CheckCircleIcon,
      color: 'from-purple-500 to-pink-600',
      questions: [
        {
          question: 'How long does it take to find someone?',
          answer: 'Every case is unique, so there\'s no fixed timeline. Some cases are resolved within hours, while others may take weeks or months. Factors include: how much information is available, when the person went missing, their age, and circumstances. We work tirelessly on every case and provide regular updates to families.'
        },
        {
          question: 'How do I track my case?',
          answer: 'You can track your case in several ways: 1) Log into your account and check the \'My Cases\' section, 2) We\'ll send you email and SMS updates on any developments, 3) You can contact our support team anytime for status updates. We believe in keeping families informed every step of the way.'
        },
        {
          question: 'What happens after I submit a report?',
          answer: 'After you submit a report, here\'s what happens: 1) Our AI system analyzes the information and searches for matches, 2) We notify relevant authorities and NGOs in the area, 3) Your case gets assigned to a coordinator who will guide you through the process, 4) We start our search and investigation efforts immediately.'
        }
      ]
    },
    security: {
      title: 'Privacy & Security',
      icon: ShieldCheckIcon,
      color: 'from-indigo-500 to-blue-600',
      questions: [
        {
          question: 'Is my information safe?',
          answer: 'Absolutely! Your privacy and security are our top priorities. We use bank-level encryption to protect all your data, and we never share personal information without your explicit consent. Our platform complies with all data protection laws, and we have strict access controls. Your trust is sacred to us.'
        },
        {
          question: 'Who can see my information?',
          answer: 'Only authorized personnel can access your information: our support team, relevant law enforcement agencies (with proper authorization), and NGOs working on your case. We have strict protocols and audit trails to ensure your data is only accessed when necessary and appropriate.'
        }
      ]
    },
    support: {
      title: 'Support & Contact',
      icon: PhoneIcon,
      color: 'from-teal-500 to-green-600',
      questions: [
        {
          question: 'How to contact support?',
          answer: 'We\'re here to help! You can contact us through: 1) This chat - we\'re available 24/7, 2) Email at support@avyakta.org, 3) Phone at our helpline, 4) Contact form on our website. Our support team responds within 2-4 hours, and for urgent cases, we have emergency protocols.'
        },
        {
          question: 'What are your working hours?',
          answer: 'Our digital platform is available 24/7, so you can report cases and access information anytime. Our support team is available from 8 AM to 10 PM IST, and we have emergency protocols for urgent cases outside these hours. We never sleep when it comes to helping families!'
        }
      ]
    },
    technical: {
      title: 'Technical Issues',
      icon: CogIcon,
      color: 'from-gray-500 to-gray-600',
      questions: [
        {
          question: 'The website is not working, what should I do?',
          answer: 'We\'re sorry to hear that! Let\'s troubleshoot: 1) Try refreshing the page, 2) Clear your browser cache, 3) Check your internet connection, 4) Try a different browser. If the problem persists, please contact our technical support with details about what you\'re experiencing. We\'ll get you back up and running quickly!'
        },
        {
          question: 'I can\'t upload photos, what\'s wrong?',
          answer: 'Photo upload issues can be due to: 1) File size (max 5MB per photo), 2) File format (we accept JPG, PNG, GIF), 3) Internet connection, 4) Browser compatibility. Try compressing your photos or using a different browser. If you\'re still having trouble, our support team can help!'
        }
      ]
    },
    help: {
      title: 'How You Can Help',
      icon: HeartIcon,
      color: 'from-pink-500 to-rose-600',
      questions: [
        {
          question: 'How can I help Avyakta?',
          answer: 'There are many ways to help! 1) Spread awareness about Avyakta, 2) Volunteer with local NGOs, 3) Share missing person posts on social media, 4) Report any suspicious activity, 5) Donate to support our mission. Every small action can make a big difference in someone\'s life. Together, we\'re stronger!'
        },
        {
          question: 'Do you accept donations?',
          answer: 'Yes, we gratefully accept donations! Your contributions help us maintain our platform, develop new features, and support families in need. We\'re a transparent organization, and you can see exactly how your donations are used. Every rupee goes directly toward our mission of bringing people home.'
        },
        {
          question: 'Are there success stories?',
          answer: 'We have countless heartwarming success stories! Families reunited after years, children found safe, elderly persons returned home. Each story reminds us why we do what we do. While we can\'t share specific details due to privacy, we can tell you that every case matters to us, and we celebrate every reunion as if it were our own family.'
        }
      ]
    }
  };

  // Filter questions based on search term and category
  const filteredData = Object.entries(faqData).filter(([key, category]) => {
    if (selectedCategory !== 'all' && key !== selectedCategory) return false;
    
    if (!searchTerm) return true;
    
    const searchLower = searchTerm.toLowerCase();
    return category.questions.some(q => 
      q.question.toLowerCase().includes(searchLower) || 
      q.answer.toLowerCase().includes(searchLower)
    );
  });

  // Toggle expanded state for FAQ items
  const toggleExpanded = (categoryKey, questionIndex) => {
    const key = `${categoryKey}-${questionIndex}`;
    const newExpanded = new Set(expandedItems);
    if (newExpanded.has(key)) {
      newExpanded.delete(key);
    } else {
      newExpanded.add(key);
    }
    setExpandedItems(newExpanded);
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-900 via-purple-900 to-slate-900 pt-20">
      <div className="container mx-auto px-4 py-8">
        {/* Header */}
        <div className="text-center mb-12">
          <h1 className="text-4xl md:text-5xl lg:text-6xl font-bold text-white mb-6">
            Frequently Asked Questions
          </h1>
          <p className="text-lg md:text-xl text-gray-300 max-w-3xl mx-auto">
            Find answers to common questions about Avyakta. Can't find what you're looking for? 
            Use our chat assistant or contact our support team.
          </p>
        </div>

        {/* Search and Filter */}
        <div className="mb-8 space-y-4">
          {/* Search Bar */}
          <div className="relative max-w-2xl mx-auto">
            <MagnifyingGlassIcon className="absolute left-4 top-1/2 transform -translate-y-1/2 w-5 h-5 text-gray-400" />
            <input
              type="text"
              placeholder="Search questions and answers..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              className="w-full pl-12 pr-4 py-3 bg-white/10 backdrop-blur-sm border border-white/20 rounded-xl text-white placeholder-gray-300 focus:outline-none focus:ring-2 focus:ring-purple-400 focus:border-transparent"
            />
          </div>

          {/* Category Filter */}
          <div className="flex flex-wrap justify-center gap-2">
            <button
              onClick={() => setSelectedCategory('all')}
              className={`px-4 py-2 rounded-full text-sm font-medium transition-all duration-300 ${
                selectedCategory === 'all'
                  ? 'bg-purple-600 text-white'
                  : 'bg-white/10 text-gray-300 hover:bg-white/20'
              }`}
            >
              All Categories
            </button>
            {Object.entries(faqData).map(([key, category]) => (
              <button
                key={key}
                onClick={() => setSelectedCategory(key)}
                className={`px-4 py-2 rounded-full text-sm font-medium transition-all duration-300 ${
                  selectedCategory === key
                    ? 'bg-purple-600 text-white'
                    : 'bg-white/10 text-gray-300 hover:bg-white/20'
                }`}
              >
                {category.title}
              </button>
            ))}
          </div>
        </div>

        {/* FAQ Content */}
        <div className="max-w-4xl mx-auto space-y-8">
          {filteredData.map(([categoryKey, category]) => (
            <div key={categoryKey} className="bg-white/5 backdrop-blur-sm rounded-2xl p-6 border border-white/10">
              {/* Category Header */}
              <div className={`inline-flex items-center space-x-3 mb-6 p-3 rounded-xl bg-gradient-to-r ${category.color}`}>
                <category.icon className="w-6 h-6 text-white" />
                <h2 className="text-xl font-bold text-white">{category.title}</h2>
              </div>

              {/* Questions */}
              <div className="space-y-4">
                {category.questions
                  .filter(q => !searchTerm || 
                    q.question.toLowerCase().includes(searchTerm.toLowerCase()) || 
                    q.answer.toLowerCase().includes(searchTerm.toLowerCase())
                  )
                  .map((item, index) => {
                    const key = `${categoryKey}-${index}`;
                    const isExpanded = expandedItems.has(key);
                    
                    return (
                      <div key={index} className="bg-white/5 rounded-xl border border-white/10 overflow-hidden">
                        <button
                          onClick={() => toggleExpanded(categoryKey, index)}
                          className="w-full px-6 py-4 text-left flex items-center justify-between hover:bg-white/5 transition-colors duration-200"
                        >
                          <h3 className="text-lg font-semibold text-white pr-4">
                            {item.question}
                          </h3>
                          <QuestionMarkCircleIcon 
                            className={`w-5 h-5 text-gray-400 transition-transform duration-200 ${
                              isExpanded ? 'rotate-180' : ''
                            }`} 
                          />
                        </button>
                        
                        {isExpanded && (
                          <div className="px-6 pb-4">
                            <div className="pt-2 border-t border-white/10">
                              <p className="text-gray-300 leading-relaxed">
                                {item.answer}
                              </p>
                            </div>
                          </div>
                        )}
                      </div>
                    );
                  })}
              </div>
            </div>
          ))}
        </div>

        {/* No Results */}
        {filteredData.length === 0 && (
          <div className="text-center py-12">
            <ExclamationTriangleIcon className="w-16 h-16 text-gray-400 mx-auto mb-4" />
            <h3 className="text-xl font-semibold text-white mb-2">No questions found</h3>
            <p className="text-gray-400 mb-6">
              Try adjusting your search terms or browse all categories.
            </p>
            <button
              onClick={() => {
                setSearchTerm('');
                setSelectedCategory('all');
              }}
              className="px-6 py-3 bg-purple-600 text-white rounded-lg hover:bg-purple-700 transition-colors"
            >
              Clear Filters
            </button>
          </div>
        )}

        {/* Contact Support */}
        <div className="mt-16 text-center">
          <div className="bg-white/5 backdrop-blur-sm rounded-2xl p-8 border border-white/10 max-w-2xl mx-auto">
            <ChatBubbleLeftRightIcon className="w-12 h-12 text-purple-400 mx-auto mb-4" />
            <h3 className="text-2xl font-bold text-white mb-4">Still need help?</h3>
            <p className="text-gray-300 mb-6">
              Our support team is here to help you 24/7. Get in touch with us for personalized assistance.
            </p>
            <div className="flex flex-col sm:flex-row gap-4 justify-center">
              <button className="px-6 py-3 bg-purple-600 text-white rounded-lg hover:bg-purple-700 transition-colors">
                Chat with us
              </button>
              <button className="px-6 py-3 bg-white/10 text-white rounded-lg hover:bg-white/20 transition-colors">
                Contact Support
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default FAQPage;
