import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { 
  HeartIcon, 
  UserGroupIcon, 
  ShieldCheckIcon, 
  GlobeAltIcon, 
  PlayIcon, 
  PauseIcon,
  CalendarIcon,
  LightBulbIcon,
  RocketLaunchIcon,
  TrophyIcon,
  StarIcon,
  UserIcon,
  AcademicCapIcon,
  BriefcaseIcon
} from '@heroicons/react/24/outline';
import { Line } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
  Filler
} from 'chart.js';

ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
  Filler
);

const About = () => {
  const [isPlaying, setIsPlaying] = useState(false);
  const [counters, setCounters] = useState({
    reports: 0,
    lives: 0,
    volunteers: 0,
    stations: 0
  });

  const handleVideoPlay = () => {
    setIsPlaying(true);
  };

  const handleVideoPause = () => {
    setIsPlaying(false);
  };

  useEffect(() => {
    const targetValues = {
      reports: 1452,
      lives: 38,
      volunteers: 75,
      stations: 125
    };

    const duration = 2000; // 2 seconds
    const steps = 60;
    const stepDuration = duration / steps;

    let currentStep = 0;
    const interval = setInterval(() => {
      currentStep++;
      setCounters({
        reports: Math.floor((targetValues.reports * currentStep) / steps),
        lives: Math.floor((targetValues.lives * currentStep) / steps),
        volunteers: Math.floor((targetValues.volunteers * currentStep) / steps),
        stations: Math.floor((targetValues.stations * currentStep) / steps)
      });

      if (currentStep === steps) {
        clearInterval(interval);
      }
    }, stepDuration);

    return () => clearInterval(interval);
  }, []);

  const chartData = {
    labels: ['2019', '2020', '2021', '2022', '2023', '2024'],
    datasets: [
      {
        label: 'Unclaimed Bodies',
        data: [1200, 1350, 1500, 1650, 1800, 1950],
        borderColor: '#f59e0b', // amber-500
        backgroundColor: (context) => {
          const chart = context.chart;
          const { ctx, chartArea } = chart;
          if (!chartArea) {
            return null;
          }
          const gradient = ctx.createLinearGradient(0, chartArea.bottom, 0, chartArea.top);
          gradient.addColorStop(0, 'rgba(239, 68, 68, 0.1)'); // red-500 with low opacity
          gradient.addColorStop(0.5, 'rgba(245, 158, 11, 0.3)'); // amber-500 with medium opacity
          gradient.addColorStop(1, 'rgba(245, 158, 11, 0.6)'); // amber-500 with high opacity
          return gradient;
        },
        borderWidth: 3,
        pointBackgroundColor: (context) => {
          const value = context.raw;
          if (value > 1800) return '#ef4444'; // red-500 for high severity
          if (value > 1500) return '#f59e0b'; // amber-500 for medium severity
          return '#10b981'; // emerald-500 for low severity
        },
        pointBorderColor: '#f8fafc',
        pointBorderWidth: 2,
        pointRadius: 8,
        pointHoverRadius: 12,
        pointHoverBackgroundColor: (context) => {
          const value = context.raw;
          if (value > 1800) return '#dc2626'; // red-600 for high severity
          if (value > 1500) return '#d97706'; // amber-600 for medium severity
          return '#059669'; // emerald-600 for low severity
        },
        tension: 0.4,
        fill: true
      }
    ]
  };

  const chartOptions = {
    responsive: true,
    maintainAspectRatio: false,
    plugins: {
      legend: {
        display: false
      },
      title: {
        display: true,
        text: [
          'Unclaimed Bodies Found in the Last 5 Years (India)',
          'A Growing Crisis We Can\'t Ignore'
        ],
        color: '#e2e8f0', // slate-200
        font: {
          size: 28,
          weight: 'bold',
          family: 'Merriweather'
        },
        padding: {
          top: 20,
          bottom: 20
        }
      },
      subtitle: {
        display: true,
        text: 'A Growing Crisis We Can\'t Ignore',
        color: '#38bdf8', // sky-400
        font: {
          size: 18,
          style: 'italic',
          family: 'Poppins'
        },
        padding: {
          bottom: 20
        }
      },
      tooltip: {
        backgroundColor: 'rgba(30, 41, 59, 0.95)', // slate-800
        titleColor: '#e2e8f0', // slate-200
        bodyColor: '#e2e8f0', // slate-200
        borderColor: '#38bdf8', // sky-400
        borderWidth: 2,
        padding: 16,
        displayColors: false,
        titleFont: {
          size: 16,
          weight: 'bold',
          family: 'Poppins'
        },
        bodyFont: {
          size: 14,
          family: 'Poppins'
        },
        callbacks: {
          label: function(context) {
            const value = context.raw;
            let severity = '';
            if (value > 1800) severity = 'ðŸ”´ High Severity';
            else if (value > 1500) severity = 'ðŸŸ¡ Medium Severity';
            else severity = 'ðŸŸ¢ Low Severity';
            return [
              `Number of Bodies: ${value.toLocaleString()}`,
              `Severity Level: ${severity}`,
              `Year-over-Year Change: ${value > 1200 ? '+' : ''}${((value - 1200) / 1200 * 100).toFixed(1)}%`
            ];
          }
        }
      }
    },
    scales: {
      y: {
        beginAtZero: true,
        grid: {
          color: 'rgba(71, 85, 105, 0.3)', // slate-500 with opacity
          drawBorder: false
        },
        ticks: {
          color: '#cbd5e1', // slate-300
          font: {
            family: 'Poppins',
            size: 12,
            weight: '500'
          },
          callback: function(value) {
            return value.toLocaleString();
          }
        },
        title: {
          display: true,
          text: 'Number of Bodies',
          color: '#38bdf8', // sky-400
          font: {
            size: 14,
            weight: 'bold',
            family: 'Poppins'
          },
          padding: {
            top: 10,
            bottom: 10
          }
        }
      },
      x: {
        grid: {
          color: 'rgba(71, 85, 105, 0.3)', // slate-500 with opacity
          drawBorder: false
        },
        ticks: {
          color: '#cbd5e1', // slate-300
          font: {
            family: 'Poppins',
            size: 12,
            weight: '500'
          }
        },
        title: {
          display: true,
          text: 'Year',
          color: '#38bdf8', // sky-400
          font: {
            size: 14,
            weight: 'bold',
            family: 'Poppins'
          },
          padding: {
            top: 10,
            bottom: 10
          }
        }
      }
    }
  };

  const timelineData = [
    {
      year: '2018',
      title: 'The Inspiration',
      description: 'A chance encounter with an unclaimed body in a local hospital sparked the idea. The founder realized that technology could bridge the gap between the unidentified and their families.',
      icon: LightBulbIcon,
      color: 'text-yellow-400'
    },
    {
      year: '2019',
      title: 'Conceptualization',
      description: 'Avyakta was conceptualized as a platform to bring dignity to the voiceless. The name "Avyakta" (meaning "unexpressed" in Sanskrit) was chosen to represent those who cannot speak for themselves.',
      icon: CalendarIcon,
      color: 'text-blue-400'
    },
    {
      year: '2020',
      title: 'Initial Launch',
      description: 'The platform was launched in partnership with 5 police stations in Delhi. The first month saw 12 successful identifications, proving the concept\'s viability.',
      icon: RocketLaunchIcon,
      color: 'text-green-400'
    },
    {
      year: '2022',
      title: 'Expansion Milestone',
      description: 'Reached 50+ cities across India with 100+ police stations connected. The platform helped identify over 500 unclaimed bodies.',
      icon: TrophyIcon,
      color: 'text-purple-400'
    },
    {
      year: '2024',
      title: 'Current Impact',
      description: 'Now serving 125+ police stations across India, with over 1450 cases handled and 38 lives saved through timely interventions.',
      icon: StarIcon,
      color: 'text-red-400'
    }
  ];

  const teamData = [
    {
      name: 'Dr. Rajesh Kumar',
      role: 'Founder & CEO',
      image: '/images/team/ceo.jpg',
      bio: '20+ years of experience in humanitarian work and technology innovation. Former medical officer who witnessed the plight of unclaimed bodies firsthand.',
      education: 'MBBS, MD - Community Medicine',
      experience: 'Ex-Government Medical Officer'
    },
    {
      name: 'Priya Sharma',
      role: 'Head of Operations',
      image: '/images/team/operations.jpg',
      bio: 'Expert in NGO management and community development. Passionate about creating systemic change through technology.',
      education: 'Masters in Social Work',
      experience: '15+ years in NGO sector'
    },
    {
      name: 'Amit Patel',
      role: 'Technical Lead',
      image: '/images/team/tech.jpg',
      bio: 'Passionate about using technology for social impact. Leads the development of our AI-powered identification system.',
      education: 'B.Tech Computer Science',
      experience: '10+ years in software development'
    },
    {
      name: 'Dr. Meera Singh',
      role: 'Medical Director',
      image: '/images/team/medical.jpg',
      bio: 'Forensic expert who ensures medical accuracy in our identification processes. Works closely with police and hospitals.',
      education: 'MBBS, MD - Forensic Medicine',
      experience: 'Ex-Forensic Expert, Delhi Police'
    },
    {
      name: 'Kavita Reddy',
      role: 'Community Outreach',
      image: '/images/team/outreach.jpg',
      bio: 'Builds partnerships with communities, NGOs, and government agencies. Ensures our platform reaches those who need it most.',
      education: 'Masters in Public Policy',
      experience: '12+ years in community development'
    },
    {
      name: 'Rahul Gupta',
      role: 'Data Analyst',
      image: '/images/team/analyst.jpg',
      bio: 'Analyzes patterns and trends to improve our identification algorithms. Creates reports that help policymakers understand the crisis.',
      education: 'Masters in Data Science',
      experience: '8+ years in data analytics'
    }
  ];

  const testimonials = [
    {
      quote: "Avyakta helped us find closure we never thought possible. After 3 years of searching, we finally found our brother.",
      author: "Anonymous Family Member",
      location: "Delhi",
      rating: 5
    },
    {
      quote: "The platform's efficiency is remarkable. We identified a missing person within 24 hours of the report.",
      author: "Police Inspector",
      location: "Mumbai",
      rating: 5
    },
    {
      quote: "As a volunteer, I've seen firsthand how this technology brings hope to families. It's more than just identification - it's dignity.",
      author: "Volunteer Coordinator",
      location: "Bangalore",
      rating: 5
    },
    {
      quote: "The compassion and respect shown by the Avyakta team is exemplary. They treat every case with the dignity it deserves.",
      author: "Hospital Administrator",
      location: "Chennai",
      rating: 5
    },
    {
      quote: "This platform has revolutionized how we handle unclaimed bodies. It's a game-changer for law enforcement.",
      author: "Senior Police Officer",
      location: "Hyderabad",
      rating: 5
    },
    {
      quote: "We found our missing daughter after 2 years. Avyakta gave us the closure we desperately needed.",
      author: "Anonymous Family",
      location: "Kolkata",
      rating: 5
    }
  ];

  return (
    <div className="font-poppins" style={{ background: 'linear-gradient(135deg, #0f2027, #203a43, #2c5364)' }}>
      {/* Hero Section with Video Background */}
      <section className="relative min-h-[70vh] sm:min-h-[80vh] flex items-center justify-center overflow-hidden" style={{ background: 'linear-gradient(135deg, #0f2027, #203a43, #2c5364)' }}>
        <div className="absolute inset-0 z-0">
          <video
            autoPlay
            loop
            muted
            playsInline
            className="w-full h-full object-cover"
            onPlay={handleVideoPlay}
            onPause={handleVideoPause}
          >
            <source src="/videos/about-hero.mp4" type="video/mp4" />
            <track
